INSERT INTO `editor` VALUES (24,'<div class=\"case\">\r\n    <h3>CSS 实现 lightbox 效果</h3>\r\n    \r\n    <ul>\r\n        <li>\r\n            <img src=\"../image/demo/demoImage-1.jpg\" alt=\"\" />\r\n        </li>\r\n        <li>\r\n            <img src=\"../image/demo/demoImage-2.jpg\" alt=\"\" />\r\n        </li>\r\n        <li>\r\n            <img src=\"../image/demo/demoImage-3.jpg\" alt=\"\" />\r\n        </li>\r\n        <li>\r\n            <img src=\"../image/demo/demoImage-4.jpg\" alt=\"\" />\r\n        </li>\r\n    </ul>\r\n</div>','.case{\r\n    overflow: hidden;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}\r\n\r\nul{\r\n    list-style: none;\r\n}\r\nli{\r\n    float: left;\r\n    position: relative;\r\n    height: 200px;\r\n    width: 240px;\r\n    margin: 5px;\r\n}\r\nimg{\r\n    position: absolute;\r\n    \r\n    max-height: 150px;\r\n    max-width: 240px;\r\n\r\n    border: solid #fff;\r\n    border-width: 5px 5px 40px 5px;\r\n    margin: 5px;\r\n    \r\n    background: #000;\r\n    \r\n    -webkit-transition: all 5s ease-in;\r\n    -moz-transition: all 5s ease-in;\r\n    -ms-transition: all 5s ease-in;\r\n    -o-transition: all 5s ease-in;\r\n    transition: all 5s ease-in;\r\n    \r\n    box-shadow:1px 1px 5px #333;\r\n}\r\nli:hover{\r\n    position: static;\r\n}\r\nli:hover:before{\r\n    position: fixed;\r\n    \r\n    bottom: 0;\r\n    top: 0;\r\n    left: 0;\r\n    right: 0;\r\n    z-index: 99;\r\n    \r\n    background: #000;\r\n    \r\n    filter: alpha(opacity=50);\r\n    opacity:.5;\r\n    \r\n    content: \"\";\r\n}\r\nli:hover:after{\r\n    \r\n}\r\nli:hover img{\r\n    position: absolute;\r\n    \r\n    bottom: 0;\r\n    top: 0;\r\n    left: 0;\r\n    right: 0;\r\n    z-index:100;\r\n    \r\n    max-height: 100%;\r\n    max-width: 100%;\r\n    \r\n    margin: auto;\r\n}','','../script/lib/jQuery/jquery-1.10.2.min.js','CSS 示例-使用 CSS 实现 lightbox 效果',NULL,'../image/preview/css-lightbox.jpg',2,NULL,'2013-01-28 16:02:38',NULL,NULL,'48','原创');
INSERT INTO `editor` VALUES (25,'<div class=\"case\">\r\n    <h3>jQuery 插件-树结构列表</h3>\r\n    \r\n    <p>更多参考：\r\n        <a href=\"http://www.ztree.me/v3/api.php\">\r\n            http://www.ztree.me/v3/api.php\r\n        </a>\r\n    </p>\r\n    \r\n    <ul id=\"tree\" class=\"ztree\"></ul>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}','$(document).ready(function(){\r\n    var treeData = [{\r\n        name:\"一级节点 1\",\r\n        //展开\r\n        open:true,\r\n        nocheck: true,\r\n        children:[{\r\n            name:\"二级节点 1-1\",\r\n            children:[{\r\n                name:\"三级节点 1-1-1\"\r\n            }, {\r\n                name:\"三级节点 1-1-2\"\r\n            }, {\r\n                name:\"三级节点 1-1-3\"\r\n            }, {\r\n                name:\"三级节点 1-1-4\"\r\n            }]\r\n        }, {\r\n            name:\"二级节点 1-2\",\r\n            children:[{\r\n                name:\"三级节点 1-2-1\"\r\n            }, {\r\n                name:\"三级节点 1-2-2\"\r\n            }, {\r\n                name:\"三级节点 1-2-3\"\r\n            }, {\r\n                name:\"三级节点 1-2-4\"\r\n            }]\r\n        }, {\r\n            name:\"二级节点 1-3\",\r\n            isParent:true\r\n        }]\r\n    }, {\r\n        name:\"一级节点 2\",\r\n        nocheck: true,\r\n        children:[{\r\n            name:\"二级节点 2-1\",\r\n            open:true,\r\n            children:[{\r\n                name:\"三级节点 2-1-1\"\r\n            }, {\r\n                name:\"三级节点 2-1-2\"\r\n            }, {\r\n                name:\"三级节点 2-1-3\"\r\n            }, {\r\n                name:\"三级节点 2-1-4\"\r\n            }]\r\n        }, {\r\n            name:\"二级节点 2-2\",\r\n            children:[{\r\n                name:\"三级节点 2-2-1\"\r\n            }, {\r\n                name:\"三级节点 2-2-2\"\r\n            }, {\r\n                name:\"三级节点 2-2-3\"\r\n            }, {\r\n                name:\"三