INSERT INTO `editor` VALUES (29,'<div class=\"case\">\r\n    <h3>JS 插件-评分插件</h3>\r\n    \r\n    <p>更多参考：\r\n        <a href=\"http://www.myjqueryplugins.com/jquery-plugin/jrating\">\r\n            http://www.myjqueryplugins.com/jquery-plugin/jrating\r\n        </a>\r\n    </p>\r\n    \r\n    <div id=\"rate\"></div>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}','/**\r\n * \t个人对插件的代码进行了简单的优化\r\n */\r\n$(document).ready(function(){\r\n    $(\'#rate\').jRating({\r\n        phpPath:\'\',\r\n        step:true,\r\n        length:20\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/jRating/jRating.jquery.css,../script/plugin/jRating/jRating.jquery.js','JS 插件-评分插件[第三方]',NULL,'../image/preview/jRating.jpg',1,NULL,'2013-01-30 14:23:47',NULL,NULL,'122','jQuery 插件');
INSERT INTO `editor` VALUES (30,'<link rel=\"stylesheet\" type=\"text/css\" href=\"../script/test/qunit-1.11.0.css\" />\r\n<div id=\"qunit\"></div>\r\n<div id=\"qunit-fixture\"></div>\r\n<video width=\"640\" height=\"360\" id=\"media\" controls=\"controls\" >\r\n    <source src=\"../media/HR-Why.mp4\" />\r\n    <source src=\"../media/HR-Why.webm\" />\r\n</video>\r\n<script src=\"../script/test/qunit-1.11.0.js\"></script>','video{\r\n    display:none;\r\n}','$(function(){\r\n    test( \"ok test\", function() {\r\n        ok( true, \"true succeeds\" );\r\n        ok( \"non-empty\", \"non-empty string succeeds\" );\r\n        \r\n        ok( false, \"false fails\" );\r\n        ok( 0, \"0 fails\" );\r\n        ok( NaN, \"NaN fails\" );\r\n        ok( \"\", \"empty string fails\" );\r\n        ok( null, \"null fails\" );\r\n        ok( undefined, \"undefined fails\" );\r\n    });\r\n    test( \"equal test\", function() {\r\n        equal( 0, 0, \"Zero; equal succeeds\" );\r\n        equal( \"\", 0, \"Empty, Zero; equal succeeds\" );\r\n        equal( \"\", \"\", \"Empty, Empty; equal succeeds\" );\r\n        equal( 0, 0, \"Zero, Zero; equal succeeds\" );\r\n        \r\n        equal( \"three\", 3, \"Three, 3; equal fails\" );\r\n        equal( null, false, \"null, false; equal fails\" );\r\n    });\r\n    test( \"deepEqual test\", function() {\r\n        var obj = { foo: \"bar\" };\r\n        \r\n        deepEqual( obj, { foo: \"bar\" }, \"Two objects can be the same in value\" );\r\n    });\r\n    test( \"a test\", function() {\r\n        expect( 2 );\r\n        function calc( x, operation ) {\r\n            return operation( x );\r\n        }\r\n        \r\n        var result = calc( 2, function( x ) {\r\n            ok( true, \"calc() calls operation function\" );\r\n            return x * x;\r\n        });\r\n        \r\n        equal( result, 4, \"2 square equals 4\" );\r\n    });\r\n    test( \"a test\", 2, function() {\r\n        \r\n        function calc( x, operation ) {\r\n            return operation( x );\r\n        }\r\n        \r\n        var result = calc( 2, function( x ) {\r\n            ok( true, \"calc() calls operation function\" );\r\n            return x * x;\r\n        });\r\n        \r\n        equal( result, 4, \"2 square equals 4\" );\r\n    });\r\n    test( \"a test\", 1, function() {\r\n        var $qunit = $( \"#qunit\" );\r\n        \r\n        $qunit.on( \"click\", function() {\r\n            ok( true, \"body was clicked!\" );\r\n        });\r\n        \r\n        $qunit.trigger( \"click\" ).off(\'click\');\r\n    });\r\n    asyncTest( \"asynchronous test: one second later!\", function() {\r\n        expect( 1 );\r\n        \r\n        setTimeout(function() {\r\n            ok( true, \"Passed and ready to resume!\" );\r\n            start();\r\n        }, 1000);\r\n    });\r\n    asyncTest( \"asynchronous test: video ready to play\", 1, function() {\r\n        var $video = $( \"video\" );\r\n        \r\n        $video.on( \"canplaythrough\", function() {\r\n            ok( true, \"video has loaded and is ready to play\" );\r\n            start();\r\n        }).trigger(\'canplaythrough\');\r\n    });\r\n    \r\n    function KeyLogger( target ) {\r\n        if ( !(this instanceof KeyLogger) ) {\r\n            return new KeyLogger( target );\r\n        }\r\n        this.target = target;\r\n        this.log = [];\r\n        \r\n        var self = this;\r\n        \r\n        this.target.off( \"keydown\" ).on( \"keydown\", function( event ) {\r\n            self.log.push( event.keyCode );\r\n        });\r\n    }\r\n    test( \"keylogger api behavior\", function() {\r\n        \r\n        var event,\r\n            $doc = $( document ),\r\n            keys = KeyLogger( $doc );\r\n        \r\n        // trigger event\r\n        event = $.Event( \"keydown\" );\r\n        event.keyCode = 9;\r\n        $doc.trigger( event );\r\n        \r\n        // verify expected behavior\r\n        equal( keys.log.length, 1, \"a key was logged\" );\r\n        equal( keys.log[ 0 ], 9, \"correct key was logged\" );\r\n        \r\n    });\r\n    test( \"Appends a div\", function() {\r\n        var $fixture = $( \"#qunit-fixture\" );\r\n        \r\n        $fixture.append( \"<div>hello!</div>\" );\r\n        equal( $( \"div\", $fixture ).length, 1, \"div added successfully!\" );\r\n    });\r\n    test( \"Appends a span\", function() {\r\n        var $fixture = $( \"#qunit-fixture\" );\r\n        \r\n        $fixture.append(\"<span>hello!</span>\" );\r\n        equal( $( \"span\", $fixture ).length, 1, \"span added successfully!\" );\r\n    });\r\n    test( \"global pollution\", function() {\r\n        window.pollute = true;\r\n        ok( pollute, \"nasty pollution\" );\r\n    });\r\n/**\r\n    test(\'test1\', function(){\r\n        ok( 1== \'1\', \'passed\');\r\n    });\r\n\r\n    test(\'test2\', function(){\r\n        ok(true, \'this test is fine\');\r\n        var value = \'hello\';\r\n        equal(\'hello\', value, \'we expect value to be hello\');\r\n    });\r\n\r\n    module(\"Module A\");\r\n\r\n    test(\"first test within module\", function() {\r\n        ok(true, \"all pass\");\r\n    });\r\n\r\n    test(\"second test within module\", function() {\r\n        ok(true, \"all pass\");\r\n    });\r\n\r\n    module(\"Module B\");\r\n\r\n    test(\"some other test\", function() {\r\n        expect(2);\r\n        equal(true, false, \"failing test\");\r\n        equal(true, true, \"passing test\");\r\n    });\r\n\r\n    module(\"module2\", {\r\n        setup: function() {\r\n            ok(true, \"once extra assert per test\");\r\n            this.testData = \"foobar\";\r\n        },\r\n        teardown: function() {\r\n            ok(true, \"and one extra assert after each test\");\r\n        }\r\n    });\r\n    test(\"test with setup and teardown\", function() {\r\n        expect(2);\r\n        deepEqual (this.testData, \"foobar\");\r\n    });\r\n\r\n    asyncTest(\"异步测试\", function() {\r\n        expect(2);\r\n        $.getJSON(\"../editor/fileList.php\", function(result) {\r\n            equal(result.x, \"sss\");\r\n            equal(result.y, \"sss\");\r\n            start();\r\n        });\r\n    });\r\n*/\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/test/qunit-1.11.0.css,../script/test/qunit-1.11.0.js','JS 插件-单元测试[第三方]',NULL,'../image/preview/qunit.jpg',1,NULL,'2013-02-01 09:47:07',NULL,NULL,'','');
