   });\r\n    $(\'#addNow\').on(\'click\', function(){\r\n        var date = new Date();\r\n        var y = date.getFullYear(),\r\n            m = fillZero( date.getMonth()+1 ),\r\n            d = fillZero( date.getDate()),\r\n            h = fillZero( date.getHours()),\r\n            min = fillZero( date.getMinutes()),\r\n            s = fillZero( date.getSeconds() );\r\n        var a = y+\'-\'+m+\'-\'+d+\' \'+h+\':\'+min+\':\'+s;\r\n        $timeLine.addEvent({date:a, title:a, content:\'阿斯顿发撒旦法撒旦阿斯顿发撒旦法撒旦阿斯顿发撒旦法撒旦阿斯顿发撒旦法撒旦阿斯顿发撒旦法撒旦阿斯顿发撒旦法撒旦\'});\r\n    });\r\n    $(\'#addRandom\').on(\'click\', function(){\r\n        var date = new Date();\r\n        date = new Date(date.getTime() - Math.floor(31536000000*Math.random()));\r\n        var y = date.getFullYear(),\r\n            m = fillZero( date.getMonth()+1 ),\r\n            d = fillZero( date.getDate()),\r\n            h = fillZero( date.getHours()),\r\n            min = fillZero( date.getMinutes()),\r\n            s = fillZero( date.getSeconds() );\r\n        var a = y+\'-\'+m+\'-\'+d+\' \'+h+\':\'+min+\':\'+s;\r\n        $timeLine.addEvent({date:a, title:a, content:\'jkhkjhhjkhjkh\'});\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../style/style.css,../script/jquery.extend.js,../script/plugin/timeLine/timeLine.css,../script/plugin/timeLine/jquery.timeLine.js','JS 插件-时间轴（timeLine）测试版',NULL,'../image/preview/timeLine.jpg',1,NULL,'2013-04-15 10:59:41',NULL,NULL,'48,1,6,122','原创,JS 插件,JavaScript,jQuery 插件');
INSERT INTO `editor` VALUES (32,'<div class=\"case\">\r\n    <h3>翻转插件</h3>\r\n\r\n    <p>该插件依赖于 jQuery UI\r\n        <br />\r\n        <br />更多参考：\r\n        <a href=\"http://lab.smashup.it/flip\">\r\n            http://lab.smashup.it/flip\r\n        </a>\r\n    </p>\r\n    \r\n    <input type=\"button\" value=\"flip\" id=\"flipButton\" />\r\n    \r\n    <div id=\"flip\" class=\"flip\">该插件依赖于 jQuery UI</div>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}\r\n\r\n.flip{\r\n    width:390px;\r\n    height:510px;\r\n    margin:5px;\r\n    opacity:.9;\r\n    background:#c0c0c0;\r\n    text-align: center;\r\n    line-height: 4em;\r\n}','$(function(){\r\n    var $flip = $(\'#flip\').flip({\r\n        direction: \'lr\',\r\n        color: \'#e1e1e1\',\r\n        content: \'asdfsadfsadfasdf\'\r\n    });\r\n    \r\n    $(\'#flipButton\').on(\'click\', function(){\r\n        $flip.revertFlip();\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/lib/jQueryUI/jquery-ui-1.10.3.custom.min.css,../script/lib/jQueryUI/jquery-ui-1.10.3.custom.min.js,../script/plugin/flip/jquery.flip.min.js','JS 插件-翻转插件[第三方]',NULL,'../image/default/no-pic.png',1,NULL,'2013-04-15 11:13:46',NULL,NULL,'122','jQuery 插件');
INSERT INTO `editor` VALUES (33,'<div class=\"case\">\r\n    <div id=\"pagination\" class=\"module module-large\"></div>\r\n</div>','.case{\r\n    overflow: hidden;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}','$(function(){\r\n    var $pagination = $.pagination({\r\n        container: \'#pagination\',\r\n        count: 100,\r\n        pageIndex: 1,\r\n        pageSize: 10,\r\n        pageShow: 5,\r\n        action: function(page, size){\r\n            $pagination.triggerHandler(\'setPage\', [page, size]);\r\n        }\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../style/style.css,../script/jquery.extend.js,../script/plugin/pagination/jquery.pagination.js','JS 插件-分页插件（pagination）测试版',NULL,'../image/preview/pagination.jpg',1,NULL,'2013-04-15 11:22:36',NULL,NULL,'48,1,6,122','原创,JS 插件,JavaScript,jQuery 插件');
INSERT INTO `editor` VALUES (34,'<div class=\"case\">\r\n    <p>拖拽功能 需要 jQuery UI 相关库文件支持\r\n        <br />可能由于版本原因，使用新版的 jQuery UI 后，拖拽功能，会报错\r\n        <br />\r\n        <br />将第 58 行 .droppable(\'disable\'); 改为 .droppable().droppable(\'disable\');\r\n        <br />并注释第 213、229 行的 console.log 语句\r\n        <br />\r\n        <br />更多参考：\r\n        <a href=\"https://github.com/wesnolte/jOrgChart\">\r\n            https://github.com/wesnolte/jOrgChart\r\n        </a>\r\n    </p>\r\n    \r\n    <ul id=\"org\" style=\"display:none\">\r\n        <li>\r\n            Food\r\n            <ul>\r\n                <li id=\"beer\">Beer</li>\r\n                <li>Vegetables\r\n                    <a href=\"//wesnolte.com\" target=\"_blank\">Click me</a>\r\n                    <ul>\r\n                        <li>Pumpkin</li>\r\n                        <li>\r\n                            <a href=\"http://tquila.com\" target=\"_blank\">Aubergine</a>\r\n                        </li>\r\n                    </ul>\r\n                </li>\r\n                <li class=\"fruit\">Fruit\r\n                    <ul>\r\n                        <li>Apple\r\n                            <ul>\r\n                                <li>Granny Smith</li>\r\n                            </ul>\r\n                        </li>\r\n                        <li>Berries\r\n                            <ul>\r\n                                <li>Blueberry</li>\r\n                                <li>Raspberry</li>\r\n                                <li>Cucumber</li>\r\n                            </ul>\r\n                        </li>\r\n                    </ul>\r\n                </li>\r\n                <li>Bread</li>\r\n                <li class=\"collapsed\">Chocolate\r\n                    <ul>\r\n                        <li>Topdeck</li>\r\n                        <li>Reese\'s Cups</li>\r\n                    </ul>\r\n                </li>\r\n            </ul>\r\n        </li>\r\n    </ul>\r\n    <div id=\"chart\" class=\"orgChart\"></div>\r\n</div>','.case{\r\n    overflow: auto;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\n.jOrgChart .line{\r\n    border-color: #9b4400;\r\n}\r\n.jOrgChart .down{\r\n    background-color: #9b4400;\r\n}\r\nbody .jOrgChart .node{\r\n    border: #c0c0c0 solid 5px;\r\n    border-radius: 10px;\r\n    background: #44CEF6;\r\n    color: #efefef;\r\n}','$(function(){\r\n    $(\"#org\").jOrgChart({\r\n        chartElement:\'#chart\',\r\n        dragAndDrop:true\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/lib/jQueryUI/jquery-ui-1.10.3.custom.min.css,../script/lib/jQueryUI/jquery-ui-1.10.3.custom.min.js,../script/plugin/jOrgChart/jquery.jOrgChart.css,../script/plugin/jOrgChart/jquery.jOrgChart.js','JS 插件--组织结构图[第三方]',NULL,'../image/preview/jOrgChart.jpg',1,NULL,'2013-04-15 12:55:39',NULL,NULL,'122','jQuery 插件');
INSERT INTO `editor` VALUES (35,'<div class=\"case\">\r\n    <p>更多参考：\r\n        <a href=\"https://github.com/heelhook/chardin.js\">\r\n            https://github.com/heelhook/chardin.js\r\n        </a>\r\n    </p>\r\n    \r\n    <input type=\"button\" id=\"show\" value=\"显示注解\" style=\"float: left;\" />\r\n    \r\n    <div id=\"chardinExample\" class=\"module module-small module-metro module-blog\"\r\n        data-toggle=\"chardinjs\"\r\n        data-position=\"bottom\"\r\n        data-intro=\"这是一个动态磁砖\">\r\n        <h1 class=\"module_title\" data-toggle=\"chardinjs\" data-position=\"right\" data-intro=\"这是该磁砖的标题\">\r\n            博客 Blog\r\n        </h1>\r\n    </div>\r\n</div>','.case{\r\n    overflow: hidden;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\np{\r\n    color: #000;\r\n}\r\na{\r\n    color: #000;\r\n}','$(function(){\r\n    var chardinExample = $(\'body\').chardinJs();\r\n    \r\n    $(\'#show\').on(\'click\', function(){\r\n        chardinExample.toggle();\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../style/style.css,../script/plugin/chardin/chardinjs.css,../script/plugin/chardin/chardinjs.min.js','JS 插件-注解插件[第三方]',NULL,'../image/preview/chardin.jpg',1,NULL,'2013-05-09 13:49:17',NULL,NULL,'122','jQuery 插件');
INSERT INTO `editor` VALUES (36,'<div class=\"Container\" id=\"Container\">\r\n    <div class=\"module module-small module-metro module-metro module-blog\">\r\n        <h1 class=\"module_title\">博客</h1>\r\n        <span class=\"module_info\"></span>\r\n    </div>\r\n    <div class=\"module module-large\" id=\"warm\">\r\n        这是一个基于宽带为 960px 宽的页面的网格系统\r\n        <br />每个模块设定相同的外边距，可以达成栅格系统的效果\r\n        <br />比如 设置外边距为 5px，则可以达成一个 950 宽度 栅格间距为 10px 的栅格系统\r\n    </div>\r\n</div>','.Container{\r\n    overflow:hidden;\r\n    width: 960px !important;\r\n    padding-top: 0 !important;\r\n    background:url(\"../image/demo/grid.jpg\");\r\n}\r\n.module-large{\r\n    width: 950px !important;\r\n}\r\n#warm{\r\n    height:150px;\r\n    line-height:40px;\r\n}','','../script/lib/jQuery/jquery-1.10.2.min.js,../style/style.css','页面模板-网格系统',NULL,'../image/preview/grid-950.jpg',5,NULL,'2013-05-10 11:05:01',NULL,NULL,'','');
INSERT INTO `editor` VALUES (37,'<div class=\"case\">\r\n    <p>更多参考：\r\n        <a href=\"http://sdlddr.iteye.com/blog/1856389\">\r\n            http://sdlddr.iteye.com/blog/1856389\r\n        </a>\r\n    </p>\r\n    \r\n    <div id=\"gooFlow\"></div>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}','var property={\r\n        width:700,\r\n        height:380,\r\n        toolBtns:[\"task\",\"node\",\"chat\",\"state\",\"plug\"],\r\n        haveHead:true,\r\n        headBtns:[\"new\",\"open\",\"save\",\"undo\",\"redo\",\"reload\"],//如果haveHead=true，则定义HEAD区的按钮\r\n        haveTool:true,\r\n        useOperStack:true\r\n    },\r\n    remark={\r\n        cursor:\"选择指针\",\r\n        direct:\"转换连线\",\r\n        start:\"开始结点\",\r\n        end:\"结束结点\",\r\n        task:\"任务结点\",\r\n        node:\"自动结点\",\r\n        chat:\"决策结点\",\r\n        state:\"状态结点\",\r\n        plug:\"附加插件\",\r\n        fork:\"分支结点\",\r\n        \"join\":\"联合结点\",\r\n        complex:\"复合结点\",\r\n        group:\"组织划分框编辑开关\"\r\n    },\r\n    demo,\r\n    node = {\r\n        \'gooFlow_node_2\':{\r\n            name:\'用户打开网站首页\', left:145, top:17, type:\'start\', width:24, height:24},\r\n        \'gooFlow_node_3\':{\r\n            name:\'点击链接\', left:108, top:96, type:\'fork\', width:86, height:24\r\n        },\r\n        \'gooFlow_node_7\':{\r\n            name:\'看功能演示\', left:64, top:205, type:\'state\', width:86, height:24\r\n        },\r\n        \'gooFlow_node_12\':{\r\n            name:\'查小区相册\', left:163, top:269, type:\'state\', width:86, height:24\r\n        },\r\n        \'gooFlow_node_16\':{\r\n            name:\'登录\', left:301, top:9, type:\'chat\', width:86, height:24\r\n        },\r\n        \'gooFlow_node_23\':{\r\n            name:\'注册\', left:305, top:93, type:\'chat\', width:86, height:24\r\n        },\r\n        \'gooFlow_node_38\':{\r\n            name:\'点击链接\', left:426, top:159, type:\'fork\', width:86, height:24\r\n        },\r\n   