\n  });\r\n\r\n  $(\'#demo3\').find(\'a\').fancybox({\r\n    prevEffect : \'none\',\r\n    nextEffect : \'none\',\r\n  \r\n    closeBtn  : false,\r\n    arrows    : false,\r\n    nextClick : true,\r\n  \r\n    helpers : {\r\n      title:{\r\n        type:\'outside\'\r\n      },\r\n      thumbs : {\r\n        width  : 50,\r\n        height : 50\r\n      }\r\n    }\r\n  });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/fancybox/jquery.fancybox.css,../script/plugin/fancybox/jquery.fancybox-buttons.css,../script/plugin/fancybox/jquery.fancybox-thumbs.css,../script/plugin/fancybox/jquery.fancybox.js,../script/plugin/fancybox/jquery.fancybox-buttons.js,../script/plugin/fancybox/jquery.fancybox-thumbs.js','JS 插件-fancybox[第三方]',NULL,'../image/preview/fancybox.jpg',1,NULL,'2013-07-16 11:31:01',NULL,NULL,'122,125','jQuery 插件,相册效果');
INSERT INTO `editor` VALUES (44,'<div class=\"case\">\r\n    <p>更多参考：\r\n        <a href=\"https://github.com/jeromeetienne/jquery-qrcode\">\r\n            https://github.com/jeromeetienne/jquery-qrcode\r\n        </a>\r\n    </p>\r\n    \r\n    <p>使用 table 生成二维码</p>\r\n    <div id=\"qrcodeTable\"></div>\r\n    \r\n    <p>使用 canvas 生成二维码</p>\r\n    <div id=\"qrcodeCanvas\"></div>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\ndiv{\r\n  margin:20px;\r\n}','$(function(){\r\n  \t$(\'#qrcodeTable\').qrcode({\r\n\t\trender:\"table\",\r\n\t\ttext:\"http://www.zmkm.com\"\r\n\t});\t\r\n\t$(\'#qrcodeCanvas\').qrcode({\r\n        text:\"http://www.zmkm.com\"\r\n\t});\t\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/qrcode/jquery.qrcode.min.js','JS 插件-二维码生成器[第三方]',NULL,'../image/preview/qrcode.jpg',1,NULL,'2013-07-16 17:30:59',NULL,NULL,'122','jQuery 插件');
INSERT INTO `editor` VALUES (45,'<!-- 上箭头 -->\r\n<div class=\"icon arrow arrow-up\"></div>\r\n\r\n<!-- 下箭头 -->\r\n<div class=\"icon arrow arrow-down\"></div>\r\n\r\n<!-- 左箭头 -->\r\n<div class=\"icon arrow arrow-left\"></div>\r\n\r\n<!-- 右箭头 -->\r\n<div class=\"icon arrow arrow-right\"></div>\r\n\r\n<!-- 上左箭头 -->\r\n<div class=\"icon arrow arrow-topLeft\"></div>\r\n\r\n<!-- 上右箭头 -->\r\n<div class=\"icon arrow arrow-topRight\"></div>\r\n\r\n<!-- 下左箭头 -->\r\n<div class=\"icon arrow arrow-bottomLeft\"></div>\r\n\r\n<!-- 下右箭头 -->\r\n<div class=\"icon arrow arrow-bottomRight\"></div>\r\n\r\n<!-- 曲线箭头 -->\r\n<div class=\"icon arrow arrow-curved\"></div>\r\n\r\n<!-- 梯形 -->\r\n<div class=\"icon trapezoid\"></div>\r\n\r\n<!-- 六角星形 -->\r\n<div class=\"icon star star-six\"></div>\r\n\r\n<!-- 五角星形 -->\r\n<div class=\"icon star star-five\"></div>\r\n\r\n<!-- 五边形 -->\r\n<div class=\"icon pentagon\"></div>\r\n\r\n<!-- 六边形 -->\r\n<div class=\"icon hexagon\"></div>\r\n\r\n<!-- 八边形 -->\r\n<div class=\"icon octagon\"></div>\r\n\r\n<!-- 心形 -->\r\n<div class=\"icon heart\"></div>\r\n\r\n<!-- 无限环 -->\r\n<div class=\"icon infinity\"></div>\r\n\r\n<!-- 菱形 -->\r\n<div class=\"icon square\"></div>\r\n\r\n<!-- 蛋形 -->\r\n<div class=\"icon egg\"></div>\r\n\r\n<!-- 太极 -->\r\n<div class=\"icon taiji\"></div>\r\n\r\n<!-- 奖章 -->\r\n<div class=\"icon ribbon\"></div>\r\n\r\n<!-- 屏幕 -->\r\n<div class=\"icon screen\"></div>','.icon{float:left; margin:10px;}\r\n.arrow, .star{width:0; height:0;}\r\n\r\n.arrow-up{\r\n    border-left: 50px solid transparent;\r\n    border-right: 50px solid transparent;\r\n    border-bottom: 100px solid red;\r\n}\r\n\r\n.arrow-down{\r\n    border-left: 50px solid transparent;\r\n    border-right: 50px solid transparent;\r\n    border-top: 100px solid red;\r\n}\r\n\r\n.arrow-left{\r\n    border-top: 50px solid transparent;\r\n    border-bottom: 50px solid transparent;\r\n    border-right: 100px solid red;\r\n}\r\n\r\n.arrow-right{\r\n    border-top: 50px solid transparent;\r\n    border-bottom: 50px solid transparent;\r\n    border-left: 100px solid red;\r\n}\r\n\r\n.arrow-topLeft{\r\n    border-top:100px solid red;\r\n    border-right:100px solid transparent;\r\n}\r\n\r\n.arrow-topRight{\r\n    border-top:100px solid red;\r\n    border-left:100px solid transparent;\r\n}\r\n\r\n.arrow-bottomLeft{\r\n    border-bottom:100px solid red;\r\n    border-right:100px solid transparent;\r\n}\r\n  \r\n.arrow-bottomRight{\r\n    border-bottom:100px solid red;\r\n    border-left:100px solid transparent;\r\n}\r\n\r\n.arrow-curved{\r\n    position: relative;\r\n\r\n    border-top: 9px solid transparent;\r\n    border-right: 9px solid red;\r\n  \r\n    -webkit-transform: rotate(10deg);\r\n    -moz-transform: rotate(10deg);\r\n    -ms-transform: rotate(10deg);\r\n    -o-transform: rotate(10deg);\r\n    transform: rotate(10deg);\r\n}\r\n.arrow-curved:after{\r\n    position: absolute;\r\n    \r\n    top: -12px;\r\n    left: -9px;\r\n    width: 12px;\r\n    height: 12px;  \r\n  \r\n    border: 0 solid transparent;\r\n    border-top: 3px solid red;\r\n    border-radius: 20px 0 0;\r\n    \r\n    -webkit-transform: rotate(45deg);\r\n    -moz-transform: rotate(45deg);\r\n    -ms-transform: rotate(45deg);\r\n    -o-transform: rotate(45deg);\r\n    transform: rotate(45deg);\r\n      \r\n    content: \"\";\r\n}\r\n    \r\n.trapezoid{\r\n    width: 100px;\r\n    height: 0;\r\n\r\n    border-bottom: 100px solid red;\r\n    border-left: 50px solid transparent;\r\n    border-right: 50px solid transparent;\r\n}\r\n      \r\n.star-six{\r\n    position: relative;\r\n          \r\n    border-left:50px solid transparent;\r\n    border-right: 50px solid transparent;\r\n    border-bottom: 100px solid red;\r\n}\r\n.star-six:after{\r\n    position: absolute;\r\n\r\n    top: 30px;\r\n    left: -50px;\r\n\r\n    width: 0;\r\n    height: 0;\r\n\r\n    border-left: 50px solid transparent;\r\n    border-right: 50px solid transparent;\r\n    border-top: 100px solid red;\r\n\r\n    content: \"\";\r\n}\r\n            \r\n.star-five{\r\n    display: block;\r\n    position: relative;\r\n\r\n    margin: 50px 0;\r\n    border-bottom: 70px solid red;\r\n    border-left: 100px solid transparent;\r\n    border-right: 100px solid transparent;\r\n\r\n    color: red;\r\n\r\n    -webkit-transform: rotate(35deg);\r\n    -moz-transform: rotate(35deg);\r\n    -ms-transform: rotate(35deg);\r\n    -o-transform: rotate(35deg);\r\n    transform: rotate(35deg);\r\n}\r\n.star-five:before{\r\n    display: block;\r\n    position: absolute;\r\n\r\n    top: -45px;\r\n    left: -65px;\r\n    \r\n    width: 0;\r\n    height: 0;\r\n    \r\n    border-bottom: 80px solid red;\r\n    border-left: 30px solid transparent;\r\n    border-right: 30px solid transparent;\r\n\r\n    -webkit-transform: rotate(-35deg);\r\n    -moz-transform: rotate(-35deg);\r\n    -ms-transform: rotate(-35deg);\r\n    -o-transform: rotate(-35deg);\r\n    transform: rotate(-35deg);\r\n\r\n    content: \"\";\r\n}\r\n.star-five:after{\r\n    display: block;\r\n    position: absolute;\r\n\r\n    top: 3px;\r\n    left: -105px;\r\n\r\n    width: 0;\r\n    height: 0;\r\n\r\n    border-bottom: 70px solid red;\r\n    border-left: 100px solid transparent;\r\n    border-right: 100px solid transparent;\r\n\r\n    -webkit-transform: rotate(-70deg);\r\n    -moz-transform: rotate(-70deg);\r\n    -ms-transform: rotate(-70deg);\r\n    -o-transform: rotate(-70deg);\r\n    transform: rotate(-70deg);\r\n\r\n    content: \"\";\r\n    color: red;\r\n}\r\n\r\n.pentagon{\r\n    position: relative;\r\n\r\n    top: 35px;\r\n\r\n    width: 54px;\r\n\r\n    border-width: 50px 18px 0;\r\n    border-style: solid;\r\n    border-color: red transparent;\r\n}\r\n.pentagon:before{\r\n    position: absolute;\r\n\r\n    top: -85px;\r\n    left: -18px;\r\n    \r\n    width: 0;\r\n    height: 0;\r\n                \r\n    border-width: 0 45px 35px;\r\n    border-style: solid;\r\n    border-color: transparent transparent red;\r\n\r\n    c