INSERT INTO `editor` VALUES (55,'<h1>密码强度测试</h1>\r\n\r\n<form id=\"myform\" action=\"\" method=\"get\" accept-charset=\"utf-8\">\r\n    \r\n    <input id=\"myPassword\" type=\"password\" name=\"\" value=\"\">\r\n    \r\n</form>\r\n\r\n<p>更多参考：\r\n    <a href=\"https://github.com/aarondo/Strength.js\">\r\n        https://github.com/aarondo/Strength.js\r\n    </a>\r\n</p>','html{\r\n    background:#4EC094;\r\n    font-family: \'Lato\', sans-serif;\r\n    color:white;\r\n}\r\n#myform input[type=\"password\"],\r\n#myform input[type=\"text\"]{\r\n    background:transparent;\r\n    border: 2px solid #46AC84;\r\n    color: #777;\r\n    font-family: \"Lato\", sans-serif;\r\n    font-size: 14px;\r\n    padding: 9px 5px;\r\n    height: 21px;\r\n    text-indent: 6px;\r\n    -webkit-appearance: none;\r\n    -webkit-border-radius: 6px;\r\n    -moz-border-radius: 6px;\r\n    border-radius: 6px;\r\n    -webkit-box-shadow: none;\r\n    -moz-box-shadow: none;\r\n    box-shadow: none;\r\n    -webkit-transition: border .25s linear, color .25s linear;\r\n    -moz-transition: border .25s linear, color .25s linear;\r\n    -o-transition: border .25s linear, color .25s linear;\r\n    transition: border .25s linear, color .25s linear;\r\n    -webkit-backface-visibility: hidden;\r\n    width:100%;\r\n}\r\n#myform input[type=\"password\"]:focus,\r\n#myform input[type=\"text\"]:focus{\r\n    outline:0;\r\n}\r\n#myform{\r\n    width: 500px;\r\n    margin: 0 auto;\r\n    position: relative;\r\n    margin-bottom:60px;\r\n}\r\n.strength_meter{\r\n    position: absolute;\r\n    left: 0px;\r\n    top: 0px;\r\n    width: 100%;\r\n    height: 43px;\r\n    z-index:-1;\r\n    border-radius:5px;\r\n    padding-right:13px;\r\n}\r\n.button_strength {\r\n    text-decoration: none;\r\n    color: #FFF;\r\n    font-size: 13px;\r\n}\r\n.strength_meter div{\r\n    width:0%;\r\n    height: 43px;\r\n    text-align: right;\r\n    color: #000;\r\n    line-height: 43px;\r\n    -webkit-transition: all .3s ease-in-out;