INSERT INTO `editor` VALUES (57,'<div class=\"case\">\r\n    <p>更多参考：\r\n        <a href=\"https://github.com/ozeetee/jqtimeline\">\r\n            https://github.com/ozeetee/jqtimeline\r\n        </a>\r\n    </p>\r\n    \r\n    <div id=\"myTimeline\"></div>\r\n</div>','.case{\r\n    overflow: auto;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}','$(function(){\r\n    var ev = [{\r\n        id: 1,\r\n        name: \"Joined Faecbook\",\r\n        on: new Date(2011,2,15)\r\n    }, {\r\n        id: 11,\r\n        name: \"Updated my first status message\",\r\n        on: new Date(2011,2,17)\r\n    }, {\r\n        id: 2,\r\n        name: \"Joined Twitter\",\r\n        on: new Date(2011,5,30)\r\n    }, {\r\n        id: 9,\r\n        name: \"Created a new blogger account\",\r\n        on: new Date(2011,7,5)\r\n    }, {\r\n        id: 3,\r\n        name: \"Trip to Australia\",\r\n        on: new Date(2012,5,5)\r\n    }, {\r\n        id: 4,\r\n        name: \"Trip to New Zealand\",\r\n        on: new Date(2012,5,30)\r\n    }, {\r\n        id: 5,\r\n        name: \"Awesome new year\",\r\n        on: new Date(2013,0,1)\r\n    }, {\r\n        id: 6,\r\n        name: \"Will go to Moon\",\r\n        on: new Date(2013,6,10)\r\n    }, {\r\n        id: 7,\r\n        name: \"Will go to Mars\",\r\n        on: new Date(2014,6,10)\r\n    }, {\r\n        id: 8,\r\n        name: \"No idea about this date\",\r\n        on: new Date(2015,6,10)\r\n    }];\r\n    \r\n    var tl = $(\'#myTimeline\').jqtimeline({\r\n        events: ev,\r\n        numYears: 4,\r\n        startYear: 2011,\r\n        click: function(e,event){\r\n            alert(event.name);\r\n        }\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/jqtimeline/jquery.jqtimeline.css,../script/plugin/jqtimeline/jquery.jqtimeline.js','JS 插件-时间轴[第三方]',NULL,'../image/preview/jqtimeline.jpg',1,NULL,'2013-07-20 11:29:10',NULL,NULL,'122','jQuery 插件');
INSERT INTO `editor` VALUES (58,'<div class=\"case\">\r\n    <p>更多参考：\r\n        <a href=\"http://fredhq.com/projects/roundabout\">\r\n            http://fredhq.com/projects/roundabout\r\n        </a>\r\n    </p>\r\n    \r\n    <div>\r\n        <h2>标准模式</h2>\r\n        <ul id=\"standard\">\r\n            <li><img src=\"../image/demo/demoImage-1.jpg\" alt=\"\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-2.jpg\" alt=\"\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-3.jpg\" alt=\"\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-4.jpg\" alt=\"\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-5.jpg\" alt=\"\" /></li>\r\n        </ul>\r\n        \r\n        <h2>立体模式</h2>\r\n        <ul id=\"stereoscopic\">\r\n            <li><img src=\"../image/demo/demoImage-1.jpg\" alt=\"\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-2.jpg\" alt=\"\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-3.jpg\" alt=\"\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-4.jpg\" alt=\"\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-5.jpg\" alt=\"\" /></li>\r\n        </ul>\r\n    </div>\r\n</div>','.case{\r\n    overflow: hidden;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\nul{\r\n    list-style: none;\r\n    padding: 0;\r\n    margin: 0 auto;\r\n    width: 42em;\r\n    height: 24em;\r\n}\r\nli{\r\n    height: 20em;\r\n    width: 30em;\r\n    background-color: #ccc;\r\n    text-align: center;\r\n    cursor: pointer;\r\n}\r\nli.roundabout-in-focus{\r\n    cursor: default;\r\n}\r\nli span{\r\n    display: block;\r\n    padding-top: 6em;\r\n}\r\nimg{\r\n    width: 100%;\r\n    height: 100%;\r\n}','$(function(){\r\n    $(\'#standard\').roundabout();\r\n    \r\n    $(\'#stereoscopic\').roundabout({\r\n        tilt: 8,\r\n        childSelector: \'li\',\r\n        minZ: 100,\r\n        maxZ: 300\r\n    });\r\n});','../script/lib/jQue