INSERT INTO `editor` VALUES (61,'<div class=\"case\">\r\n    <p>更多参考：\r\n        <a href=\"http://www.littlewebthings.com/projects/countdown/\">\r\n            http://www.littlewebthings.com/projects/countdown/\r\n        </a>\r\n    </p>\r\n    \r\n    <div id=\"countdown_dashboard\">\r\n        <div class=\"dash weeks_dash\">\r\n            <span class=\"dash_title\">周</span>\r\n            <div class=\"digit\">0</div>\r\n            <div class=\"digit\">0</div>\r\n        </div>\r\n        \r\n        <div class=\"dash days_dash\">\r\n            <span class=\"dash_title\">天</span>\r\n            <div class=\"digit\">0</div>\r\n            <div class=\"digit\">0</div>\r\n        </div>\r\n        \r\n        <div class=\"dash hours_dash\">\r\n            <span class=\"dash_title\">小时</span>\r\n            <div class=\"digit\">0</div>\r\n            <div class=\"digit\">0</div>\r\n        </div>\r\n        \r\n        <div class=\"dash minutes_dash\">\r\n            <span class=\"dash_title\">分钟</span>\r\n            <div class=\"digit\">0</div>\r\n            <div class=\"digit\">0</div>\r\n        </div>\r\n        \r\n        <div class=\"dash seconds_dash\">\r\n            <span class=\"dash_title\">秒</span>\r\n            <div class=\"digit\">0</div>\r\n            <div class=\"digit\">0</div>\r\n        </div>\r\n        \r\n    </div>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}','$(function() {\r\n    var date = new Date();\r\n    \r\n    date = new Date( date.getTime() + 24*60*60*1000 );\r\n    $(\'#countdown_dashboard\').countDown({\r\n        targetDate: {\r\n            \'day\': date.getDate(),\r\n            \'month\': date.getMonth()+1,\r\n            \'year\': date.getFullYear(),\r\n            \'hour\': date.getHours(),\r\n            \'min\': date.getMinutes(),\r\n            \'sec\': date.getSeconds()\r\n        }\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/lwtCountdown/jquery.lwtCountdown.css,../script/plugin/lwtCountdown/jquery.lwtCountdown-1.0.js','JS 插件-倒计时[第三方]',NULL,'../image/preview/lwtCountdown.jpg',1,NULL,'2013-07-20 15:38:52',NULL,NULL,'122','jQuery 插件');
INSERT INTO `editor` VALUES (62,'<div class=\"fallback-message\">\r\n    <p>您的浏览器不支持 impress.js 所需要的功能，请使用最新版本的浏览器</p>\r\n</div>\r\n\r\n<div id=\"impress\">\r\n    <div id=\"bored\" class=\"step slide\" data-x=\"-1000\" data-y=\"-1500\">\r\n        <q>Aren\'t you just <b>bored</b> with all those slides-based presentations?</q>\r\n    </div>\r\n    \r\n    <div class=\"step slide\" data-x=\"0\" data-y=\"-1500\">\r\n        <q>Don\'t you think that presentations given <strong>in modern browsers</strong> shouldn\'t <strong>copy the limits</strong> of \'classic\' slide decks?</q>\r\n    </div>\r\n    \r\n    <div class=\"step slide\" data-x=\"1000\" data-y=\"-1500\">\r\n        <q>Would you like to <strong>impress your audience</strong> with <strong>stunning visualization</strong> of your talk?</q>\r\n    </div>\r\n    \r\n    <div id=\"title\" class=\"step\" data-x=\"0\" data-y=\"0\" data-scale=\"4\">\r\n        <span class=\"try\">then you should try</span>\r\n        <h1>impress.js<sup>*</sup></h1>\r\n        <span class=\"footnote\"><sup>*</sup> no rhyme intended</span>\r\n    </div>\r\n    \r\n    <div id=\"its\" class=\"step\" data-x=\"850\" data-y=\"3000\" data-rotate=\"90\" data-scale=\"5\">\r\n        <p>It\'s a <strong>presentation tool</strong> <br/>\r\n            inspired by the idea behind <a href=\"http://prezi.com\">prezi.com</a> <br/>\r\n            and based on the <strong>power of CSS3 transforms and transitions</strong> in modern browsers.</p>\r\n    </div>\r\n    \r\n    <div id=\"big\" class=\"step\" data-x=\"3500\" data-y=\"2100\" data-rotate=\"180\" data-scale=\"6\">\r\n        <p>visualize your <b>big</b> <span class=\"thoughts\">thoughts</span></p>\r\n    </div>\r\n    \r\n    <div id=\"tiny\" class=\"step\" data-x=\"2825\" data-y=\"2325\" data-z=\"-3000\" data-rotate=\"300\" data-scale=\"1\">\r\n        <p>and <b>tiny</b> ideas</p>\r\n    </div>\r\n    \r\n    <div id=\"ing\" class=\"step\" data-x=\"3500\" data-y=\"-850\" data-rotate=\"270\" data-scale=\"6\">\r\n        <p>by <b class=\"positioning\">positioning</b>, <b class=\"rotating\">rotating</b> and <b class=\"scaling\">scaling</b> them on an infinite canvas</p>\r\n    </div>\r\n    \r\n    <div id=\"imagination\" class=\"step\" data-x=\"6700\" data-y=\"-300\" data-scale=\"6\">\r\n        <p>the only <b>limit</b> is your <b class=\"imagination\">imagination</b></p>\r\n    </div>\r\n    \r\n    <div id=\"source\" class=\"step\" data-x=\"6300\" data-y=\"2000\" data-rotate=\"20\" data-scale=\"4\">\r\n        <p>want to know more?</p>\r\n        <q><a href=\"http://github.com/bartaz/impress.js\">use the source</a>, Luke!</q>\r\n    </div>\r\n    \r\n    <div id=\"one-more-thing\" class=\"step\" data-x=\"6000\" data-y=\"4000\" data-scale=\"2\">\r\n        <p>one more thing...</p>\r\n    </div>\r\n    \r\n    <div id=\"its-in-3d\" class=\"step\" data-x=\"6200\" data-y=\"4300\" data-z=\"-100\" data-rotate-x=\"-40\" data-rotate-y=\"10\" data-scale=\"2\">\r\n        <p><span class=\"have\">have</span>\r\n            <span class=\"you\">you</span>\r\n            <span class=\"noticed\">noticed</span>\r\n            <span class=\"its\">it\'s</span>\r\n            <span class=\"in\">in</span>\r\n            <b>3D<sup>*</sup></b>?\r\n        </p>\r\n        <span class=\"footnote\">* beat that, prezi ;)</span>\r\n    </div>\r\n    \r\n    <div id=\"overview\" class=\"step\" data-x=\"3000\" data-y=\"1500\" data-scale=\"10\">\r\n    </div>\r\n</div>','.fallback-message {\r\n    font-family: sans-serif;\r\n    line-height: 1.3;\r\n    \r\n    width: 780px;\r\n    padding: 10px 10px 0;\r\n    margin: 20px auto;\r\n    \r\n    border: 1px solid #E4C652;\r\n    border-radius: 10px;\r\n    background: #EEDC94;\r\n}\r\n\r\n.fallback-message p {\r\n    margin-bottom: 10px;\r\n}\r\n\r\n.impress-supported .fallback-message {\r\n    display: none;\r\n}\r\n\r\n/*\r\nNow let\'s style the presentation steps.\r\n\r\nWe start with basics to make sure it displays correctly in everywhere ...\r\n*/\r\n\r\n.step {\r\n    position: relative;\r\n    width: 900px;\r\n    padding: 40px;\r\n    margin: 20px auto;\r\n    \r\n    -webkit-box-sizing: border-box;\r\n    -moz-box-sizing:    border-box;\r\n    -ms-box-sizing:     border-box;\r\n    -o-box-sizing:      border-box;\r\n    box-sizing:         border-box;\r\n    \r\n    font-family: \'PT Serif\', georgia, serif;\r\n    font-size: 48px;\r\n    line-height: 1.5;\r\n}\r\n\r\n/*\r\n... and we enhance the styles for impress.js.\r\n\r\nBasically we remove the margin and make inactive steps a little bit transparent.\r\n*/\r\n.impress-enabled .step {\r\n    margin: 0;\r\n    opacity: 0.3;\r\n    \r\n    -webkit-transition: opacity 1s;\r\n    -moz-transition:    opacity 1s;\r\n    -ms-transition:     opacity 1s;\r\n    -o-transition:      opacity 1s;\r\n    transition:         opacity 1s;\r\n}\r\n\r\n.impress-enabled .step.active { opacity: 1 }\r\n\r\n/*\r\nThese \'slide\' step styles were heavily inspired by HTML5 Slides:\r\nhttp://html5slides.googlecode.com/svn/trunk/styles.css\r\n\r\n;)\r\n\r\nThey cover everything what you see on first three steps of the demo.\r\n*/\r\n.slide {\r\n    display: block;\r\n    \r\n    width: 900px;\r\n    height: 700px;\r\n    padding: 40px 60px;\r\n    \r\n    background-color: white;\r\n    border: 1px solid rgba(0, 0, 0, .3);\r\n    border-radius: 10px;\r\n    box-shadow: 0 2px 6px rgba(0, 0, 0, .1);\r\n    \r\n    color: rgb(102, 102, 102);\r\n    text-shadow: 0 2px 2px rgba(0, 0, 0, .1);\r\n    \r\n    font-family: \'Open Sans\', Arial, sans-serif;\r\n    font-size: 30px;\r\n    line-height: 36px;\r\n    letter-spacing: -1px;\r\n}\r\n\r\n.slide q {\r\n    display: block;\r\n    font-size: 50px;\r\n    line-height: 72px;\r\n    \r\n    margin-top: 100px;\r\n}\r\n\r\n.slide q strong {\r\n    white-space: nowrap;\r\n}','/**\r\n *  更多参考  https://github.com/bartaz/impress.js/\r\n *  中文版    http://www.cnblogs.com/Darren_code/archive/2013/01/04/impressjs.html\r\n */\r\n$(function(){\r\n    impress().init();\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/impress/impress.js','JS 插件-幻灯片[第三方]',NULL,'../image/preview/impress.jpg',1,NULL,'2013-07-22 09:14:29',NULL,NULL,'122','jQuery 插件');
INSERT INTO `editor` VALUES (63,'<div class=\"case\">\r\n    <p>更多参考：\r\n        <a href=\"http://tutorialzine.com/2010/07/colortips-jquery-tooltip-plugin/\">\r\n            http://tutorialzine.com/2010/07/colortips-jquery-tooltip-plugin/\r\n        </a>\r\n    </p>\r\n    \r\n    <a href=\"#\" id=\"tips_1\" title=\"这是一个黄色提示\">提示1</a>\r\n    \r\n    <a href=\"#\" id=\"tips_2\" title=\"这是一个红色提示\">提示2</a>\r\n    \r\n    <a href=\"#\" id=\"tips_3\" title=\"这是一个绿色提示\">提示3</a>\r\n    \r\n    <a href=\"#\" id=\"tips_4\" title=\"这是一个白色提示\">提示4</a>\r\n    \r\n    <a href=\"#\" id=\"tips_5\" title=\"这是一个蓝色提示\">提示5</a>\r\n    \r\n    <a href=\"#\" id=\"tips_6\" title=\"这是一个黑色提示\">提示6</a>\r\n    \r\n</div>','.case{\r\n    overflow: hidden;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\np a{\r\n    float: none;\r\n    margin: 0;\r\n}\r\n\r\na{\r\n    float: left;\r\n    margin:50px 100px;\r\n}','$(function(){\r\n    \r\n    $(\'#tips_1\').colorTip({\r\n        color:\'yellow\'\r\n    });\r\n    \r\n    $(\'#tips_2\').colorTip({\r\n        color:\'red\'\r\n    });\r\n    \r\n    \r\n    $(\'#tips_3\').colorTip({\r\n        color:\'green\'\r\n    });\r\n    \r\n    $(\'#tips_4\').colorTip({\r\n        color:\'white\'\r\n    });\r\n    \r\n    $(\'#tips_5\').colorTip({\r\n        color:\'blue\'\r\n    });\r\n    \r\n    $(\'#tips_6\').colorTip({\r\n        color:\'black\'\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/colortip/colortip-1.0-jquery.css,../script/plugin/colortip/colortip-1.0-jquery.js','JS 插件-工具提示-1[第三方]',NULL,'../image/preview/colortip.jpg',1,NULL,'2013-07-22 13:44:01',NULL,NULL,'122','jQuery 插件');
INSERT INTO `editor` VALUES (64,'<div class=\"case\">\r\n    <p>更多参考：\r\n        <a href=\"https://github.com/drewwilson/TipTip\">\r\n            https://github.com/drewwilson/TipTip\r\n        </a>\r\n    </p>\r\n    \r\n    <a href=\"#\" id=\"tipTip\" title=\"这是一个提示\">提示</a>\r\n</div>','.case{\r\n    overflow: hidden;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\na{\r\n    margin: 50px;\r\n}','$(function(){\r\n    $(\"#tipTip\").tipTip();\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/tipTip/jquery.tipTip.css,../script/plugin/tipTip/jquery.tipTip.minified.js','JS 插件-工具提示-2[第三方]',NULL,'../image/preview/tipTip.jpg',1,NULL,'2013-07-22 14:23:26',NULL,NULL,'122','jQuery 插件');
INSERT INTO `editor` VALUES (65,'<div class=\"case\">\r\n    <p>更多参考：\r\n        <a href=\"https://github.com/akquinet/jquery-toastmessage-plugin\">\r\n            https://github.com/akquinet/jquery-toastmessage-plugin\r\n        </a>\r\n    </p>\r\n    \r\n    <div>\r\n        <h2>自动消失的提示</h2>\r\n        <input type=\"button\" value=\"成功提示\" id=\"succ\" />\r\n        <input type=\"button\" value=\"消息提醒\" id=\"msg\"/>\r\n        <input type=\"button\" value=\"警告提示\" id=\"warning\"/>\r\n        <input type=\"button\" value=\"错误提示\" id=\"error\"/>\r\n    </div>\r\n    \r\n    <div>\r\n        <h2>固定的提示</h2>\r\n        <input type=\"button\" value=\"成功提示\" id=\"succSticky\"/>\r\n        <input type=\"button\" value=\"消息提醒\" id=\"msgSticky\"/>\r\n        <input type=\"button\" value=\"警告提示\" id=\"warningSticky\"/>\r\n        <input type=\"button\" value=\"错误提示\" id=\"errorSticky\"/>\r\n    </div>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}','$(function(){\r\n    $(\'#succ\').on(\'click\', function(){\r\n        $.fn.toastmessage(\'showSuccessToast\', \'这是一个成功提示\');\r\n    });\r\n    \r\n    $(\'#succSticky\').on(\'click\', function(){\r\n        $.fn.toastmessage(\'showToast\', {\r\n            text:\'这是一个不会消失的成功提示\',\r\n            sticky:true,\r\n            position:\'top-right\',\r\n            type:\'success\',\r\n            closeText:\'\',\r\n            close:function(){}\r\n        });\r\n    });\r\n    \r\n    $(\'#msg\').on(\'click\', function(){\r\n        $.fn.toastmessage(\'showNoticeToast\', \'这是一个消息提示\');\r\n    });\r\n    \r\n    $(\'#msgSticky\').on(\'click\', function(){\r\n        $.fn.toastmessage(\'showToast\', {\r\n            text:\'这是一个不会消失的消息提示\',\r\n            sticky:true,\r\n            position:\'top-right\',\r\n            type:\'notice\',\r\n            closeText:\'\',\r\n            close:function(){}\r\n        });\r\n    });\r\n    \r\n    $(\'#warning\').on(\'click\', function(){\r\n        $.fn.toastmessage(\'showWarningToast\', \'这是一个警告提示\');\r\n    });\r\n    \r\n    $(\'#warningSticky\').on(\'click\', function(){\r\n        $.fn.toastmessage(\'showToast\', {\r\n            text:\'这是一个不会消失的警告提示\',\r\n            sticky:true,\r\n            position:\'top-right\',\r\n            type:\'warning\',\r\n            closeText:\'\',\r\n            close:function(){}\r\n        });\r\n    });\r\n    \r\n    $(\'#error\').on(\'click\', function(){\r\n        $.fn.toastmessage(\'showErrorToast\', \'这是一个错误提示\');\r\n    });\r\n    \r\n    $(\'#errorSticky\').on(\'click\', function(){\r\n        $.fn.toastmessage(\'showToast\', {\r\n            text:\'这是一个不会消失的错误提示\',\r\n            sticky:true,\r\n            position:\'top-right\',\r\n            type:\'error\',\r\n            closeText:\'\',\r\n            close:function(){}\r\n        });\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/toastmessage/jquery.toastmessage.css,../script/plugin/toastmessage/jquery.toastmessage.js','JS 插件-消息提示[第三方]',NULL,'../image/preview/toastmessage.jpg',1,NULL,'2013-07-24 10:45:46',NULL,NULL,'122','jQuery 插件');
INSERT INTO `editor` VALUES (66,'<div class=\"case\">\r\n    <input type=\"button\" value=\"抖动\" id=\"shake\" />\r\n    \r\n    <input type=\"button\" value=\"闪烁\" id=\"twinkle\" />\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}','$(function(){\r\n    var $body = $(window.top.document.body);\r\n    $body.queue();\r\n    \r\n    $(\'#shake\').on(\'click\', function(){\r\n        $body.css(\'position\', \'relative\').animate({\r\n            top:\'-5px\',\r\n        }, 100).animate({\r\n            left:\'-5px\'\r\n        }, 100).animate({\r\n            top:\'5px\'\r\n        }, 100).animate({\r\n            left:\'5px\'\r\n        }, 100).animate({\r\n            top:\'-5px\'\r\n        }, 100).animate({\r\n            left:\'-5px\'\r\n        }, 100).animate({\r\n            top:\'0px\',\r\n                left:\'0px\'\r\n        }, 100);\r\n    });\r\n    \r\n    $(\'#twinkle\').on(\'click\', function(){\r\n        $body\r\n        .fadeTo(100, 0)\r\n        .fadeTo(100, 1)\r\n        .fadeTo(100, 0)\r\n        .fadeTo(100, 1)\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js','JS 实验-页面效果',NULL,'../image/default/no-pic.png',4,NULL,'2013-07-24 11:19:13',NULL,NULL,'','');
INSERT INTO `editor` VALUES (67,'<div class=\"case\">\r\n    <h3>D3 数据可视化-1</h3>\r\n    \r\n    <p>\r\n        D3.js 也可以使用 div 来实现部分效果，但为了更好的效果，还是应该使用 SVG，也就是说需要 HTML5 的支持\r\n        <br />\r\n        下面两个图表，上面的是用 div 来实现的，下面的是用 SVG 来实现的\r\n    </p>\r\n    \r\n    <div class=\"testTarget\" id=\"testTarget\"></div>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}\r\n\r\n.chart{\r\n    border: 1px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\n.chart div {\r\n    font: 10px sans-serif;\r\n    background-color: steelblue;\r\n    text-align: right;\r\n    padding: 3px;\r\n    margin: 1px;\r\n    color: white;\r\n}\r\n\r\n.chart rect {\r\n    stroke: white;\r\n    fill: steelblue;\r\n}','$(function(){\r\n    var data = [4, 8, 15, 16, 23, 42],    // 测试数据\r\n        \r\n        x = d3.scale.linear()    // 直线标度\r\n                .domain([0, d3.max( data )])    // max 为最大值\r\n                .range([0, 420]),    // 规定容器的范围\r\n        \r\n        y = d3.scale.ordinal()    // 序数比例尺\r\n                .domain( data ).rangeBands([0, 120]),    \r\n        // rangeBands 自动分档 6 个数组元素，返回的是总长度/数组的元素个数 = 20\r\n        // 想得到整数时，可以使用 rangeRoundBands()\r\n        \r\n        // 容器\r\n        container = d3.select(\'#testTarget\'),\r\n        \r\n        chartDiv = container.append(\'div\').attr(\'class\', \'chart\').style(\'width\', \'420px\')\r\n                    .style(\'height\', \'140px\'),    // 使用 dom 元素，设置样式要使用 style 方法\r\n        \r\n        // SVG 图表\r\n        chartSvg = container.append(\'svg\').attr(\'class\', \'chart\').attr(\'width\', 420).attr(\'height\', 140)\r\n                    .attr(\'transform\', \'translate(10, 15)\');    // 使用 svg 元素，设置样式用 attr 方法\r\n    \r\n    /**\r\n     * 基于 div 的数据可视化\r\n     */\r\n    chartDiv.selectAll(\'div\').data( data ).enter().append(\'div\').style(\'width\', function(d){\r\n        return x(d) +\'px\';\r\n    }).text(function(d){\r\n        return d;\r\n    });\r\n\r\n    /**\r\n     * 基于 SVG 的数据可视化\r\n     */\r\n    // 绘制矩形\r\n    chartSvg.selectAll(\'rect\').data( data ).enter().append(\'rect\').attr(\'y\', function(d, i){\r\n        return i * 20;\r\n    }).attr(\'width\', x).attr(\'height\', 20);\r\n    \r\n    // 矩形形图上的数值\r\n    chartSvg.selectAll(\'text\').data( data ).enter().append(\'text\').attr(\'x\', function(d){\r\n        return x(d) -10;\r\n    }).attr(\'y\', function(d){\r\n        return y(d) + y.rangeBand() /2;\r\n    }).attr(\'dx\', -3) // 右边距 padding-right\r\n        .attr(\'dy\', \'.35em\') // 标签垂直居中\r\n        .attr(\'text-anchor\', \'end\') // 文字水平居右\r\n        .text(String);\r\n    \r\n    // 纵向标识线\r\n    chartSvg.selectAll(\'line\').data( x.ticks(10) )    // 粗略得设置刻度线的数量\r\n        .enter().append(\'line\').attr(\'x1\', x).attr(\'y1\', 0).attr(\'x2\', x).attr(\'y2\', 120)\r\n        .style(\'stroke\', \'#ccc\');\r\n    \r\n    // 标识线数值\r\n    chartSvg.selectAll(\'a\').data( x.ticks(10) ).enter().append(\'text\').attr(\'class\', \'rule\')\r\n        .attr(\'x\', x).attr(\'y\', 0).attr(\'dy\', -3).attr(\'text-anchor\', \'middle\').text(String);\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/lib/d3/d3.v3.min.js','D3 数据可视化-1',NULL,'../image/preview/d3-1.jpg',1,NULL,'2013-07-20 13:27:37',NULL,NULL,'55,97,104,137','数据可视化,D3.js,SVG,编程实践');
INSERT INTO `editor` VALUES (68,'<div class=\"case\">\r\n    <p>更多参考：\r\n        <a href=\"http://tutorialzine.com/2011/09/jquery-pointpoint-plugin/\">\r\n            http://tutorialzine.com/2011/09/jquery-pointpoint-plugin/\r\n        </a>\r\n    </p>\r\n    \r\n    <input type=\"button\" id=\"reqStart\" value=\"要求用户点击中央红色按钮\" />\r\n    <input type=\"button\" id=\"reqEnd\" value=\"停止要求\" />\r\n    \r\n    <a href=\"#\" class=\"button\" id=\"push\">点击按钮</a>\r\n</div>','.case{\r\n    overflow: hidden;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\n.button{\r\n    position:fixed;\r\n    top:300px;\r\n    left:50%;\r\n    \r\n    width: 100px;\r\n    height: 100px;\r\n    \r\n    margin-left: -50px;\r\n    \r\n    background: red;\r\n    \r\n    text-align:center;\r\n    line-height: 100px;\r\n    color:white;\r\n}','$(function(){\r\n    var $pp;\r\n    \r\n    $(\'#reqStart\').on(\'click\', function(){\r\n        $pp = $(\'#push\').pointPoint({\r\n            \r\n            // 设置样式 class\r\n            //      \'class\':\'myNewPointer\',\r\n            \r\n            // 与鼠标的距离\r\n            //      \'distance\':100\r\n        });\r\n    });\r\n    \r\n    $(\'#reqEnd\').on(\'click\', function(){\r\n        $pp.destroyPointPoint();\r\n    });\r\n})','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/pointpoint/jquery.pointpoint.css,../script/plugin/pointpoint/transform.js,../script/plugin/pointpoint/jquery.pointpoint.js','JS 插件-鼠标指向提示[第三方]',NULL,'../image/preview/pointpoint.jpg',1,NULL,'2013-07-26 14:04:31',NULL,NULL,'122','jQuery 插件');
INSERT INTO `editor` VALUES (69,'<div class=\"case\">\r\n    <p>建议使用已知大小的图片来达到最好效果\r\n        <br />\r\n        <br />更多资料：\r\n        <a href=\"http://cube3x.com/adipoli-jquery-image-hover-plugin/\">\r\n            http://cube3x.com/adipoli-jquery-image-hover-plugin/\r\n        </a>\r\n    </p>\r\n    \r\n    <div id=\"example_1\">\r\n        <h2>默认效果</h2>\r\n        <img src=\"../image/demo/demoImage-1.jpg\" alt=\"\" />\r\n        <img src=\"../image/demo/demoImage-2.jpg\" alt=\"\" />\r\n        <img src=\"../image/demo/demoImage-6.jpg\" alt=\"\" />\r\n    </div>\r\n    \r\n    <div id=\"example_2\">\r\n        <h2>灰化（需要 html5 支持）</h2>\r\n        <img src=\"../image/demo/demoImage-3.jpg\" alt=\"\" />\r\n        <img src=\"../image/demo/demoImage-4.jpg\" alt=\"\" />\r\n        <img src=\"../image/demo/demoImage-5.jpg\" alt=\"\" />\r\n    </div>\r\n    \r\n    <div id=\"example_3\">\r\n        <h2>弹出效果</h2>\r\n        <img src=\"../image/demo/demoImage-7.jpg\" alt=\"\" />\r\n        <img src=\"../image/demo/demoImage-8.jpg\" alt=\"\" />\r\n        <img src=\"../image/demo/demoImage-9.jpg\" alt=\"\" />\r\n    </div>\r\n    \r\n    <div id=\"example_4\">\r\n        <h2>分割下滑</h2>\r\n        <img src=\"../image/demo/demoImage-10.jpg\" alt=\"\" />\r\n        <img src=\"../image/demo/demoImage-11.jpg\" alt=\"\" />\r\n        <img src=\"../image/demo/demoImage-12.jpg\" alt=\"\" />\r\n    </div>\r\n    \r\n    <div id=\"example_5\">\r\n        <h2>方块淡出</h2>\r\n        <img src=\"../image/demo/demoImage-13.jpg\" alt=\"\" />\r\n        <img src=\"../image/demo/demoImage-14.jpg\" alt=\"\" />\r\n        <img src=\"../image/demo/demoImage-15.jpg\" alt=\"\" />\r\n    </div>\r\n    \r\n    <div id=\"example_6\">\r\n        <h2>分割收起</h2>\r\n        <img src=\"../image/demo/demoImage-16.jpg\" alt=\"\" />\r\n        <img src=\"../image/demo/demoImage-17.jpg\" alt=\"\" />\r\n        <img src=\"../image/demo/demoImage-18.jpg\" alt=\"\" />\r\n    </div>\r\n    \r\n    <div id=\"example_7\">\r\n        <h2>方块收起</h2>\r\n        <img src=\"../image/demo/demoImage-19.jpg\" alt=\"\" />\r\n        <img src=\"../image/demo/demoImage-20.jpg\" alt=\"\" />\r\n        <img src=\"../image/demo/demoImage-21.jpg\" alt=\"\" />\r\n    </div>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\ndiv{\r\n    clear:both;  \r\n}\r\n\r\nimg{\r\n    float: left;\r\n    \r\n    max-width:280px;\r\n    max-height:200px;\r\n}','$(function(){\r\n    \r\n    $(\'#example_1\').find(\'img\').adipoli();\r\n    \r\n    $(\'#example_2\').find(\'img\').adipoli({\r\n        startEffect:\'grayscale\',\r\n        hoverEffect:\'normal\'\r\n    });\r\n    \r\n    $(\'#example_3\').find(\'img\').adipoli({\r\n        startEffect:\'normal\',\r\n        hoverEffect:\'popout\'\r\n    });\r\n    \r\n    $(\'#example_4\').find(\'img\').adipoli({\r\n        startEffect:\'overlay\',\r\n        hoverEffect:\'sliceDown\'\r\n    });\r\n    \r\n    $(\'#example_5\').find(\'img\').adipoli({\r\n        startEffect:\'transparent\',\r\n        hoverEffect:\'boxRandom\'\r\n    });\r\n    $(\'#example_6\').find(\'img\').adipoli({\r\n        startEffect:\'overlay\',\r\n        hoverEffect:\'foldLeft\'\r\n    });\r\n    $(\'#example_7\').find(\'img\').adipoli({\r\n        startEffect:\'transparent\',\r\n        hoverEffect:\'boxRainGrowReverse\'\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/adipoli/jquery.adipoli.css,../script/plugin/adipoli/jquery.adipoli.min.js','JS 插件-图片悬浮效果[第三方]',NULL,'../image/preview/adipoli.jpg',1,NULL,'2013-07-26 15:14:45',NULL,NULL,'122,125','jQuery 插件,相册效果');
INSERT INTO `editor` VALUES (70,'<div class=\"case\">\r\n    <h3>D3 数据可视化-2</h3>\r\n    \r\n    <div class=\"testTarget\" id=\"testTarget\"></div>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}\r\n\r\n.chart{\r\n    border: 1px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\n.chart rect{\r\n    fill: steelblue;\r\n    stroke: white;\r\n}','$(function(){\r\n    var data = [\r\n            {value:64, time:1386989714171}, {value:71, time:1386989714173}, {value:17, time:1386989714182},\r\n            {value:60, time:1386989714209}, {value:42, time:1386989714221}, {value:6, time:1386989714226},\r\n            {value:85, time:1386989714304}, {value:44, time:1386989714354}, {value:53, time:1386989714366},\r\n            {value:28, time:1386989714382}, {value:35, time:1386989714551}, {value:97, time:1386989714693},\r\n            {value:94, time:1386989714716}, {value:53, time:1386989714762}, {value:3, time:1386989714770},\r\n            {value:45, time:1386989714782}, {value:97, time:1386989714792},{value:34, time:1386989714800},\r\n            {value:61, time:1386989714802}, {value:69, time:1386989714813}\r\n        ],\r\n        w = 20,\r\n        h = 80,\r\n        x = d3.scale.linear().domain([0, 1]).range([0, w]),\r\n        y = d3.scale.linear().domain([0, 100]).rangeRound([0, h]),\r\n        // rangeRound()    代替 range() 比例尺输出的所有值都会舍入到最近的整数值\r\n        \r\n        container = d3.select(\'#testTarget\'),\r\n        chart1 = container.append(\'svg\').attr(\'class\', \'chart\').attr(\'width\', w * data.length - 1)\r\n                    .attr(\'height\', h),\r\n        chart2 = container.append(\'svg\').attr(\'class\', \'chart\').attr(\'width\', w * data.length - 1)\r\n                    .attr(\'height\', h),\r\n        count = 0,\r\n        timer;\r\n    \r\n    // 静态图表\r\n    chart1.selectAll(\'rect\').data( data ).enter().append(\'rect\').attr(\'x\', function(d, i){\r\n        return x(i) -5;\r\n    }).attr(\'y\', function(d){\r\n        return h - y(d.value) -5;\r\n    }).attr(\'width\', w).attr(\'height\', function(d){\r\n        return y(d.value);\r\n    });\r\n    \r\n    \r\n    // 动态图表\r\n    function redraw(){\r\n        var rect = chart2.selectAll(\'rect\').data(data, function(d){    // 键函数\r\n            return d.time;\r\n        });\r\n        \r\n        rect.enter().insert(\'rect\', \'line\').attr(\'x\', function(d, i){\r\n            return x(i) - 5;\r\n        }).attr(\'y\', function(d){\r\n            return h - y(d.value) -5;\r\n        }).attr(\'width\', w).attr(\'height\', function(d){\r\n            return y(d.value);\r\n        });\r\n        \r\n        rect.transition()    // transition() 在选择元素后，改变任何属性之前\r\n            .duration(1000)    // duration() 可以控制动画的持续时间 必须放到 transition() 之后\r\n            .attr(\'x\', function(d, i){\r\n                return x(i) - 5;\r\n            });\r\n        \r\n        rect.exit()    // 退出\r\n            .remove();    // 删除\r\n    }\r\n    \r\n    function run(){\r\n        setInterval(function(){\r\n            \r\n            data.shift();\r\n            data.push({\r\n                value: parseInt(100 * Math.random()),\r\n                time: +new Date()\r\n            });\r\n            \r\n            redraw();\r\n        }, 1000);\r\n    }\r\n    \r\n    redraw();\r\n    run();\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/lib/d3/d3.v3.min.js','D3 数据可视化-2',NULL,'../image/preview/d3-2.jpg',1,NULL,'2013-09-04 15:32:47',NULL,NULL,'55,97,104,137','数据可视化,D3.js,SVG,编程实践');
INSERT INTO `editor` VALUES (71,'<div class=\"case\">\r\n    <p>更多参考：\r\n        <a href=\"https://github.com/zurb/twentytwenty\">\r\n            https://github.com/zurb/twentytwenty\r\n        </a>\r\n    </p>\r\n    \r\n    <div id=\"example\">\r\n        <img src=\"../image/demo/demoImage-1.jpg\" />\r\n        <img src=\"../image/demo/demoImage-2.jpg\" />\r\n    </div>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\n#example{\r\n    width: 670px;\r\n}','$(function(){\r\n    $(\'#example\').twentytwenty({default_offset_pct: 0.7});\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/twentytwenty/foundation.css,../script/plugin/twentytwenty/jquery.twentytwenty.css,../script/plugin/twentytwenty/jquery.event.move.js,../script/plugin/twentytwenty/jquery.twentytwenty.js','JS 插件-图片对比[第三方]',NULL,'../image/preview/twentytwenty.jpg',1,NULL,'2013-08-02 09:47:26',NULL,NULL,'122','jQuery 插件');
INSERT INTO `editor` VALUES (72,'<div class=\"case\">\r\n    <p>更多参考：\r\n        <a href=\"https://github.com/jschr/textillate\">\r\n            https://github.com/jschr/textillate\r\n        </a>\r\n    </p>\r\n    \r\n    <h2 id=\"test_1\">这是第一个测试标题</h2>\r\n    \r\n    <h2 id=\"test_2\">这是第二个测试标题</h2>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}','$(function(){\r\n    $(\'#test_1\').textillate();\r\n    \r\n    $(\'#test_2\').textillate({\r\n        \'in\': {\r\n            effect: \'rollIn\'\r\n        }\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/textillate/animate.css,../script/plugin/textillate/jquery.lettering.js,../script/plugin/textillate/jquery.textillate.js','JS 插件-文字效果[第三方]',NULL,'../image/preview/textillate.jpg',1,NULL,'2013-08-05 09:41:38',NULL,NULL,'122,87','jQuery 插件,文字特效');
