INSERT INTO `editor` VALUES (75,'<div class=\"case\">\r\n    <h3>D3 数据可视化-3</h3>\r\n    \r\n    <div class=\"testTarget\" id=\"testTarget\"></div>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}\r\n\r\n.axis path,\r\n.axis line{\r\n    fill: none;\r\n    stroke: black;\r\n    shape-rendering: crispEdges;\r\n}\r\n\r\n.axis text{\r\n    font-family: sans-serif;\r\n    font-size: 11px;\r\n}','$(function(){\r\n    var dataset = [\r\n            [5, 20, 1], [480, 90, 2], [250, 50, 3], [100, 33, 4], [330, 95, 5],\r\n            [410, 12, 6], [475, 44, 7], [25, 67, 8], [85, 21, 9], [220, 88, 10],\r\n            [600, 150, 11], [19, 220, 12]\r\n        ],\r\n        \r\n        w = 600,\r\n        h = 400,\r\n        padding = 20,\r\n        chart = d3.select(\'#testTarget\').append(\'svg\').attr(\'width\', w).attr(\'height\', h),\r\n        // x 轴\r\n        xScale = d3.scale.linear().domain([0, d3.max(dataset, function(d){\r\n            return d[0];\r\n        })]).range([padding, w - padding * 2]),\r\n        // y 轴\r\n        yScale = d3.scale.linear().domain([0, d3.max(dataset, function(d){\r\n            return d[1];\r\n        })]).range([h - padding, padding]),\r\n        // 半径 5-15\r\n        rScale = d3.scale.linear().domain([0, d3.max(dataset, function(d){\r\n            return d[2];\r\n        })]).range([5, 15]),\r\n        // x 轴坐标\r\n        xAxis = d3.svg.axis().scale(xScale).orient(\'bottom\').ticks(5),\r\n        yAxis = d3.svg.axis().scale(yScale).orient(\'left\').ticks(5);\r\n    \r\n    chart.selectAll(\'circle\').data(dataset).enter().append(\'circle\').attr(\'fill\', \'lime\')\r\n        .attr(\'transform\', \'translate(10, 0)\').attr(\'cx\', function(d){\r\n            return xScale(d[0]);\r\n        }).attr(\'cy\', function(d){\r\n            return yScale(d[1]);\r\n        }).attr(\'r\', function(d){\r\n            return rScale(d[2]);\r\n        });\r\n    \r\n    chart.append(\'g\').attr(\'class\', \'axis\').attr(\'transform\', \'translate(10,\'+ (h - padding) +\')\')\r\n        .call( xAxis );\r\n    chart.append(\'g\').attr(\'class\', \'axis\').attr(\'transform\', \'translate(30, 0)\').call( yAxis );\r\n    \r\n    function reDraw(){\r\n        chart.selectAll(\'circle\').data(dataset).transition().duration(1000)\r\n            .delay(function(d, i){    // 延迟运行\r\n                return i *50;\r\n            }).ease(\'elastic\')    // 设置动画效果\r\n            .attr(\'cy\', function(d){\r\n                return yScale(d[1]);\r\n            }).attr(\'r\', function(d){\r\n                return rScale(d[2]);\r\n            });\r\n    }\r\n    \r\n    setInterval(function(){\r\n        dataset.push( dataset.shift() );\r\n        \r\n        reDraw();\r\n    }, 1000);\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/lib/d3/d3.v3.min.js','D3 数据可视化-3',NULL,'../image/preview/d3-3.jpg',1,NULL,'2013-09-05 16:12:25',NULL,NULL,'55,97,104,137','数据可视化,D3.js,SVG,编程实践');
