INSERT INTO `editor` VALUES (77,'<div class=\"case\">\r\n    <p>更多参考：\r\n        <a href=\"http://fabien-d.github.io/alertify.js/\">\r\n            http://fabien-d.github.io/alertify.js/\r\n        </a>\r\n        <br />\r\n        <a href=\"https://github.com/fabien-d/alertify.js\">\r\n            https://github.com/fabien-d/alertify.js\r\n        </a>\r\n    </p>\r\n    \r\n    <div class=\"demo\">\r\n        <input type=\"button\" value=\"弹窗\" id=\"defaultAlert\" />\r\n        <input type=\"button\" value=\"确认弹窗\" id=\"defaultConfirm\" />\r\n        <input type=\"button\" value=\"输入弹窗\" id=\"defaultPrompt\" />\r\n    </div>\r\n    \r\n    <div class=\"demo\">\r\n        <input type=\"button\" value=\"信息提示\" id=\"defaultLog\" />\r\n        <input type=\"button\" value=\"成功提示\" id=\"defaultSucc\" />\r\n        <input type=\"button\" value=\"错误提示\" id=\"defaultError\" />\r\n    </div>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\n.demo{\r\n  margin:20px;\r\n}','$(function(){\r\n    $(\'#defaultAlert\').on(\'click\', function(){\r\n        alertify.alert(\"Message\");\r\n    });\r\n    \r\n    $(\'#defaultConfirm\').on(\'click\', function(){\r\n        alertify.confirm(\"Message\", function (e) {\r\n            if (e) {\r\n                // user clicked \"ok\"\r\n            } else {\r\n                // user clicked \"cancel\"\r\n            }\r\n        });\r\n    });\r\n    \r\n    $(\'#defaultPrompt\').on(\'click\', function(){\r\n        alertify.prompt(\"Message\", function (e, str) {\r\n            // str is the input text\r\n            if (e) {\r\n                // user clicked \"ok\"\r\n            } else {\r\n                // user clicked \"cancel\"\r\n            }\r\n        }, \"Default Value\");\r\n    });\r\n    \r\n    $(\'#defaultLog\').on(\'click\', function(){\r\n        alertify.log(\"Notification\", \'\', 10000);\r\n    });\r\n    \r\n    $(\'#defaultSucc\').on(\'click\', function(){\r\n        alertify.success(\"Success notification\");\r\n    });\r\n    \r\n    $(\'#defaultError\').on(\'click\', function(){\r\n        alertify.error(\"Error notification\");\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/alertify/alertify.core.css,../script/plugin/alertify/alertify.default.css,../script/plugin/alertify/alertify.min.js','JS 插件-信息提示框[第三方]',NULL,'../image/preview/alertify.jpg',1,NULL,'2013-07-27 10:46:49',NULL,NULL,'122','jQuery 插件');
INSERT INTO `editor` VALUES (78,'<div class=\"case\">\r\n    <h3>相邻兄弟选择器 element + element</h3>\r\n    <h4>li + li ：与 &lt;li&gt; 相邻的 &lt;li&gt; 元素字体加粗</h4>\r\n    <ul>\r\n        <li>不与 &lt;li&gt; 相邻</li>\r\n        <li>与 &lt;li&gt; 相邻</li>\r\n        <li>与 &lt;li&gt; 相邻</li>\r\n    </ul>\r\n    <ol>\r\n        <li>不与 &lt;li&gt; 相邻</li>\r\n        <li>与 &lt;li&gt; 相邻</li>\r\n        <li>与 &lt;li&gt; 相邻</li>\r\n    </ol>\r\n</div>\r\n\r\n<div class=\"case\">\r\n    <h3>element ~ element</h3>\r\n    <h4>h4 ~ div 选择前面有 &lt;p&gt; 元素的每个 &lt;div&gt; 元素 背景颜色为橙色</h4>\r\n    <div>这是被选中的元素</div>\r\n    <p>这是没有被选中的元素</p>\r\n    <div>这是被选中的元素</div>\r\n</div>\r\n\r\n<div class=\"case\">\r\n    <h3>lang 属性值选择器 :lang(language)</h3>\r\n    <h4 lang=\"lt\">h4:lang(lt) 选择带有以 \"it\" 开头的 lang 属性值的每个 &lt;p&gt; 元素 背景颜色为红色</h4>\r\n</div>\r\n\r\n<div class=\"case\">\r\n    <h3>属性选择器 [attribute]</h3>\r\n    <span title=\"test1\">[title] 带有 title 属性的元素背景为蓝色</span>\r\n    <span title=\"test2\">[title=test2] title 属性等于 test2 的元素文字颜色为红色</span>\r\n    <span title=\"test3 next\">[title~=next] title 属性值包含单词 \"next\" 的元素文字为粗体</span>\r\n    <span title=\"next-test4\">[title|=next] title 属性值以单词 \"next\" 为前缀的元素文字颜色为黄色</span>\r\n    <p>以下为 CSS3 新增</p>\r\n    <span title=\"new test5\">[title^=\"new\"] title 属性值以 \"new\" 开头的元素文字颜色为鲜绿色</span>\r\n    <span title=\"test6\">[title$=\"st6\"] title 属性值以 \"st6\" 结尾的元素字体颜色为栗色</span>\r\n    <span title=\"last test7\">[title*=\"test\"] title 属性值包含 \"test\" 的元素的字体为斜体</span>\r\n</div>\r\n\r\n<div class=\"case\">\r\n    <h3>首字母选择器 :first-letter</h3>\r\n    <h4>p:first-letter &lt;p&gt; 元素中的第一个字符 为黑色</h4>\r\n    <p>第一个字符是黑色的</p>\r\n</div>\r\n\r\n<div class=\"case\">\r\n    <h3>首行选择器 :first-line</h3>\r\n    <h4>blockquote:first-line &lt;p&gt; 元素中的第一行 背景颜色为紫色</h4>\r\n    <blockquote>这是第一行 背景颜色为紫红色\r\n        <br />这是第二行\r\n        <br />这是第三行\r\n    </blockquote>\r\n</div>\r\n\r\n<div class=\"case\">\r\n    <h3>父元素中处于第一个的该元素 :first-child</h3>\r\n    <h4>dt:first-child &lt;dt&gt; 是父元素中第一个元素 背景颜色为藏青色</h4>\r\n    <dl>\r\n        <dt>这是第一个元素 背景颜色为藏青色</dt>\r\n        <dd>这是第二个元素</dd>\r\n    </dl>\r\n</div>\r\n\r\n<div class=\"case\">\r\n    <h3>父元素中首个该元素 :first-of-type CSS3 新加入</h3>\r\n    <h4>dd:first-of-type 父元素中首个 &lt;dd&gt; 元素 背景颜色为紫色</h4>\r\n    <dl>\r\n        <dt>这是第一个 &lt;dt&gt; 元素</dt>\r\n        <dd>这是第一个 &lt;dd&gt; 元素</dd>\r\n        <dt>这是第二个 &lt;dt&gt; 元素</dt>\r\n        <dd>这是第二个 &lt;dd&gt; 元素</dd>\r\n    </dl>\r\n</div>','.case{\r\n    position: relative;\r\n    overflow: hidden;\r\n    height: auto;\r\n    min-height: 90px;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n    background: gray;\r\n    color: white;\r\n}\r\n\r\nh4 ~ div{\r\n    margin: 5px;\r\n    height: 20px;\r\n    background: orange;\r\n}\r\nli + li{\r\n    font-weight: bold;\r\n}\r\nh4:lang(lt){\r\n    background: red;\r\n}\r\n[title]{\r\n    background: blue;\r\n}\r\n[title=test2]{\r\n    color: red;\r\n}\r\n[title~=next]{\r\n    font-weight: bold;\r\n}\r\n[title|=next]{\r\n    color: yellow;\r\n}\r\n[title^=\"new\"]{\r\n    color: lime;\r\n}\r\n[title$=\"st6\"]{\r\n    color: maroon;\r\n}\r\n[title*=\"test\"]{\r\n    font-style: italic;\r\n}\r\np:first-letter{\r\n    color: black;\r\n}\r\nblockquote:first-line{\r\n    background: fuchsia;\r\n}\r\ndt:first-child{\r\n    background: navy;\r\n}\r\ndd:first-of-type{\r\n    background: purple;\r\n}','','../script/lib/jQuery/jquery-1.10.2.min.js','CSS 示例-选择器',NULL,'../image/default/no-pic.png',2,NULL,'2013-09-05 09:52:38',NULL,NULL,'','');
INSERT INTO `editor` VALUES (79,'<div class=\"case\">\r\n    <p>更多参考：\r\n        <a href=\"http://kalyani.com/2010/10/subway-map-visualization-jquery-plugin/\">\r\n            http://kalyani.com/2010/10/subway-map-visualization-jquery-plugin/\r\n        </a>\r\n    </p>\r\n    \r\n    <div id=\"subwayMap\" class=\"subway-map\" data-columns=\"12\" data-rows=\"10\" data-cellSize=\"40\" data-legendId=\"legend\" data-textClass=\"text\" data-gridNumbers=\"true\" data-grid=\"false\" data-lineWidth=\"8\">\r\n        <ul data-color=\"#ff4db2\" data-label=\"jQuery Widgets\">          \r\n            <li data-coords=\"2,2\" data-marker=\"interchange\"><a href=\"http://jqueryui.com/demos/accordion/\">Accordion</a></li>  \r\n            <li data-coords=\"4,2\"><a href=\"http://jqueryui.com/demos/autocomplete/\"><a href=\"http://jqueryui.com/demos/autocomplete/\">Auto\\ncomplete</a></li>  \r\n            <li data-coords=\"5,3\" data-dir=\"E\"></li>  \r\n            <li data-coords=\"5,7\" data-marker=\"@station\" data-labelPos=\"W\"><a href=\"http://jqueryui.com/demos/slider/\">Slider</a></li> <!-- marker-only node -->  \r\n            <li data-coords=\"6,4\" data-dir=\"S\" data-marker=\"interchange\" data-markerInfo=\"h5\"><a href=\"http://jqueryui.com/demos/datepicker/\">Date\\npicker</a></li>  \r\n            <li data-coords=\"7,4\"></li>  \r\n            <li data-coords=\"7.15,8\" data-marker=\"@station\" data-labelPos=\"E\"><a href=\"http://jqueryui.com/demos/dialog/\">Dialog</a></li>  <!-- marker-only node, moved to the right by 0.15 -->\r\n            <li data-coords=\"8,3\" data-dir=\"E\"></li>  \r\n            <li data-coords=\"8,2\"></li>  \r\n            <li data-coords=\"9,1\" data-dir=\"N\"></li>  \r\n            <li data-coords=\"10,2\" data-dir=\"E\" data-marker=\"interchange\" data-labelPos=\"E\"><a href=\"http://jqueryui.com/demos/button/\">Button</a></li>  \r\n            <li data-coords=\"10,5\"></li>  \r\n            <li data-coords=\"9,6\" data-dir=\"S\" data-marker=\"station\"><a href=\"http://jqueryui.com/demos/progressbar/\">Progress\\nbar</a></li>  \r\n            <li data-coords=\"6,9\"></li>  \r\n            <li data-coords=\"5,8\" data-dir=\"W\"></li>  \r\n            <li data-coords=\"5,7\"></li>  \r\n            <li data-coords=\"4,6\" data-dir=\"N\"></li>  \r\n            <li data-coords=\"2,6\"><a href=\"http://jqueryui.com/demos/tabs/\">Tabs</a></li>  \r\n        </ul>\r\n        \r\n        <ul data-color=\"#00ff00\" data-label=\"jQuery Interactions\" data-shiftCoords=\"0,-1\">      \r\n            <li data-coords=\"2,6\"></li> \r\n            <li data-coords=\"2,5.9\" data-marker=\"@interchange\"> </li> <!-- marker-only node, moved up by 0.10 -->\r\n            <li data-coords=\"5,6\" data-marker=\"@station\" data-labelPos=\"N\"><a href=\"http://jqueryui.com/demos/selectable/\">Selectable</a></li>  \r\n            <li data-coords=\"6,6\"></li>  \r\n            <li data-coords=\"7,3\" data-marker=\"@station\" data-labelPos=\"W\"><a href=\"http://jqueryui.com/demos/resizeable/\">Resizeable</a></li>  \r\n            <li data-coords=\"7,5\" data-dir=\"E\" data-marker=\"station\" data-labelPos=\"E\"><a href=\"http://jqueryui.com/demos/droppable/\">Droppable</a></li>  \r\n            <li data-coords=\"7,1\" data-marker=\"interchange\" data-labelPos=\"W\"><a href=\"http://jqueryui.com/demos/draggable/\">Draggable</a></li>  \r\n        </ul>\r\n        \r\n    </div>\r\n    <div id=\"legend\"></div>\r\n</div>','.case{\r\n    overflow: hidden;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\n/* The main DIV for the map */\r\n.subway-map{\r\n    margin: 0;\r\n    width: 500px;\r\n    height:410px;\r\n    background-color: white;\r\n}\r\n\r\n/* Text labels */\r\n.text{\r\n    text-decoration: none;\r\n    color: black;\r\n}\r\n\r\n#legend{\r\n    float: left;\r\n    width: 250px;\r\n    height:400px;\r\n}\r\n\r\n#legend div{\r\n    height: 25px;\r\n}\r\n\r\n#legend span{\r\n  