INSERT INTO `editor` VALUES (80,'<div class=\"case\">\r\n    <h3>D3 数据可视化-4</h3>\r\n    \r\n    <div class=\"testTarget\" id=\"testTarget\"></div>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}\r\n\r\n.chord path {\r\n    fill-opacity: 0.67;\r\n    stroke: #000;\r\n    stroke-width: 0.5px;\r\n}','$(function(){\r\n    var matrix = [\r\n            [11975,  5871, 8916, 2868], [ 1951, 10048, 2060, 6171],\r\n            [ 8010, 16145, 8090, 8045], [ 1013,   990,  940, 6907]\r\n        ],\r\n        \r\n        width = 800,\r\n        height = 500,\r\n        innerRadius = Math.min(width, height) *0.41,\r\n        outerRadius = innerRadius *1.1,\r\n        \r\n        // 颜色数组\r\n        fill = [\'#000000\', \'#ffdd89\', \'#957244\', \'#f26223\'],\r\n\r\n        svg = d3.select(\'#testTarget\').append(\'svg\').attr(\'width\', width).attr(\'height\', height).append(\'g\')\r\n                .attr(\'transform\', \'translate(\'+ width/2 +\',\'+ height/2 +\')\'),\r\n\r\n        // d3.layou.chord() d3 布局方法 根据矩阵关系生成弦图\r\n        chord = d3.layout.chord().padding(0.05).sortSubgroups( d3.descending ).matrix( matrix );\r\n        // d3.descending 降序排序\r\n    \r\n    svg.append(\'g\').selectAll(\'path\').data( chord.groups ).enter().append(\'path\').style(\'fill\', function(d){\r\n        return fill[d.index];\r\n    }).style(\'stroke\', function(d){\r\n        return fill[d.index];\r\n    }).attr(\'d\', d3.svg.arc().innerRadius( innerRadius ).outerRadius( outerRadius ));\r\n    // d3.svg.arc() d3 用 SVG 的 path 元素绘制弧形的内置方法\r\n    \r\n    svg.append(\'g\').attr(\'class\', \'chord\').selectAll(\'path\').data( chord.chords ).enter().append(\'path\')\r\n        .attr(\'d\', d3.svg.chord().radius(innerRadius)).style(\'fill\', function(d){\r\n            return fill[d.target.index];\r\n        }).style(\'opacity\', 1);\r\n    \r\n    function groupTicks(d){\r\n        var k = ( d.endAngle - 