INSERT INTO `editor` VALUES (87,'<div class=\"photo demo_1\" data-stellar-background-ratio=\"0.5\"></div>\r\n<div class=\"photo demo_2\" data-stellar-background-ratio=\"0.5\"></div>\r\n<div class=\"photo demo_3\" data-stellar-background-ratio=\"0.5\"></div>\r\n<div class=\"photo demo_4\" data-stellar-background-ratio=\"0.5\"></div>\r\n<div class=\"photo demo_1\" data-stellar-background-ratio=\"0.5\"></div>\r\n<div class=\"photo demo_2\" data-stellar-background-ratio=\"0.5\"></div>\r\n<div class=\"photo demo_3\" data-stellar-background-ratio=\"0.5\"></div>\r\n<div class=\"photo demo_4\" data-stellar-background-ratio=\"0.5\"></div>  \r\n\r\n<div class=\"case\">\r\n    <h3>视差滚动 stellar</h3>\r\n    \r\n    <p>\r\n        插件会根据现实区域大小自动计算\r\n        \r\n        <br />\r\n        <br />更多参考：\r\n        <a href=\"https://github.com/markdalgleish/stellar.js\">\r\n            https://github.com/markdalgleish/stellar.js\r\n        </a>\r\n    </p>\r\n</div>','.case{\r\n    position: fixed;\r\n        \r\n        left: 10px;\r\n        right: 10px;\r\n        top: 10px;\r\n    \r\n    border: 5px solid #c0c0c0;\r\n    padding: 10px;\r\n        \r\n        color: red;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}\r\n\r\n.photo {\r\n    position: relative;\r\n    \r\n    width: 960px;\r\n    height: 450px;\r\n    \r\n    background-attachment: fixed;\r\n    background-position: 50% 0;\r\n    background-repeat: no-repeat;\r\n    -webkit-background-size: 900px 640px;\r\n    background-size: 960px 640px;\r\n}\r\n.demo_1 {\r\n    background-image: url(../image/demo/demoImage-1.jpg);\r\n}\r\n.demo_2 {\r\n    background-image: url(../image/demo/demoImage-2.jpg);\r\n}\r\n.demo_3 {\r\n    background-image: url(../image/demo/demoImage-3.jpg);\r\n}\r\n.demo_4 {\r\n    background-image: url(../image/demo/demoImage-4.jpg);\r\n}','$(function(){\r\n    \r\n    $.stellar({\r\n        horizontalScrolling: false,\r\n        verticalOffset: 40\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/stellar/jquery.stellar.min.js','视差滚动 stellar',NULL,'../image/default/no-pic.png',1,NULL,'2013-09-16 10:24:32',NULL,NULL,'122,129','jQuery 插件,视差滚动');
INSERT INTO `editor` VALUES (88,'<div class=\"case\">\r\n    <h3>D3 数据可视化-7</h3>\r\n    \r\n    <div class=\"testTarget\" id=\"testTarget\"></div>\r\n</div>','.case{\r\n    overflow: hidden;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}\r\n\r\nsvg{\r\n  font-size: 10px;\r\n}\r\n\r\nsvg{\r\n    clear:both;\r\n    float: left;\r\n    font-size: 10px;\r\n}\r\n\r\npath{\r\n    fill: transparent;\r\n}\r\n\r\n.line{\r\n    stroke-width: 1\r\n}\r\ng line{\r\n    stroke:#c0c0c0; stroke-width:1;\r\n}\r\n.axis path, .axis line{\r\n    fill: none;\r\n    stroke: #000;\r\n    shape-rendering: crispEdges;\r\n}','var data = [\r\n    {date: \'2012/12/31\', bc1: 9455,             cmb: 12190, ccb: 2914},\r\n    {date: \'2013/02/14\', bc1: 8955,             cmb: 11765, ccb: 8314},\r\n    {date: \'2013/07/21\', bc1: 13573, bc2: 5440, cmb: 12164, ccb: 8327},\r\n    {date: \'2013/08/11\', bc1: 13564, bc2: 5440, cmb: 11900, ccb: 7327},\r\n    {date: \'2013/08/25\', bc1: 13564, bc2: 8914, cmb: 11900, ccb: 7327},\r\n    {date: \'2013/09/01\', bc1: 13564, bc2: 4710, cmb: 11900, ccb: 7327},\r\n    {date: \'2013/09/15\', bc1: 13564, bc2: 8683, cmb: 11633, ccb: 7327},\r\n    {date: \'2013/09/29\', bc1: 13064, bc2: 504,  cmb: 11633, ccb: 7327},\r\n    {date: \'2013/10/13\', bc1: 12576, bc2: 5083, cmb: 10837, ccb: 11824},\r\n    {date: \'2013/11/03\', bc1: 12576, bc2: 3583, cmb: 10626, ccb: 11824},\r\n    {date: \'2013/11/14\', bc1: 12576, bc2: 7697, cmb: 10336, ccb: 11824},\r\n    {date: \'2013/11/17\', bc1: 12576, bc2: 6697, cmb: 10339, ccb: 11824},\r\n    {date: \'2013/11/21\', bc1: 12576, bc2: 2493, cmb: 10339, ccb: 11824},\r\n    {date: \'2013/12/04\', bc1: 12576, bc2: 993, cmb: 10831, ccb: 11824},\r\n    {date: \'2013/12/15\', bc1: 12576, bc2: 3787, cmb: 11831, ccb: 11824},\r\n    {date: \'2013/12/22\', bc1: 12587, bc2: 3290, cmb: 11701, ccb: 11834}\r\n];\r\n$(function(){\r\n    var width = 600,\r\n        height = 400,\r\n        margin = 50,\r\n        fill = [\'#7d4f9e\', \'#30dff3\', \'#b22c46\', \'#d9d919\', \'#1d1626\'],\r\n\r\n        x = d3.time.scale().range([0, width]),\r\n        y = d3.scale.linear().range([height, 0]),\r\n\r\n        xAxis = d3.svg.axis().scale(x).orient(\'bottom\'),\r\n        yAxis = d3.svg.axis().scale(y).orient(\'left\'),\r\n\r\n        svg = d3.select(\'#testTarget\').append(\'svg\')\r\n            .attr(\'width\', width + margin*3)\r\n            .attr(\'height\', height + margin)\r\n            .append(\'g\').attr(\'transform\', \'translate(100,30)\'),\r\n\r\n        i = 0, j = data.length;\r\n    for(; i < j; i++ ){\r\n        data[i].date = new Date( data[i].date );\r\n    }\r\n\r\n    var line_bc1 = d3.svg.line().x(function(d){\r\n            return x( d.date );\r\n        }).y(function(d){\r\n            return y( d.bc1 || 0 );\r\n        }),\r\n        line_bc2 = d3.svg.line().x(function(d){\r\n            return x( d.date );\r\n        }).y(function(d){\r\n            return y( d.bc2 || 0 );\r\n        }),\r\n        line_cmb = d3.svg.line().x(function(d){\r\n            return x( d.date );\r\n        }).y(function(d){\r\n            return y( d.cmb || 0 );\r\n        }),\r\n        line_ccb = d3.svg.line().x(function(d){\r\n            return x( d.date );\r\n        }).y(function(d){\r\n            return y( d.ccb || 0 );\r\n        }),\r\n        line_all = d3.svg.line().x(function(d){\r\n            return x( d.date );\r\n        }).y(function(d){\r\n            return y( d.bc1 + ( d.bc2 || 0 ) + d.cmb + d.ccb );\r\n        });\r\n\r\n    x.domain([d3.min(data, function(d){\r\n        return d.date;\r\n    }) -3600*24*5*1000, d3.max(data, function(d){\r\n        return d.date;\r\n    })]);\r\n    y.domain([0, 50000]);\r\n\r\n    // 折现\r\n    svg.append(\'path\').attr(\'class\', \'line\').style(\'stroke\', fill[0]).datum(data).attr(\'d\', line_bc1);\r\n    svg.append(\'path\').attr(\'class\', \'line\').style(\'stroke\', fill[1]).datum(data).attr(\'d\', line_bc2);\r\n    svg.append(\'path\').attr(\'class\', \'line\').style(\'stroke\', fill[2]).datum(data).attr(\'d\', line_cmb);\r\n    svg.append(\'path\').attr(\'class\', \'line\').style(\'stroke\', fill[3]).datum(data).attr(\'d\', line_ccb);\r\n    svg.append(\'path\').attr(\'class\', \'line\').style(\'stroke\', fill[4]).datum(data).attr(\'d\', line_all);\r\n\r\n    var point = svg.selectAll(\'.x\').data( data ).enter().append(\'g\').attr(\'class\', \'x\'),\r\n        datetime = {};\r\n    point.filter(function(d){\r\n        var date = d.date,\r\n            y = date.getFullYear(),\r\n            m = date.getMonth(),\r\n            day = date.getDate(),\r\n            rs;\r\n\r\n        if( !(y in datetime) ){\r\n            datetime[y] = {};\r\n        }\r\n\r\n        if( m in datetime[y] ){\r\n            rs = false;\r\n        }\r\n        else{\r\n            if( day > 10 ){\r\n                datetime[y][m] = 1;\r\n                rs = true;\r\n            }\r\n            else rs = false;\r\n        }\r\n\r\n        return rs;\r\n    }).append(\'line\').attr(\'x1\', function(d){\r\n        return x( d.date );\r\n    }).attr(\'y1\', 0).attr(\'x2\', function(d){\r\n        return x( d.date );\r\n    }).attr(\'y2\', y(height)).attr(\'stroke-dasharray\', \'5 5\');\r\n\r\n    point.append(\'circle\').attr(\'cx\', function(d){\r\n        return x(d.date );\r\n    }).attr(\'cy\', function(d){\r\n        return y( d.bc1 );\r\n    }).attr(\'r\', 4).attr(\'fill\', \'#fff\').attr(\'stroke-width\', 1).attr(\'stroke\', fill[0]);\r\n    point.append(\'circle\').attr(\'cx\', function(d){\r\n        return x(d.date );\r\n    }).attr(\'cy\', function(d){\r\n        return y( d.bc2 || 0 );\r\n    }).attr(\'r\', 4).attr(\'fill\', \'#fff\').attr(\'stroke-width\', 1).attr(\'stroke\', fill[1]);\r\n    point.append(\'circle\').attr(\'cx\', function(d){\r\n        return x(d.date );\r\n    }).attr(\'cy\', function(d){\r\n        return y( d.cmb || 0 );\r\n    }).attr(\'r\', 4).attr(\'fill\', \'#fff\').attr(\'stroke-width\', 1).attr(\'stroke\', fill[2]);\r\n    point.append(\'circle\').attr(\'cx\', function(d){\r\n        return x(d.date );\r\n    }).attr(\'cy\', function(d){\r\n        return y( d.ccb || 0 );\r\n    }).attr(\'r\', 4).attr(\'fill\', \'#fff\').attr(\'stroke-width\', 1).attr(\'stroke\', fill[3]);\r\n    point.append(\'circle\').attr(\'cx\', function(d){\r\n        return x(d.date );\r\n    }).attr(\'cy\', function(d){\r\n        return y(d.bc1 + ( d.bc2 || 0 )+( d.cmb || 0 )+( d.ccb || 0 ) );\r\n    }).attr(\'r\', 4).attr(\'fill\', \'#fff\').attr(\'stroke-width\', 1).attr(\'stroke\', fill[4]);\r\n\r\n    // 坐标轴\r\n    svg.append(\'g\').attr(\'class\', \'axis x\').attr(\'transform\', \'translate(0, 400)\').call( xAxis );\r\n    svg.append(\'g\').attr(\'class\', \'axis y\').call( yAxis );\r\n});\r\n$(function(){\r\n    var d = [0, 30, 100, 200, 500, 1000, 1800, 3000, 5000,\r\n             10000, 20000, 50000, 100000, 200000, 500000, 1000000],\r\n        width = 600,\r\n        height = 400,\r\n        margin = 100,\r\n        x = d3.scale.linear().domain([d3.min(d), d3.max(d)]).range([0, width]),\r\n        y = d3.scale.linear().domain([0, d.length+1]).range([height, 0]),\r\n        xAxis = d3.svg.axis().scale(x).orient(\'bottom\'),\r\n        yAxis = d3.svg.axis().scale(y).orient(\'left\'),\r\n        line = d3.svg.line().x(function(d){\r\n            return x(d);\r\n        }).y(function(d, i){\r\n            return y(i);\r\n        });\r\n    \r\n    var svg = d3.select(\'#testTarget\').append(\'svg\')\r\n        .attr(\'width\', width)\r\n        .attr(\'height\', height + margin);\r\n    \r\n    svg.append(\'path\').attr(\'class\', \'line\').style(\'stroke\', \'#000\').datum(d).attr(\'d\', line).attr(\'transform\', \'translate(40, 0)\');\r\n    \r\n    svg.append(\'g\')\r\n        .attr(\'class\', \'axis x\')\r\n        .attr(\'transform\', \'translate(40, 400)\')\r\n        .call( xAxis );\r\n    svg.append(\'g\')\r\n        .attr(\'class\', \'axis y\').attr(\'transform\', \'translate(40, 0)\')\r\n        .call( yAxis );\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/lib/d3/d3.v3.min.js','D3 数据可视化-7',NULL,'../image/preview/d3-8.jpg',4,NULL,'2013-09-16 12:50:20',NULL,NULL,'97,104,55','D3.js,SVG,数据可视化');
INSERT INTO `editor` VALUES (89,'<div style=\"z-index:9;height:800px;border:1px solid gray\" id=\"container\"></div>\r\n\r\n<script src=\"http://api.map.baidu.com/api?v=1.2\"></script>','','$(function(){\r\n    var map = new BMap.Map(\"container\", {\r\n        mapType: BMAP_HYBRID_MAP //设置卫星图为底图\r\n    });\r\n    \r\n    //    map.centerAndZoom(new BMap.Point(116.325218,39.977441),12);\r\n    map.centerAndZoom(new BMap.Point(121.53418, 38.861441), 18);     // 初始化地图,设置中心点坐标和地图级别。\r\n    map.addControl(new BMap.NavigationControl());  //添加鱼骨控件\r\n    map.addControl(new BMap.MapTypeControl());          //添加地图类型控件\r\n    //    map.setCurrentCity(\"北京\");          // 设置3D地图显示的城市 此项是必须设置的\r\n    map.setCurrentCity(\"大连\");\r\n    \r\n    // 新科花园\r\n    var secRingCenter = new BMap.Point(121.53418,38.861441);\r\n    var secRing = [\r\n        new BMap.Point(121.5325, 38.86),\r\n        new BMap.Point(121.5325, 38.8635),\r\n        new BMap.Point(121.536, 38.8635),\r\n        new BMap.Point(121.536, 38.86)\r\n    ];\r\n    var secRingPolygon = new BMap.Polygon(secRing, {strokeColor:\"blue\", strokeWeight:5, strokeOpacity:0.5});\r\n    map.addOverlay(secRingPolygon);\r\n    \r\n    //给多边形添加鼠标事件\r\n    secRingPolygon.addEventListener(\"mouseover\",function(){\r\n        secRingPolygon.setStrokeColor(\"red\");\r\n        map.addOverlay(secRingLabel);\r\n        map.panTo(secRingCenter);\r\n    });\r\n    secRingPolygon.addEventListener(\"mouseout\",function(){\r\n        secRingPolygon.setStrokeColor(\"blue\");\r\n        map.removeOverlay(secRingLabel);\r\n    });\r\n    secRingPolygon.addEventListener(\"click\",function(){\r\n        map.zoomIn();\r\n        secRingPolygon.setStrokeColor(\"red\");\r\n        map.setCenter(secRingCenter);\r\n    });\r\n    \r\n    //创建标签\r\n    var secRingLabel = new BMap.Label(\"新科花园,位于高新园区\", {\r\n        offset: new BMap.Size(-150,0),\r\n        position: secRingCenter\r\n    });\r\n    secRingLabel.setStyle({\r\n        \"z-index\":\"999999\",\r\n        \"padding\": \"10px\",\r\n        \"width\": \"140px\",\r\n        \"border\": \"1px solid #ccff00\"\r\n    });\r\n    \r\n    var secRingLabel2 = new BMap.Label(\"新科花园\", {offset: new BMap.Size(10,-30), position: secRingCenter});\r\n    secRingLabel2.setStyle({\r\n        \"line-height\": \"20px\",\r\n        \"text-align\": \"center\",\r\n        \"width\": \"80px\",\r\n        \"height\": \"29px\",\r\n        \"border\": \"none\",\r\n        \"padding\": \"2px\",\r\n        \"background\": \"url(http://jixingjx.com/mapapi/ac.gif) no-repeat\"\r\n    });\r\n    map.addOverlay(secRingLabel2);\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js','百度地图 API',NULL,'../image/default/no-pic.png',4,NULL,'2013-09-16 13:18:45',NULL,NULL,'120','API');
INSERT INTO `editor` VALUES (90,'<div class=\"case\">\r\n    <p>更多参考：\r\n        <a href=\"https://github.com/bradbirdsall/Swipe\">\r\n            https://github.com/bradbirdsall/Swipe\r\n        </a>\r\n    </p>\r\n    \r\n    <div id=\'mySwipe\' class=\'swipe\'>\r\n        <div class=\'swipe-wrap\'>\r\n            <div><img src=\"../image/demo/demoImage-1.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-2.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-3.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-4.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-5.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-6.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-7.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-8.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-9.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-10.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-11.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-12.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-13.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-14.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-15.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-16.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-17.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-18.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-19.jpg\" alt=\"\" /></div>\r\n            <div><img src=\"../image/demo/demoImage-20.jpg\" alt=\"\" /></div>\r\n        </div>\r\n    </div>\r\n    \r\n    <div class=\"btn\">\r\n        <button id=\"prev\">prev</button> \r\n        <button id=\"next\">next</button>\r\n    </div>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\n.swipe {\r\n    overflow: hidden;\r\n    visibility: hidden;\r\n    position: relative;\r\n    \r\n    max-width:500px;\r\n    margin:0 auto;\r\n}\r\n.swipe-wrap {\r\n    overflow: hidden;\r\n    position: relative;\r\n}\r\n.swipe-wrap > div {\r\n    float:left;\r\n    width:100%;\r\n    position: relative;\r\n}\r\nimg{\r\n    max-height: 200px;\r\n    max-width: 300px;\r\n    margin: 0 auto;\r\n}\r\n\r\n.btn{\r\n    padding-top:20px;\r\n    text-align:center;\r\n}','$(function(){\r\n    \r\n    var elem = document.getElementById(\'mySwipe\');\r\n    window.mySwipe = Swipe(elem, {\r\n        // startSlide: 4,\r\n        // auto: 3000,\r\n        // continuous: true,\r\n        // disableScroll: true,\r\n        // stopPropagation: true,\r\n        // callback: function(index, element) {},\r\n        // transitionEnd: function(index, element) {}\r\n    });\r\n    \r\n    $(\'#prev\').on(\'click\', function(){\r\n        mySwipe.prev();\r\n    })\r\n    $(\'#next\').on(\'click\', function(){\r\n        mySwipe.next();\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/swipe/swipe.css,../script/plugin/swipe/swipe.js','JS 插件-图片滑动[第三方]',NULL,'../image/preview/swipe.jpg',1,NULL,'2013-09-17 16:04:26',NULL,NULL,'125','相册效果');
INSERT INTO `editor` VALUES (91,'<div class=\"case\">\r\n    <h3>虚拟键盘</h3>\r\n    \r\n    <p>更多资料：\r\n        <a href=\"https://github.com/Mottie/Keyboard\">\r\n            https://github.com/Mottie/Keyboard\r\n        </a>\r\n    </p>\r\n    \r\n    <input id=\"keyboard\" type=\"text\">\r\n</div>','.case{\r\n    overflow: hidden;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}','$(function(){\r\n    $(\'#keyboard\').keyboard();\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/lib/jQueryUI/jquery-ui-1.10.3.custom.min.css,../script/lib/jQueryUI/jquery-ui-1.10.3.custom.min.js,../script/plugin/keyboard/jquery.keyboard.css,../script/plugin/keyboard/jquery.mousewheel.js,../script/plugin/keyboard/jquery.keyboard.js','JS 插件-虚拟键盘[第三方]',NULL,'../image/preview/keyboard.jpg',1,NULL,'2013-09-18 10:33:46',NULL,NULL,'122','jQuery 插件');
INSERT INTO `editor` VALUES (92,'<div class=\"case\">\r\n    <p>更多资料：\r\n        <a href=\"https://github.com/fliptopbox/jquery.stripe\">\r\n            https://github.com/fliptopbox/jquery.stripe\r\n        </a>\r\n    </p>\r\n    \r\n    <div class=\"gallery\" id=\"gallery\">\r\n        <ul>\r\n            <li><img src=\"../image/demo/demoImage-1.jpg\" alt=\"image-1\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-2.jpg\" alt=\"image-2\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-3.jpg\" alt=\"image-3\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-4.jpg\" alt=\"image-4\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-5.jpg\" alt=\"image-5\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-6.jpg\" alt=\"image-6\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-7.jpg\" alt=\"image-7\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-8.jpg\" alt=\"image-8\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-9.jpg\" alt=\"image-9\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-10.jpg\" alt=\"image-10\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-11.jpg\" alt=\"image-11\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-12.jpg\" alt=\"image-12\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-13.jpg\" alt=\"image-13\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-14.jpg\" alt=\"image-14\" /></li>\r\n            <li><img src=\"../image/demo/demoImage-15.jpg\" alt=\"image-15\" /></li>\r\n        </ul>\r\n    </div>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\n.gallery {\r\n    position: relative;\r\n    overflow: hidden;\r\n    padding: 0 0 30px 0;\r\n}\r\n\r\n.stripe-control a.button{\r\n    margin: 0;\r\n}','$(function(){\r\n    $(\'#gallery\').stripe({\r\n        automatic: true,\r\n        buttons: true,\r\n        delay: 5000,\r\n        \'min-width\': 20\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/stripe/jquery.stripe.css,../script/plugin/stripe/jquery.stripe.js','JS 插件-手风琴效果[第三方]',NULL,'../image/preview/stripe.jpg',1,NULL,'2013-09-18 14:39:54',NULL,NULL,'122,125','jQuery 插件,相册效果');
INSERT INTO `editor` VALUES (93,'<div class=\"case\">\r\n    <p>更多资料：\r\n        <a href=\"https://bitbucket.org/Better2Web/jquery.percentageloader\">\r\n            https://bitbucket.org/Better2Web/jquery.percentageloader\r\n        </a>\r\n    </p>\r\n    \r\n    <div id=\"topLoader\"></div>\r\n    \r\n    <button id=\"animateButton\">加载</button>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}','$(function() {\r\n    \r\n    var $topLoader = $(\"#topLoader\").percentageLoader({\r\n        width: 256,\r\n        height: 256,\r\n        controllable: true,\r\n        progress: 0.5,\r\n        onProgressUpdate: function(val){\r\n            $topLoader.setValue(Math.round(val * 100.0));\r\n        }\r\n    });\r\n    \r\n    var topLoaderRunning = false;\r\n    $(\"#animateButton\").click(function() {\r\n        if (topLoaderRunning) {\r\n            return;\r\n        }\r\n        topLoaderRunning = true;\r\n        $topLoader.setProgress(0);\r\n        $topLoader.setValue(\'0kb\');\r\n        var kb = 0;\r\n        var totalKb = 999;\r\n        \r\n        var animateFunc = function() {\r\n            kb += 17;\r\n            $topLoader.setProgress(kb / totalKb);\r\n            $topLoader.setValue(kb.toString() + \'kb\');\r\n            \r\n            if (kb < totalKb) {\r\n                setTimeout(animateFunc, 25);\r\n            } else {\r\n                topLoaderRunning = false;\r\n            }\r\n        };\r\n        \r\n        setTimeout(animateFunc, 25);\r\n        \r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/percentageloader/jquery.percentageloader-0.1.css,../script/plugin/percentageloader/jquery.percentageloader-0.1.min.js','JS 插件-动画进度条[第三方]',NULL,'../image/preview/percentageloader.jpg',1,NULL,'2013-09-18 16:57:06',NULL,NULL,'122,139','jQuery 插件,进度条');
