 <div class=\"case case48\"></div>\r\n    <div class=\"case case49\"></div>\r\n    <div class=\"case case50\"></div>\r\n</div>\r\n<input type=\"button\" value=\"show\" id=\"show\" class=\"show\" />','.show{\r\n    position: fixed;\r\n    top: 0;\r\n    left: 0;\r\n}\r\n.container{\r\n    overflow: hidden;\r\n    position: relative;\r\n    \r\n    width: 950px;\r\n    height: 500px;\r\n}\r\n.case{\r\n    position: absolute;\r\n    /* display: none; */\r\n    \r\n    width: 180px;\r\n    height: 40px;\r\n    \r\n    border: 5px solid #c0c0c0;\r\n    \r\n    background: purple;\r\n}','$(function(){\r\n    \r\n    var $case = $(\'#container\').find(\'div.case\');\r\n    console.log($case.length)\r\n    $case.slice(0, 24).css({\r\n        top: \'-190px\',\r\n        left: \'380px\'\r\n    });\r\n    \r\n    $case.slice(24).css({\r\n        top: \'950px\',\r\n        left: \'380px\'\r\n    });\r\n    \r\n    var i = 0,\r\n        animate = function(){\r\n            if( i<25 ){\r\n                $case.eq(i).animate({\r\n                    top: (Math.floor(i/5)*50)+\'px\',\r\n                    left: ((i%5)*190)+\'px\'\r\n                }, \'fast\', animate);\r\n                $case.eq(49-i).animate({\r\n                    top: (Math.floor((49-i)/5)*50)+\'px\',\r\n                    left: (((49-i)%5)*190)+\'px\'\r\n                }, \'fast\');\r\n                i++;\r\n            }\r\n            else return;\r\n        };\r\n    $(\'#show\').on(\'click\', function(){\r\n        /*\r\n    var i = 0, j = $case.length;\r\n    for(; i<j; i++){\r\n      $case.eq(i).animate({\r\n        top: (Math.floor(i/5)*50)+\'px\',\r\n        left: ((i%5)*190)+\'px\'\r\n      });\r\n    }\r\n    */\r\n        animate();\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js','页面效果实验',NULL,'../image/default/no-pic.png',4,NULL,'2013-09-21 15:47:20',NULL,NULL,'','');
INSERT INTO `editor` VALUES (96,'<div class=\"case\">\r\n    <p>更多参考：\r\n        <a href=\"https://github.com/peachananr/wheel-menu\">\r\n            https://github.com/peachananr/wheel-menu\r\n        </a>\r\n    </p>\r\n    \r\n    <a href=\"#wheel\" class=\"wheel-button\" id=\"wheelButton\">\r\n        <span>+</span>\r\n    </a>\r\n    <div class=\"pointer\">Hover me</div>\r\n    <ul id=\"wheel\"  data-angle=\"all\">\r\n        <li class=\"item\"><a href=\"#home\">A</a></li>\r\n        <li class=\"item\"><a href=\"#home\">B</a></li>\r\n        <li class=\"item\"><a href=\"#home\">C</a></li>\r\n        <li class=\"item\"><a href=\"#home\">D</a></li>\r\n        <li class=\"item\"><a href=\"#home\">E</a></li>\r\n        <li class=\"item\"><a href=\"#home\">F</a></li>\r\n        <li class=\"item\"><a href=\"#home\">G</a></li>\r\n        <li class=\"item\"><a href=\"#home\">H</a></li>\r\n        <li class=\"item\"><a href=\"#home\">I</a></li>\r\n        <li class=\"item\"><a href=\"#home\">J</a></li>\r\n    </ul>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\n.pointer {\r\n    color: #34495e;\r\n    font-family: \'Pacifico\', cursive;\r\n    font-size: 22px;\r\n    margin-top: -15px;\r\n    text-align: center;\r\n}\r\n\r\n.wheel-button, .wheel-button:visited {\r\n    line-height: 35px;\r\n    font-weight: bold;\r\n    font-size: 36px;\r\n    background: #df4727; \r\n    text-decoration: none;\r\n    padding: 10px 11px;\r\n    text-align: center;\r\n    border-radius: 50px;\r\n    width: 35px;\r\n    height: 35px;\r\n    color: white;\r\n    display: block;\r\n    margin: 100px auto 20px;\r\n    border: 3px solid #92311e;\r\n    box-shadow: 0 1px 2px rgba(0,0,0,0.25);\r\n    -moz-box-shadow: 0 1px 2px rgba(0,0,0,0.25);\r\n    -webkit-box-shadow: 0 1px 2px rgba(0,0,0,0.25);\r\n}\r\n\r\n.wheel-button:hover{ \r\n    color: white;\r\n}\r\n\r\n.wheel-button.ne {\r\n    border-color: white;\r\n    background: #1ABC9C;\r\n    color: #34FFFF;\r\n    position: absolute;\r\n    bottom: 10px;\r\n    left: 10px;\r\n}\r\n\r\n.wheel-button.nw {\r\n    border-color: white;\r\n    background-color: #E67E22;\r\n    color: #FFFC44;\r\n    position: absolute;\r\n    bottom: 10px;\r\n    right: 10px;\r\n}\r\n\r\n.wheel-button span, .wheel span{\r\n    position: relative;\r\n    -moz-transition: all 1s ease;\r\n    -webkit-transition: all 1s ease;\r\n    -o-transition: all 1s ease;\r\n    transition: all 1s ease;\r\n    display: block;\r\n}\r\n\r\n.wheel-button.active span{\r\n    transform: rotate(135deg);\r\n    -ms-transform: rotate(135deg); /* IE 9 */\r\n    -webkit-transform: rotate(135deg); /* Safari and Chrome */\r\n}\r\n\r\n.wheel li a, .wheel li a:visited{\r\n    background: rgba(0,0,0,0.65);\r\n    border-radius: 50px;\r\n    font-weight: bold;\r\n    padding: 10px;\r\n    text-align: center;\r\n    width: 20px;\r\n    height: 20px;\r\n    border: 1px solid black;\r\n    box-shadow: 0 1px 2px rgba(0,0,0,0.25), inset 0 1px 1px rgba(255,255,255,0.5);\r\n    -moz-box-shadow: 0 1px 2px rgba(0,0,0,0.25), inset 0 1px 1px rgba(255,255,255,0.5);\r\n    -webkit-box-shadow: 0 1px 2px rgba(0,0,0,0.25), inset 0 1px 1px rgba(255,255,255,0.5);\r\n    color: white;\r\n    -moz-transition: all 0.25s ease;\r\n    -webkit-transition: all 0.25s ease;\r\n    -o-transition: all 0.25s ease;\r\n    transition: all 0.25s ease;\r\n}\r\n\r\n.wheel li a:hover{\r\n    background: rgba(0,0,0,0.8);\r\n}','$(function(){\r\n    \r\n    $(\'#wheelButton\').wheelmenu({\r\n        trigger: \'hover\',\r\n        animation: \'fly\',\r\n        animationSpeed: \'fast\'\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/wheelmenu/jquery.wheelmenu.css,../script/plugin/wheelmenu/jquery.wheelmenu.min.js','JS 插件-旋转菜单[第三方]',NULL,'../image/preview/wheelmenu.jpg',1,NULL,'2013-09-25 11:32:48',NULL,NULL,'122','jQuery 插件');
