INSERT INTO `editor` VALUES (100,'<div class=\"case case1\" id=\"case1\">\r\n    <h3>CSS3 过度效果测试</h3>\r\n    <p>\r\n        与 animation 属性相类似，也是实现动画效果，唯一的区别就是 animation 可以不用触发，而 transition 只能被动的触发，或者通过 JavaScript 修改样式时来触发，如 \r\n        <input type=\"button\" value=\"触发\" id=\"trigger\" />\r\n    </p>\r\n    \r\n    \r\n    <div class=\"test\" id=\"test\">\r\n        当鼠标移动到此上，触发过段效果\r\n        <br />\r\n        参考教程：\r\n        <a href=\"http://www.w3cplus.com/content/css3-transition\">\r\n            http://www.w3cplus.com/content/css3-transition\r\n        </a>\r\n    </div>\r\n</div>','.case{\r\n    overflow: hidden;\r\n    height: 450px;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}\r\n.test{\r\n    float: left;\r\n    width: 300px;\r\n    height: 300px;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    background: green;\r\n    color: white;\r\n    text-align: center;\r\n    line-height: 2em;\r\n    \r\n    \r\n    -webkit-transition: all 3s ease-in;\r\n    -moz-transition: all 3s ease-in;\r\n    -ms-transition: all 3s ease-in;\r\n    -o-transition: all 3s ease-in;\r\n    transition: all 3s ease-in;\r\n}\r\n.test:hover{\r\n    width: 240px;\r\n    height: 240px;\r\n    border-radius: 150px;\r\n    border-width: 30px;\r\n    margin-left: 100px;\r\n    background: red;\r\n}\r\n.transition{\r\n    width: 100px;\r\n    height: 100px;\r\n    border-radius: 150px;\r\n    border-width: 30px;\r\n    margin-left: 100px;\r\n    background: yellow;\r\n}','$(function(){\r\n    var $test = $(\'#test\');\r\n    \r\n    $(\'#trigger\').on(\'click\', function(){\r\n        $test.toggleClass(\'transition\');\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js','CSS3 实验-过渡效果',NULL,'../image/default/no-pic.png',2,NULL,'2013-09-28 16:00:07',NULL,NULL,'2,8,105','CSS3,CSS,动画效果');
INSERT INTO `editor` VALUES (101,'<div class=\"case\">\r\n    <h3>CSS3 阴影效果实验</h3>\r\n    <div class=\"test test1\"></div>\r\n    <div class=\"test test2\">各个方向不同颜色的阴影</div>\r\n    <div class=\"test test3\">阴影嵌套</div>\r\n    <div class=\"test test4\">内置阴影</div>\r\n</div>','.case{\r\n    overflow: hidden;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}\r\n.test{\r\n    float: left;\r\n    \r\n    height: 300px;\r\n    width: 300px;  \r\n    \r\n    margin: 10px;\r\n    background: green;\r\n    \r\n    line-height: 200%;\r\n    text-align: center;\r\n}\r\n.test1{\r\n    -webkit-box-shadow: 5px 5px 5px #555;\r\n    -moz-box-shadow: 5px 5px 5px #555;\r\n    box-shadow: 5px 5px 5px #555;\r\n}\r\n.test2{\r\n    \r\n    -webkit-box-shadow: -4px 0 5px cyan,\r\n            0 -5px 5px blue,\r\n            0 5px 5px red,\r\n            5px 0 5px yellow;\r\n    -moz-box-shadow: -5px 0 5px cyan,\r\n            0 -5px 5px blue,\r\n            0 5px 5px red,\r\n            5px 0 5px yellow;\r\n    box-shadow: -5px 0 5px cyan,\r\n            0 -5px 5px blue,\r\n            0 5px 5px red,\r\n            5px 0 5px yellow;\r\n}\r\n.test3{\r\n    clear: left;\r\n    \r\n    -webkit-box-shadow: 0 0 5px red,\r\n        0 0 15px blue;\r\n    -moz-box-shadow: 0 0 5px red,\r\n        0 0 15px blue;\r\n    box-shadow: 0 0 5px red,\r\n        0 0 15px blue;\r\n}\r\n.test4{\r\n    background: gray;\r\n    -webkit-box-shadow: inset 0 0 10px blue;\r\n    -moz-box-shadow: inset 0 0 10px blue;\r\n    box-shadow: inset 0 0 10px blue;\r\n}','','../script/lib/jQuery/jquery-1.10.2.min.js','CSS3 实验-阴影效果',NULL,'../image/preview/box-shadow.jpg',2,NULL,'2013-09-28 18:02:22',NULL,NULL,'2,8','CSS3,CSS');
INSERT INTO `editor` VALUES (102,'<div class=\"case\">\r\n    <h3>CSS3 文字阴影效果</h3>\r\n    \r\n    <p>参考教程：\r\n        <a href=\"http://www.w3cplus.com/node/52\">\r\n            http://www.w3cplus.com/node/52\r\n        </a>\r\n    </p>\r\n    \r\n    <p class=\"test test1\">\r\n        text shadow 文字阴影\r\n    </p>\r\n    <p class=\"test test2\">\r\n        NEON effect 文字阴影\r\n    </p>\r\n    <p class=\"test test3\">\r\n        NEON effect 文字阴影\r\n    </p>\r\n    <p class=\"test test4\">\r\n        Apple Style Effect 文字阴影\r\n    </p>\r\n    <p class=\"test test5\">\r\n        Photoshop Emboss Effect 文字效果\r\n    </p>\r\n    <p class=\"test test6\">\r\n        Blury text Effect 文字效果\r\n    </p>\r\n    <p class=\"test test7\">\r\n        Photoshop Emboss 文字效果\r\n    </p>\r\n    <p class=\"test test8\">\r\n        Inset text effect 文字效果\r\n    </p>\r\n    <p class=\"test test9\">\r\n        Stroke text effect 文字效果\r\n    </p>\r\n    <p class=\"test test10\">\r\n        3D text effect 文字效果\r\n    </p>\r\n    <p class=\"test test11\">\r\n        3D text effect 文字效果\r\n    </p>\r\n    <p class=\"test test12\">\r\n        Vintge/Retro text effect 文字效果\r\n    </p>\r\n    <p class=\"test test13\">\r\n        Anaglyphic text effect 文字效果\r\n    </p>\r\n    <p class=\"test text14\">\r\n        True Inset effect 文字效果\r\n    </p>\r\n</div>','.case{\r\n    overflow: hidden;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}\r\n.test{\r\n    overflow: hidden;\r\n    margin: 10px;\r\n    background: #666666;\r\n    font: bold 55px/150% \"微软雅黑\", \"Lucida Grande\", \"Lucida Sans\", Helvetica, Arial, Sans;\r\n    color: #fff;\r\n    text-align: center;\r\n    text-transform: uppercase;\r\n}\r\n.test1{\r\n    -webkit-text-shadow: red 0 1px 0;\r\n    -moz-text-shadow: red 0 1px 0;\r\n    text-shadow: red 0 1px 0;\r\n}\r\n.test2{\r\n    -webkit-text-shadow: 0 0 20px red;\r\n    -moz-text-shadow: 0 0 20px red;\r\n    text-shadow: 0 0 20px red;\r\n}\r\n.test3{\r\n    -webkit-text-shadow: 0 0 5px #fff,\r\n            0 0 10px #fff,\r\n            0 0 15px #fff,\r\n            0 0 40px #ff00de,\r\n            0 0 70px #ff00de;\r\n    -moz-text-shadow: 0 0 5px #fff,\r\n            0 0 10px #fff,\r\n            0 0 15px #fff,\r\n            0 0 40px #ff00de,\r\n            0 0 70px #ff00de;\r\n    text-shadow: 0 0 5px #fff,\r\n            0 0 10px #fff,\r\n            0 0 15px #fff,\r\n            0 0 40px #ff00de,\r\n            0 0 70px #ff00de;\r\n}\r\n.test4{\r\n    color: #000;\r\n    -webkit-text-shadow: 0 1px 1px #fff;\r\n    -moz-text-shadow: 0 1px 1px #fff;\r\n    text-shadow: 0 1px 1px #fff;\r\n}\r\n.test5{\r\n    color: #ccc;\r\n    -webkit-text-shadow: -1px -1px 0 #fff,1px 1px 0 #333,1px 1px 0 #444;\r\n    -moz-text-shadow: -1px -1px 0 #fff,1px 1px 0 #333,1px 1px 0 #444;\r\n    text-shadow: -1px -1px 0 #fff,1px 1px 0 #333,1px 1px 0 #444;\r\n}\r\n.test6{\r\n    color: transparent;\r\n    -webkit-text-shadow: 0 0 5px #f96;\r\n    -moz-text-shadow: 0 0 5px #f96;\r\n    text-shadow: 0 0 5px #f96;\r\n}\r\n.test7{\r\n    color: transparent;\r\n    -webkit-text-shadow: 0 0 6px #F96, -1px -1px #FFF, 1px -1px #444;\r\n    -moz-text-shadow: 0 0 6px #F96, -1px -1px #FFF, 1px -1px #444;\r\n    text-shadow: 0 0 6px #F96, -1px -1px #FFF, 1px -1px #444;\r\n}\r\n.test8{\r\n    color: #566F89;\r\n    background: #C5DFF8;\r\n    -webkit-text-shadow: 1px 1px 0 #E4F1FF;\r\n    -moz-text-shadow: 1px 1px 0 #E4F1FF;\r\n    text-shadow: 1px 1px 0 #E4F1FF; \r\n}\r\n.test9{\r\n    color: #fff;\r\n    -webkit-text-shadow: 1px 1px 0 #f96,-1px -1px 0 #f96;\r\n    -moz-text-shadow: 1px 1px 0 #f96,-1px -1px 0 #f96;\r\n    text-shadow: 1px 1px 0 #f96,-1px -1px 0 #f96; \r\n}\r\n.test10{\r\n    color: #fff;\r\n    -webkit-text-shadow: 1px 1px rgba(197, 223, 248,0.8),\r\n        2px 2px rgba(197, 223, 248,0.8),\r\n        3px 3px rgba(197, 223, 248,0.8),\r\n        4px 4px rgba(197, 223, 248,0.8),\r\n        5px 5px rgba(197, 223, 248,0.8),\r\n        6px 6px rgba(197, 223, 248,0.8);\r\n    -moz-text-shadow: 1px 1px rgba(197, 223, 248,0.8),\r\n        2px 2px rgba(197, 223, 248,0.8),\r\n        3px 3px rgba(197, 223, 248,0.8),\r\n        4px 4px rgba(197, 223, 248,0.8),\r\n        5px 5px rgba(197, 223, 248,0.8),\r\n        6px 6px rgba(197, 223, 248,0.8);\r\n    text-shadow: 1px 1px rgba(197, 223, 248,0.8),\r\n        2px 2px rgba(197, 223, 248,0.8),\r\n        3px 3px rgba(197, 223, 248,0.8),\r\n        4px 4px rgba(197, 223, 248,0.8),\r\n        5px 5px rgba(197, 223, 248,0.8),\r\n        6px 6px rgba(197, 223, 248,0.8);\r\n}\r\n.test11{\r\n    color: #fff;\r\n    -webkit-text-shadow: -1px -1px rgba(197, 223, 248,0.8),\r\n        -2px -2px rgba(197, 223, 248,0.8),\r\n        -3px -3px rgba(197, 223, 248,0.8),\r\n        -4px -4px rgba(197, 223, 248,0.8),\r\n        -5px -5px rgba(197, 223, 248,0.8),\r\n        -6px -6px rgba(197, 223, 248,0.8);\r\n    -moz-text-shadow: -1px -1px rgba(197, 223, 248,0.8),\r\n        -2px -2px rgba(197, 223, 248,0.8),\r\n        -3px -3px rgba(197, 223, 248,0.8),\r\n        -4px -4px rgba(197, 223, 248,0.8),\r\n        -5px -5px rgba(197, 223, 248,0.8),\r\n        -6px -6px rgba(197, 223, 248,0.8);\r\n    text-shadow: -1px -1px rgba(197, 223, 248,0.8),\r\n        -2px -2px rgba(197, 223, 248,0.8),\r\n        -3px -3px rgba(197, 223, 248,0.8),\r\n        -4px -4px rgba(197, 223, 248,0.8),\r\n        -5px -5px rgba(197, 223, 248,0.8),\r\n        -6px -6px rgba(197, 223, 248,0.8);\r\n}\r\n.test12{\r\n    color: #eee;\r\n    -webkit-text-shadow: 5px 5px 0 #666, 7px 7px 0 #eee;\r\n    -moz-text-shadow: 5px 5px 0 #666, 7px 7px 0 #eee;\r\n    text-shadow: 5px 5px 0 #666, 7px 7px 0 #eee;\r\n}\r\n.test13{\r\n    color: rgba(255, 179, 140,0.5);\r\n    -webkit-text-shadow: 3px 3px 0 rgba(180,255,0,0.5);\r\n    -moz-text-shadow: 3px 3px 0 rgba(180,255,0,0.5);\r\n    text-shadow: 3px 3px 0 rgba(180,255,0,0.5);\r\n}\r\n.test14{\r\n    background: #666;\r\n    -webkit-background-clip: text;\r\n    -moz-background-clip: text;\r\n    background-clip: text;\r\n    color: transparent;\r\n    text-shadow: 0 3px 3px rgba(255,255,255,0.5);\r\n}','','../script/lib/jQuery/jquery-1.10.2.min.js','CSS3 实验-文字阴影',NULL,'../image/preview/text-shadow.jpg',2,NULL,'2013-09-29 14:50:53',NULL,NULL,'2,8,87','CSS3,CSS,文字特效');
INSERT INTO `editor` VALUES (103,'<div class=\"case\">\r\n    <h3>CSS 实现选项卡效果</h3>\r\n    \r\n    <div class=\"tabs\">\r\n        <div class=\"tab\">\r\n            <div class=\"tab_head tab_head-current\">head 1</div>\r\n            <div class=\"tab_content\">content 1</div>\r\n        </div>\r\n        <div class=\"tab\">\r\n            <div class=\"tab_head\">head 2</div>\r\n            <div class=\"tab_content\">content 2</div>\r\n        </div>\r\n        <div class=\"tab\">\r\n            <div class=\"tab_head\">head 3</div>\r\n            <div class=\"tab_content\">content 3</div>\r\n        </div>\r\n        <div class=\"tab\">\r\n            <div class=\"tab_head\">head 4</div>\r\n            <div class=\"tab_content\">content 4</div>\r\n        </div>\r\n    </div>\r\n</div>','.case{\r\n    height: 500px;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}\r\n\r\n.tabs{\r\n    position: relative;\r\n    height: 100%;\r\n}\r\n.tab_head{\r\n    float: left;\r\n    height:69px;\r\n    min-width: 100px;\r\n    border: 1px solid #c0c0c0;\r\n    border-bottom: 0;\r\n    border-radius: 10px 10px 0 0;\r\n    margin-right: 10px;\r\n    background: #efefef;\r\n    \r\n    line-height: 200%;\r\n    text-align: center;\r\n}\r\n.tab_head-current,\r\n.tab_head:hover{\r\n    position: relative;\r\n    z-index: 10;\r\n    border-bottom: 1px solid white;\r\n    background: white;\r\n}\r\n.tab_head-current + .tab_content{\r\n    opacity: 1;\r\n}\r\n.tab_head:hover + .tab_content{\r\n    opacity: 1;\r\n}\r\n.tab_content{\r\n    position: absolute;\r\n    bottom: 0;\r\n    left: 0;\r\n    right: 0;\r\n    top: 70px;\r\n    border: 1px solid #c0c0c0;\r\n    background: white;\r\n    \r\n    opacity: 0;\r\n    \r\n    line-height: 200%;\r\n    text-align: center;\r\n    \r\n    -webkit-transition : opacity 0.5s ease-in 0.218s;\r\n    -moz-transition : opacity 0.5s ease-in 0.218s;\r\n    -o-transition : opacity 0.5s ease-in 0.218s;\r\n    -ms-transition : opacity 0.5s ease-in 0.218s;\r\n    transition : opacity 0.5s ease-in 0.218s;\r\n}','','../script/lib/jQuery/jquery-1.10.2.min.js','CSS 示例-CSS 实现 tabs 选项卡',NULL,'../image/default/no-pic.png',4,NULL,'2013-10-08 15:30:27',NULL,NULL,'','');
INSERT INTO `editor` VALUES (104,'<div class=\"case\">\r\n    <p>\r\n        更多参考：\r\n        <a href=\"http://www.forehack.com/3d-css-cube/\">\r\n            http://www.forehack.com/3d-css-cube/\r\n        </a>\r\n    </p>\r\n    \r\n    <input type=\"button\" id=\"rotateX\" value=\"x 轴旋转\" />\r\n    <input type=\"button\" id=\"rotateY\" value=\"y 轴旋转\" />\r\n    <input type=\"button\" id=\"rotateZ\" value=\"Z 轴旋转\" />\r\n    \r\n    <div class=\"wrap\">\r\n        <div class=\"cube\" id=\"cube\">\r\n            <div class=\"front\">前</div>\r\n            <div class=\"back\">后</div>\r\n            <div class=\"top\">上</div>\r\n            <div class=\"bottom\">下</div>\r\n            <div class=\"left\">左</div>\r\n            <div class=\"right\">右</div>\r\n        </div>\r\n    </div>\r\n</div>','.case{\r\n    height: 800px;\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n\r\n.wrap {\r\n    margin: 200px;\r\n    \r\n    -webkit-perspective: 800px;\r\n    -moz-perspective: 800px;\r\n    -ms-perspective: 800px;\r\n    perspective: 800px;\r\n    \r\n    -webkit-perspective-origin: 50% 100px;\r\n    -moz-perspective-origin: 50% 100px;\r\n    -ms-perspective-origin: 50% 100px;\r\n    perspective-origin: 50% 100px;\r\n}\r\n.cube {\r\n    position: relative;\r\n    width: 200px;\r\n    \r\n    -webkit-transform-style: preserve-3d;\r\n    -moz-transform-style: preserve-3d;\r\n    -ms-transform-style: preserve-3d;\r\n    transform-style: preserve-3d;\r\n}\r\n.cube div {\r\n    position: absolute;\r\n    width: 200px;\r\n    height: 200px;\r\n    \r\n    line-height: 200px;\r\n    text-align: center;\r\n    \r\n    box-shadow: 0 0 30px rgba(125, 125, 125, 0.8) inset;\r\n}\r\n.front {\r\n    -webkit-transform: translateZ(100px);\r\n    -moz-transform: translateZ(100px);\r\n    -ms-transform: translateZ(100px);\r\n    -o-transform: translateZ(100px);\r\n    transform: translateZ(100px);\r\n}\r\n.back {\r\n    -webkit-transform: translateZ(-100px) rotateY(180deg);\r\n    -moz-transform: translateZ(-100px) rotateY(180deg);\r\n    -ms-transform: translateZ(-100px) rotateY(180deg);\r\n    -o-transform: translateZ(-100px) rotateY(180deg);\r\n    transform: translateZ(-100px) rotateY(180deg);\r\n}\r\n.left {\r\n    -webkit-transform: rotateY(270deg) translateX(-100px);\r\n    -moz-transform: rotateY(270deg) translateX(-100px);\r\n    -ms-transform: rotateY(270deg) translateX(-100px);\r\n    -o-transform: rotateY(270deg) translateX(-100px);\r\n    transform: rotateY(270deg) translateX(-100px);\r\n    \r\n    -webkit-transform-origin: center left;\r\n    -moz-transform-origin: center left;\r\n    -ms-transform-origin: center left;\r\n    -o-transform-origin: center left;\r\n}\r\n.right {\r\n    -webkit-transform: rotateY(-270deg) translateX(100px);\r\n    -moz-transform: rotateY(-270deg) translateX(100px);\r\n    -ms-transform: rotateY(-270deg) translateX(100px);\r\n    -o-transform: rotateY(-270deg) translateX(100px);\r\n    transform: rotateY(-270deg) translateX(100px);\r\n            \r\n    -webkit-transform-origin: top right;\r\n    -moz-transform-origin: top right;\r\n    -ms-transform-origin: top right;\r\n    -o-transform-origin: top right;\r\n    transform-origin: top right;\r\n}\r\n.top {\r\n    -webkit-transform: rotateX(-90deg) translateY(-100px);\r\n    -moz-transform: rotateX(-90deg) translateY(-100px);\r\n    -ms-transform: rotateX(-90deg) translateY(-100px);\r\n    -o-transform: rotateX(-90deg) translateY(-100px);\r\n    transform: rotateX(-90deg) translateY(-100px);\r\n\r\n    -webkit-transform-origin: top center 0;\r\n    -moz-transform-origin: top center 0;\r\n    -ms-transform-origin: top center 0;\r\n    -o-transform-origin: top center 0;\r\n    transform-origin: top center 0;\r\n}\r\n.bottom {\r\n    -webkit-transform: rotateX(90deg) translateY(100px);\r\n    -moz-transform: rotateX(90deg) translateY(100px);\r\n    -ms-transform: rotateX(90deg) translateY(100px);\r\n    -o-transform: rotateX(90deg) translateY(100px);\r\n    transform: rotateX(90deg) translateY(100px);\r\n\r\n    -webkit-transform-origin: bottom center;\r\n    -moz-transform-origin: bottom center;\r\n    -ms-transform-origin: bottom center;\r\n    -o-transform-origin: bottom center;\r\n    transform-origin: bottom center;\r\n}\r\n\r\n@-webkit-keyframes rotateY{\r\n    from{\r\n        -webkit-transform: rotateY(0);\r\n    }\r\n    to{\r\n        -webkit-transform: rotateY(360deg);\r\n    }\r\n}\r\n@keyframes rotateY{\r\n    from{\r\n        transform: rotateY(0);\r\n    }\r\n    to{\r\n        transform: rotateY(360deg);\r\n    }\r\n}\r\n.rotateY {\r\n    -webkit-animation: rotateY 5s linear infinite;\r\n    -moz-animation: rotateY 5s linear infinite;\r\n    -o-animation: rotateY 5s linear infinite;\r\n    animation: rotateY 5s linear infinite;\r\n}\r\n\r\n@-webkit-keyframes rotateX{\r\n    from{\r\n        -webkit-transform: rotateX(0);\r\n    }\r\n    to{\r\n        -webkit-transform: rotateX(-360deg);\r\n    }\r\n}\r\n@keyframes rotateX {\r\n    from{\r\n        transform: rotateX(0);\r\n    }\r\n    to{\r\n        transform: rotateX(-360deg);\r\n    }\r\n}\r\n.rotateX {\r\n    -webkit-transform-origin: 0 100px;\r\n    -moz-transform-origin: 0 100px;\r\n    -ms-transform-origin: 0 100px;\r\n    -o-transform-origin: 0 100px;\r\n    transform-origin: 0 100px;\r\n    \r\n    -webkit-animation: rotateX 5s infinite linear;\r\n    -moz-animation: rotateX 5s infinite linear;\r\n    -o-animation: rotateX 5s infinite linear;\r\n    animation: rotateX 5s infinite linear;\r\n}\r\n.rotateX .top {\r\n    -webkit-transform: rotateX(-270deg) translateY(-100px);\r\n    -moz-transform: rotateX(-270deg) translateY(-100px);\r\n    -ms-transform: rotateX(-270deg) translateY(-100px);\r\n    -o-transform: rotateX(-270deg) translateY(-100px);\r\n    transform: rotateX(-270deg) translateY(-100px);\r\n}\r\n.rotateX .back {\r\n    -webkit-transform: translateZ(-100px) rotateX(180deg);\r\n    -moz-transform: translateZ(-100px) rotateX(180deg);\r\n    -ms-transform: translateZ(-100px) rotateX(180deg);\r\n    -o-transform: translateZ(-100px) rotateX(180deg);\r\n    transform: translateZ(-100px) rotateX(180deg);\r\n}\r\n.rotateX .bottom {\r\n    -webkit-transform: rotateX(-90deg) translateY(100px);\r\n    -moz-transform: rotateX(-90deg) translateY(100px);\r\n    -ms-transform: rotateX(-90deg) translateY(100px);\r\n    -o-transform: rotateX(-90deg) translateY(100px);\r\n    transform: rotateX(-90deg) translateY(100px);\r\n}\r\n\r\n@-webkit-keyframes rotateZ {\r\n    from{\r\n        -webkit-transform: rotateZ(0);\r\n    }\r\n    to{\r\n        -webkit-transform: rotateZ(-360deg);\r\n    }\r\n}\r\n@keyframes rotateZ {\r\n    from{\r\n        transform: rotateZ(0);\r\n    }\r\n    to{\r\n        transform: rotateZ(-360deg);\r\n    }\r\n}\r\n.rotateZ{\r\n    -webkit-animation: rotateZ 5s infinite linear;\r\n    -moz-animation: rotateZ 5s infinite linear;\r\n    -o-animation: rotateZ 5s infinite linear;\r\n    animation: rotateZ 5s infinite linear;\r\n}','$(function(){\r\n    var $cube = $(\'#cube\');\r\n    \r\n    $(\'#rotateX\').on(\'click\', function(){\r\n        $cube.removeClass(\'rotateY rotateZ\').toggleClass(\'rotateX\');\r\n    });\r\n    \r\n    $(\'#rotateY\').on(\'click\', function(){\r\n        $cube.removeClass(\'rotateX rotateZ\').toggleClass(\'rotateY\');\r\n    });\r\n    \r\n    $(\'#rotateZ\').on(\'click\', function(){\r\n        $cube.removeClass(\'rotateX rotateY\').toggleClass(\'rotateZ\');\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js','CSS3 实验-3D 立方体旋转',NULL,'../image/preview/3d-cube.jpg',2,NULL,'2013-11-12 10:12:59',NULL,NULL,'2,8,105','CSS3,CSS,动画效果');
INSERT INTO `editor` VALUES (105,'<div class=\"case\">\r\n    <p>可能比例小了点，但还是将就吧，做个练习自娱自乐的</p>\r\n    \r\n    <div class=\"watch_wrap\" id=\"watch\">\r\n        <span class=\"watch_hourHand\" id=\"hourHand\"></span>\r\n        <span class=\"watch_minuteHand\" id=\"minuteHand\"></span>\r\n        <span class=\"watch_secondHand\" id=\"secondHand\"></span>\r\n        <span class=\"watch_mark watch_mark-1\"></span>\r\n        <span class=\"watch_mark watch_mark-2\"></span>\r\n        <span class=\"watch_mark watch_mark-3\"></span>\r\n        <span class=\"watch_mark watch_mark-4\"></span>\r\n        <span class=\"watch_mark watch_mark-5\"></span>\r\n        <span class=\"watch_mark watch_mark-6\"></span>\r\n        <span class=\"watch_mark watch_mark-7\"></span>\r\n        <span class=\"watch_mark watch_mark-8\"></span>\r\n        <span class=\"watch_mark watch_mark-9\"></span>\r\n        <span class=\"watch_mark watch_mark-10\"></span>\r\n        <span class=\"watch_mark watch_mark-11\"></span>\r\n        <span class=\"watch_mark watch_mark-12\"></span>\r\n    </div>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n.watch_wrap {\r\n    position: relative;\r\n    \r\n    height: 80px;\r\n    width: 80px;\r\n    \r\n    border: 5px solid purple;\r\n    border-radius: 80px;\r\n    \r\n    margin: 10px auto;\r\n}\r\n.watch_hourHand,\r\n.watch_minuteHand,\r\n.watch_secondHand,\r\n.watch_mark {\r\n    position: absolute;\r\n    \r\n    -webkit-transform-origin: center 40px 0;\r\n    -moz-transform-origin: center 40px 0;\r\n    -ms-transform-origin: center 40px 0;\r\n    -o-transform-origin: center 40px 0;\r\n    transform-origin: center 40px 0;\r\n}\r\n.watch_hourHand,\r\n.watch_minuteHand,\r\n.watch_secondHand {\r\n    height: 0;\r\n    width: 0;\r\n    border-color: transparent transparent purple;\r\n    border-style: solid;\r\n}\r\n.watch_hourHand {\r\n    left: 30px;\r\n    top: 20px;\r\n    \r\n    border-width: 5px 10px;\r\n    \r\n    -webkit-transform-origin: center 20px 0;\r\n    -moz-transform-origin: center 20px 0;\r\n    -ms-transform-origin: center 20px 0;\r\n    -o-transform-origin: center 20px 0;\r\n    transform-origin: center 20px 0;\r\n}\r\n.watch_hourHand:after {\r\n    display: block;\r\n    height: 10px;\r\n    width: 10px;\r\n    \r\n    margin: 5px 0 0 -5px;\r\n    \r\n    background: none repeat scroll 0 0 purple;\r\n    \r\n    content: \"\";\r\n}\r\n.watch_minuteHand {\r\n    left: 34px;\r\n    border-width: 12px 6px;\r\n}\r\n.watch_minuteHand:after {\r\n    display: block;\r\n    height: 20px;\r\n    width: 6px;\r\n    \r\n    margin: 12px 0 0 -3px;\r\n    \r\n    background: none repeat scroll 0 0 purple;\r\n    \r\n    content: \"\";\r\n}\r\n.watch_secondHand {\r\n    left: 34px;\r\n    border-width: 6px;\r\n}\r\n.watch_secondHand:after {\r\n    display: block;\r\n    height: 30px;\r\n    width: 4px;\r\n    \r\n    margin: 6px 0 0 -2px;\r\n    \r\n    background: none repeat scroll 0 0 purple;\r\n    \r\n    content: \"\";\r\n}\r\n.watch_mark {\r\n    left: 38px;\r\n    top: 0;\r\n    \r\n    height: 5px;\r\n    width: 4px;\r\n    \r\n    background: none repeat scroll 0 0 purple;\r\n}\r\n.watch_mark-2 {\r\n    -webkit-transform: rotate(30deg);\r\n    -moz-transform: rotate(30deg);\r\n    -ms-transform: rotate(30deg);\r\n    transform: rotate(30deg);\r\n}\r\n.watch_mark-3 {\r\n    -webkit-transform: rotate(60deg);\r\n    -moz-transform: rotate(60deg);\r\n    -ms-transform: rotate(60deg);\r\n    transform: rotate(60deg);\r\n}\r\n.watch_mark-4 {\r\n    -webkit-transform: rotate(90deg);\r\n    -moz-transform: rotate(90deg);\r\n    -ms-transform: rotate(90deg);\r\n    transform: rotate(90deg);\r\n}\r\n.watch_mark-5 {\r\n    -webkit-transform: rotate(120deg);\r\n    -moz-transform: rotate(120deg);\r\n    -ms-transform: rotate(120deg);\r\n    transform: rotate(120deg);\r\n}\r\n.watch_mark-6 {\r\n    -webkit-transform: rotate(150deg);\r\n    -moz-transform: rotate(150deg);\r\n    -ms-transform: rotate(150deg);\r\n    transform: rotate(150deg);\r\n}\r\n.watch_mark-7 {\r\n    -webkit-transform: rotate(180deg);\r\n    -moz-transform: rotate(180deg);\r\n    -ms-transform: rotate(180deg);\r\n    transform: rotate(180deg);\r\n}\r\n.watch_mark-8 {\r\n    -webkit-transform: rotate(210deg);\r\n    -moz-transform: rotate(210deg);\r\n    -ms-transform: rotate(210deg);\r\n    transform: rotate(210deg);\r\n}\r\n.watch_mark-9 {\r\n    -webkit-transform: rotate(240deg);\r\n    -moz-transform: rotate(240deg);\r\n    -ms-transform: rotate(240deg);\r\n    transform: rotate(240deg);\r\n}\r\n.watch_mark-10 {\r\n    -webkit-transform: rotate(270deg);\r\n    -moz-transform: rotate(270deg);\r\n    -ms-transform: rotate(270deg);\r\n    transform: rotate(270deg);\r\n}\r\n.watch_mark-11 {\r\n    -webkit-transform: rotate(300deg);\r\n    -moz-transform: rotate(300deg);\r\n    -ms-transform: rotate(300deg);\r\n    transform: rotate(300deg);\r\n}\r\n.watch_mark-12 {\r\n    -webkit-transform: rotate(330deg);\r\n    -moz-transform: rotate(330deg);\r\n    -ms-transform: rotate(330deg);\r\n    transform: rotate(330deg);\r\n}','$(function(){\r\n    var prefix = \'\',\r\n        temp = document.createElement(\'div\').style;\r\n    \r\n    if( \'-webkit-transform\' in temp ){\r\n        prefix = \'-webkit-\';\r\n    }\r\n    else if( \'-moz-transform\' in temp ){\r\n        prefix = \'-moz-\';\r\n    }\r\n    else if( \'-ms-transform\' in temp ){\r\n        prefix = \'-ms-\';\r\n    }\r\n    else if( \'-o-transform\' in temp ){\r\n        prefix = \'-o-\';\r\n    }\r\n    \r\n    var $watch = $(\'#watch\'),\r\n        $hourHand = $watch.find(\'#hourHand\'),\r\n        $minuteHand = $watch.find(\'#minuteHand\'),\r\n        $secondHand = $watch.find(\'#secondHand\'),\r\n        \r\n        setTime = function(){\r\n            var time = new Date();\r\n            $hourHand.get(0).style[prefix +\'transform\'] = \'rotate(\'+\r\n                ((time.getHours()>11 ?\r\n                     time.getHours()-12 :\r\n                     time.getHours())*30 + Math.floor( time.getMinutes()/12 )*6) +\'deg)\';\r\n            $minuteHand.get(0).style[prefix +\'transform\'] = \'rotate(\'+ time.getMinutes()*6 +\'deg)\';\r\n            $secondHand.get(0).style[prefix +\'transform\'] = \'rotate(\'+ time.getSeconds()*6 +\'deg)\';\r\n        };\r\n    \r\n    setTime();\r\n    setInterval(setTime, 1000);\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js','CSS3 实验-变形效果+JS 实现时钟效果',NULL,'../image/default/no-pic.png',2,NULL,'2013-11-13 16:00:13',NULL,NULL,'2,8,6,105','CSS3,CSS,JavaScript,动画效果');
INSERT INTO `editor` VALUES (106,'<div id=\"Container\" class=\"Container\">\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n    <div class=\"module module-tiny module-arrow\"><span class=\"icon1\"></span><span class=\"icon2\"></span></div>\r\n</div>','.Container{\r\n    width: 960px !important;\r\n}\r\n.module-tiny{\r\n    overflow:hidden;\r\n    height:150px;\r\n    border-radius:75px;\r\n    background:#E1E1E1 !important;\r\n    \r\n    -moz-transform-origin: center center;\r\n    -webkit-transform-origin: center center;\r\n    -o-transform-origin: center center;\r\n    -ms-transform-origin: center center;\r\n    transform-origin: center center;\r\n}\r\n.icon1{\r\n    float:left;\r\n    width:0;\r\n    height:0;\r\n    border-left:transparent solid 75px;\r\n    border-right:transparent solid 75px;\r\n    border-bottom:#2ECC71 solid 75px;\r\n    background:transparent !important;\r\n}\r\n.icon2{\r\n    float:left;\r\n    width:70px;\r\n    height:75px;\r\n    margin:0 40px;\r\n    background:#2ECC71 !important;\r\n}','$(function(){\r\n    var prefix = \'\',\r\n        temp = document.createElement(\'div\').style;\r\n    \r\n    if( \'-webkit-transform\' in temp ){\r\n        prefix = \'-webkit-\';\r\n    }\r\n    else if( \'-moz-transform\' in temp ){\r\n        prefix = \'-moz-\';\r\n    }\r\n    else if( \'-ms-transform\' in temp ){\r\n        prefix = \'-ms-\';\r\n    }\r\n    else if( \'-o-transform\' in temp ){\r\n        prefix = \'-o-\';\r\n    }\r\n    \r\n    var $arrow = $(\'.module-arrow\'),\r\n        cache = [],\r\n        i = $arrow.length,\r\n        temp, obj;\r\n    \r\n    while(i--){\r\n        obj = $arrow.eq(i);\r\n        temp = obj.offset();\r\n        temp.top += 75;\r\n        temp.left += 75;\r\n        temp.target = obj;\r\n        cache.push(temp)\r\n    }\r\n    \r\n    i = cache.length;\r\n    var $doc = $(document).on(\'mousemove\', function(e){\r\n        var l = i;\r\n        while(l--){\r\n            obj = cache[l];\r\n            temp = Math.floor( Math.atan2( obj.top - e.pageY, obj.left - e.pageX )*180/3.14)-90;\r\n            obj.target.css(prefix +\'transform\', \'rotate(\'+ temp +\'deg)\' );\r\n        }\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../style/style.css','页面效果-指向鼠标',NULL,'../image/preview/arrow.jpg',4,NULL,'2013-11-14 11:14:27',NULL,NULL,'105,2,48','动画效果,CSS3,原创');
