INSERT INTO `editor` VALUES (113,'<div class=\"case\">\r\n    <h3>文本溢出隐藏</h3>\r\n    \r\n    <p>\r\n        text-overflow 属性是一个 CSS3 属性，需要配合 overflow: hidden; white-space: nowrap; 以及 width、height 属性使用，\r\n        而且在早版本的 Firefox 浏览器中不会正常显示出省略号\r\n        <br />\r\n        <br />参考教程：\r\n        <a href=\"http://www.w3cplus.com/content/css3-text-overflow\">\r\n            http://www.w3cplus.com/content/css3-text-overflow\r\n        </a>\r\n    </p>\r\n    \r\n    <ul>\r\n        <li title=\"这是一段超出区域范围的文本\">这是一段超出区域范围的文本</li>\r\n        <li title=\"这是另一段超出区域范围的文本\">这是另一段超出区域范围的文本</li>\r\n        <li title=\"这是又一段超出区域范围的文本\">这是又一段超出区域范围的文本</li>\r\n    </ul>\r\n</div>','.case{\r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}\r\n\r\nli{\r\n    overflow: hidden;\r\n    \r\n    width: 100px;\r\n    \r\n    border: 1px solid #c0c0c0;\r\n    margin: 5px;\r\n    padding: 5px;\r\n    \r\n    line-height: 40px;\r\n    \r\n    text-overflow: ellipsis;\r\n    -o-text-overflow: ellipsis;\r\n\r\n    white-space: nowrap;\r\n    word-wrap: normal;\r\n}','','../script/lib/jQuery/jquery-1.10.2.min.js','CSS 示例-文本溢出隐藏',NULL,'../image/default/no-pic.png',2,NULL,'2013-11-16 16:04:47',NULL,NULL,'2,8','CSS3,CSS');
