INSERT INTO `editor` VALUES (184,'<link rel=\"stylesheet\" id=\"themes\" href=\"../script/plugin/pace/themes/pace-theme-barber-shop.css\" />\r\n<div class=\"case\">\r\n\t<h3>JS 插件-页面进度条</h3>\r\n\r\n    <p>更多参考：\r\n        <a href=\"http://github.hubspot.com/pace/\">\r\n            http://github.hubspot.com/pace/\r\n        </a>\r\n        <br />\r\n        <a href=\"http://github.hubspot.com/pace/docs/welcome/\">\r\n            http://github.hubspot.com/pace/docs/welcome/\r\n        </a>\r\n    </p>\r\n    <div class=\"testExample\">\r\n        <p>点击按钮更换皮肤</p>\r\n        \r\n        <button type=\"button\" id=\"run\" class=\"btn btn-run\">运行</button>\r\n        <button type=\"button\" class=\"btn btn-set\" id=\"minimal\">Minimal</button>\r\n        <button type=\"button\" class=\"btn btn-set\" id=\"flash\">Flash</button>\r\n        <button type=\"button\" class=\"btn btn-set\" id=\"barderShop\">Barder Shop</button>\r\n        <button type=\"button\" class=\"btn btn-set\" id=\"macos\">Mac OS</button>\r\n        <button type=\"button\" class=\"btn btn-set\" id=\"fillLeft\">Fill Left</button>\r\n        <button type=\"button\" class=\"btn btn-set\" id=\"flatTop\">Flat Top</button>\r\n        <button type=\"button\" class=\"btn btn-set\" id=\"bigCounter\">Big Counter</button>\r\n        <button type=\"button\" class=\"btn btn-set\" id=\"cornerIndicator\">Corner Indicator</button>\r\n        <button type=\"button\" class=\"btn btn-set\" id=\"bounce\">Bounce</button>\r\n        <button type=\"button\" class=\"btn btn-set\" id=\"loadingBar\">Loading Bar</button>\r\n        <button type=\"button\" class=\"btn btn-set\" id=\"centerCircle\">Center Circle</button>\r\n        <button type=\"button\" class=\"btn btn-set\" id=\"centerAtorm\">Center Atom</button>\r\n        <button type=\"button\" class=\"btn btn-set\" id=\"centerRadar\">Center Radar</button>\r\n        <button type=\"button\" class=\"btn btn-set\" id=\"centerSimple\">Center Simple</button>\r\n    </div>\r\n</div>','.case{\r\n    overflow: hidden;\r\n        \r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}\r\n\r\n.btn{\r\n    float: left;\r\n    height: 30px;\r\n    width: 120px;\r\n    margin: 5px;\r\n    line-height: 30px;\r\n}','$(function(){\r\n    var $run = $(\'#run\').on(\'click\', function(){\r\n            Pace.restart();\r\n        })\r\n        , $themes = $(\'#themes\').on(\'load\', function(){\r\n            $run.trigger(\'click\');\r\n        })\r\n        ;\r\n    \r\n    paceOptions = {\r\n            elements: true\r\n        }\r\n        \r\n        ;\r\n    \r\n    Pace.ignore(function(){});\r\n    \r\n    Pace.on(\'hide\', function(){\r\n        console.log(\'done\');\r\n    });\r\n    \r\n    $(\'#minimal\').on(\'click\', function(){\r\n        $themes.attr(\'href\', \'../script/plugin/pace/themes/pace-theme-minimal.css\');\r\n            $run.trigger(\'click\');\r\n    });\r\n    $(\'#flash\').on(\'click\', function(){\r\n        $themes.attr(\'href\', \'../script/plugin/pace/themes/pace-theme-flash.css\');\r\n            $run.trigger(\'click\');\r\n    });\r\n    $(\'#barderShop\').on(\'click\', function(){\r\n        $themes.attr(\'href\', \'../script/plugin/pace/themes/pace-theme-barber-shop.css\');\r\n            $run.trigger(\'click\');\r\n    });\r\n    $(\'#macos\').on(\'click\', function(){\r\n        $themes.attr(\'href\', \'../script/plugin/pace/themes/pace-theme-mac-osx.css\');\r\n            $run.trigger(\'click\');\r\n    });\r\n    $(\'#fillLeft\').on(\'click\', function(){\r\n        $themes.attr(\'href\', \'../script/plugin/pace/themes/pace-theme-fill-left.css\');\r\n            $run.trigger(\'click\');\r\n    });\r\n    $(\'#flatTop\').on(\'click\', function(){\r\n        $themes.attr(\'href\', \'../script/plugin/pace/themes/pace-theme-flat-top.css\');\r\n            $run.trigger(\'click\');\r\n    });\r\n    $(\'#bigCounter\').on(\'click\', function(){\r\n  