INSERT INTO `editor` VALUES (185,'<div class=\"case\">\r\n    <h3>CSS3 实现图片切换效果</h3>\r\n        \r\n    <div class=\"testExample\">\r\n        <ul class=\"demoList\">\r\n            <li class=\"demoItem demoItem-1\">\r\n                <img src=\"../image/demo/demoImage-1.jpg\" alt=\"\" class=\"demoImg\" />\r\n            </li>\r\n            <li class=\"demoItem demoItem-2\">\r\n                <img src=\"../image/demo/demoImage-2.jpg\" alt=\"\" class=\"demoImg\" />\r\n            </li>\r\n            <li class=\"demoItem demoItem-3\">\r\n                <img src=\"../image/demo/demoImage-3.jpg\" alt=\"\" class=\"demoImg\" />\r\n            </li>\r\n            <li class=\"demoItem demoItem-4\">\r\n                <img src=\"../image/demo/demoImage-4.jpg\" alt=\"\" class=\"demoImg\" />\r\n            </li>\r\n        </ul>\r\n    </div>\r\n</div>','.case{\r\n    overflow: hidden;\r\n    \r\n    border: 5px solid #c0c0c0;\r\n    margin: 10px;\r\n    padding: 10px;\r\n}\r\n.case h3{\r\n    text-align: center;\r\n}\r\n\r\n@-webkit-keyframes cssSlider{\r\n    0% {\r\n        left: 300%;\r\n    }\r\n    10% {\r\n        left: 300%;\r\n    }\r\n    25% {\r\n        left: 200%;\r\n    }\r\n    35% {\r\n        left: 200%;\r\n    }\r\n    50% {\r\n        left: 100%;\r\n    }\r\n    60% {\r\n        left: 100%;\r\n    }\r\n    75% {\r\n        left: 0;\r\n    }\r\n    85% {\r\n        left: 0\r\n    }\r\n    100% {\r\n        left: -100%;\r\n    }\r\n}\r\n@-moz-keyframes cssSlider{\r\n    0% {\r\n        left: 300%;\r\n    }\r\n    10% {\r\n        left: 300%;\r\n    }\r\n    25% {\r\n        left: 200%;\r\n    }\r\n    35% {\r\n        left: 200%;\r\n    }\r\n    50% {\r\n        left: 100%;\r\n    }\r\n    60% {\r\n        left: 100%;\r\n    }\r\n    75% {\r\n        left: 0;\r\n    }\r\n    85% {\r\n        left: 0\r\n    }\r\n    100% {\r\n        left: -100%;\r\n    }\r\n}\r\n@-ms-keyframes cssSlider{\r\n    0% {\r\n        left: 300%;\r\n    }\r\n    10% {\r\n        left: 300%;\r\n    }\r\n    25% {\r\n        left: 200%;\r\n    }\r\n    35% {\r\n        left: 200%;\r\n    }\r\n    50% {\r\n        left: 100%;\r\n    }\r\n    60% {\r\n        left: 100%;\r\n    }\r\n    75% {\r\n        left: 0;\r\n    }\r\n    85% {\r\n        left: 0\r\n    }\r\n    100% {\r\n        left: -100%;\r\n    }\r\n}\r\n@keyframes cssSlider{\r\n    0% {\r\n        left: 300%;\r\n    }\r\n    10% {\r\n        left: 300%;\r\n    }\r\n    25% {\r\n        left: 200%;\r\n    }\r\n    35% {\r\n        left: 200%;\r\n    }\r\n    50% {\r\n        left: 100%;\r\n    }\r\n    60% {\r\n        left: 100%;\r\n    }\r\n    75% {\r\n        left: 0;\r\n    }\r\n    85% {\r\n        left: 0\r\n    }\r\n    100% {\r\n        left: -100%;\r\n    }\r\n}\r\n\r\n.testExample{\r\n    overflow: hidden;\r\n    width: 300px;\r\n    border: 2px solid #c0c0c0;\r\n    border-radius: 5px;\r\n    padding: 5px;\r\n    background: #efefef;\r\n}\r\n.demoList{\r\n    overflow: hidden;\r\n    position: relative;\r\n    height: 200px;\r\n    width: 300px;\r\n    margin: 0;\r\n    padding: 0;\r\n    list-style: none;\r\n}\r\n.demoImg{\r\n    position: absolute;\r\n    top: 0;\r\n    bottom: 0;\r\n    max-height: 200px;\r\n    max-width: 300px;\r\n    margin: auto;\r\n}\r\n.demoItem{\r\n    position: absolute;\r\n    height: 200px;\r\n    width: 300px;\r\n    \r\n    -webkit-animation: cssSlider 12s linear infinite;\r\n    -moz-animation: cssSlider 12s linear infinite;\r\n    -ms-animation: cssSlider 12s linear infinite;\r\n    animation: cssSlider 12s linear infinite;\r\n}\r\n.demoItem-1{\r\n    -webkit-animation-delay: 12s;\r\n    -moz-animation-delay: 12s;\r\n    -ms-animation-delay: 12s;\r\n    animation-delay: 12s;\r\n}\r\n.demoItem-2{\r\n    left: 100%;\r\n    -webkit-animation-delay: 3s;\r\n    -moz-animation-delay: 3s;\r\n    -ms-animation-delay: 3s;\r\n    animation-delay: 3s;\r\n}\r\n.demoItem-3{\r\n    left: 200%;\r\n    -webkit-animation-delay: 6s;\r\n    -moz-animation-delay: 6s;\r\n    -ms-animation-delay: 6s;\r\n    animation-delay: 6s;\r\n}\r\n.demoItem-4{\r\n    left: 300%;\r\n    -webkit-animation-delay: 9s;\r\n    -moz-animation-delay: 9s;\r\n    -ms-animation-delay: 9s;\r\n    animation-delay: 9s;\r\n}','$(function(){\r\n\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js','CSS3 实现图片切换效果',NULL,'../image/default/no-pic.png',NULL,NULL,'2014-02-20 11:00:15',NULL,NULL,'2,8,48,125','CSS3,CSS,原创,相册效果');
INSERT INTO `editor` VALUES (186,'<div class=\"case\">\r\n\t<h3>JS 插件-烟雾线条笔刷</h3>\r\n    \r\n    <p>更多参考：\r\n        <a href=\"https://github.com/paulirish/harmony\">\r\n            https://github.com/paulirish/harmony\r\n        </a>\r\n        <br />\r\n        <a href=\"http://www.mrdoob.com/projects/harmony/index.html\">\r\n            http://www.mrdoob.com/projects/harmony/index.html\r\n        </a>\r\n    </p>\r\n\r\n</div>','.case{\r\n\toverflow: hidden;\r\n    z-index: 3;\r\n    height: 300px;\r\n\tborder: 5px solid #c0c0c0;\r\n\tmargin: 10px;\r\n\tpadding: 10px;\r\n}\r\n.case h3{\r\n\ttext-align: center;\r\n}\r\n\r\ncanvas{\r\n    position: fixed;\r\n    top: 0;\r\n    z-index: 2;\r\n    \r\n    cursor: crosshair;\r\n}','$(function(){\r\n    harmony();\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/plugin/harmony/harmony.js,../script/plugin/harmony/dollar.js','JS 插件-烟雾线条笔刷',NULL,'../image/default/no-pic.png',NULL,NULL,'2014-02-20 17:37:23',NULL,NULL,'1,3','JS 插件,HTML5');
INSERT INTO `editor` VALUES (187,'<div class=\"case\">\r\n    <h3>HTML5 实验-meter 标签</h3>\r\n    \r\n    <p>meter 标签定义度量衡，仅用于已知最大和最小的度量\r\n        <br />必须定义度量的范围\r\n        <br />high 属性：定义度量的值位于哪个点，被界定为高的值\r\n        <br />low  属性：定义度量的值位于哪个点，被界定为低的值\r\n        <br />max  属性：定义最大值，默认值是 1\r\n        <br />min  属性：定义最小值，默认值是 0\r\n        <br />optimum 属性：定义什么样的度量值是最佳的值；\r\n        如果该值高于 \"high\" 属性的值，则意味着值越高越好；\r\n        如果该值低于 \"low\" 属性的值，则意味着值越低越好\r\n        <br />value 属性：定义度量的值\r\n        <br />\r\n        <br />以下列表为最小值为 0，最大值为 1000，最佳值为 320，合适范围 200-600，各个值得显示情况\r\n        <br />&lt;meter min=\"0\" max=\"1000\" low=\"200\" height=\"600\" value=\"\" optimum=\"320\"&gt;&lt;/meter&gt;\r\n    </p>\r\n    \r\n    <dl class=\"testExample\" id=\"testExample\">\r\n        <dt>0%(0)</dt>\r\n        <dd>\r\n            <meter min=\"0\" max=\"1000\" low=\"200\" high=\"600\" value=\"0\" optimum=\"320\">0%</meter>\r\n        </dd>\r\n        <dt>10%(100)</dt>\r\n        <dd>\r\n            <meter min=\"0\" max=\"1000\" low=\"200\" high=\"600\" value=\"100\" optimum=\"320\">10%</meter>\r\n        </dd>\r\n        <dt>25%(250)</dt>\r\n        <dd>\r\n            <meter min=\"0\" max=\"1000\" low=\"200\" high=\"600\" value=\"250\" optimum=\"320\">25%</meter>\r\n        </dd>\r\n        <dt>32%(320)</dt>\r\n        <dd>\r\n            <meter min=\"0\" max=\"1000\" low=\"200\" height=\"600\" value=\"320\" optimum=\"320\">32%</meter>\r\n        </dd>\r\n        <dt>50%(500)</dt>\r\n        <dd>\r\n            <meter min=\"0\" max=\"1000\" low=\"200\" high=\"600\" value=\"500\" optimum=\"320\">50%</meter>\r\n        </dd>\r\n        <dt>60%(600)</dt>\r\n        <dd>\r\n            <meter min=\"0\" max=\"1000\" low=\"200\" high=\"600\" value=\"600\" optimum=\"320\">60%</meter>\r\n        </dd>\r\n        <dt>75%(750)</dt>\r\n        <dd>\r\n            <meter min=\"0\" max=\"1000\" low=\"200\" high=\"600\" value=\"750\" optimum=\"320\">75%</meter>\r\n        </dd>\r\n        <dt>100%(1000)</dt>\r\n        <dd>\r\n            <meter min=\"0\" max=\"1000\" low=\"200\" high=\"600\" value=\"1000\" optimum=\"320\">100%</meter>\r\n        </dd>\r\n    </dl>\r\n</div>','.case{\r\n\toverflow: hidden;\r\n\r\n\tborder: 5px solid #c0c0c0;\r\n\tmargin: 10px;\r\n\tpadding: 10px;\r\n    \r\n    font-family: \"Consolas\",Monaco,Andale,monospace;\r\n}\r\n.case h3{\r\n\ttext-align: center;\r\n}\r\n\r\nspan{\r\n    float: left;\r\n    min-width: 100px;\r\n}','$(function(){\r\n\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js','HTML5 实验-meter 标签',NULL,'../image/preview/meter.jpg',NULL,NULL,'2014-02-21 09:44:18',NULL,NULL,'3','HTML5');
INSERT INTO `editor` VALUES (188,'<div class=\"container case\" ng-app=\"myApp\">\r\n    <div class=\"row\">\r\n        <div class=\"col-md-12\">\r\n            <h3>{{ appName }}</h3>\r\n            \r\n            <h4>Author: {{ author }}</h4>\r\n            <div class=\"row caseExample\" ng-controller=\"MyController\">\r\n                <div class=\"col-md-12\">                    \r\n                    <form action=\"\" role=\"form\">\r\n                        <div class=\"form-group\">\r\n                            <label for=\"name\">请输入姓名：</label>\r\n                            <input type=\"text\" name=\"name\" id=\"name\" class=\"form-control\" ng-model=\"person.name\" placeholder=\"请输入姓名\" />\r\n                        </div>\r\n                    </form>\r\n                    <p>Hello, {{ person.name }}</p>\r\n                    <p>{{ clock }}</p>\r\n                </div>\r\n            </div>\r\n            \r\n            <div class=\"row caseExample\" ng-controller=\"ParentController\">\r\n                <div class=\"col-md-12\" ng-controller=\"ChildController\">\r\n                    <h5>控制器的继承</h5>\r\n                    <form action=\"\" role=\"form\">\r\n                        <div class=\"form-group\">\r\n                            <input type=\"text\" name=\"\" id=\"\" class=\"form-control\" ng-model=\"person.name\" placeholder=\"Name\" />\r\n                        </div>\r\n                    </form>\r\n                    <a href=\"\" class=\"btn btn-link\" ng-click=\"sayHello()\">Say hello</a>\r\n                    \r\n                    <p>{{ person }}</p>\r\n                </div>\r\n            </div>\r\n            \r\n            <div class=\"row caseExample\" ng-controller=\"PlayerController\">\r\n                <div class=\"col-md-12\">\r\n                    <h5>音乐播放</h5>\r\n                    <button type=\"button\" ng-click=\"play()\" ng-show=\"!playing\" class=\"btn btn-primary button\">Play</button>\r\n                    <button type=\"button\" ng-click=\"stop()\" ng-show=\"playing\" class=\"btn btn-primary button\">Stop</button>\r\n                    <p>\r\n                        Playing audio: <b>{{ playing }}</b>\r\n                    </p>\r\n                </div>\r\n            </div>\r\n            \r\n            <div class=\"row caseExample\">\r\n                <div class=\"col-md-12\" ng-controller=\"CounterController\">\r\n                    <h5>计数器</h5>\r\n                    <button type=\"button\" ng-click=\"add(1)\" class=\"btn btn-primary button\">+1</button>\r\n                    <button type=\"button\" ng-click=\"subtract(1)\" class=\" btn btn-primary button\">-1</button>\r\n                    <button class=\"btn btn-default\" type=\"button\" ng-click=\"counter = counter + 1\">+1</button>\r\n                    <h4>Current count: {{ counter }}</h4>\r\n                </div>\r\n            </div>\r\n            \r\n            <div class=\"row caseExample\">\r\n                <div class=\"col-md-12\" ng-controller=\"RepeatController\">\r\n                    <h5>循环</h5>\r\n                    <ul>\r\n                        <li ng-repeat=\"book in booklist\">{{ book.name }} by {{ book.author }}</li>\r\n                    </ul>\r\n                </div>\r\n            </div>\r\n            \r\n            <div class=\"row caseExample\">\r\n                <div class=\"col-md-12\" ng-controller=\"DisplayController\">\r\n                    <h5>显示/隐藏</h5>\r\n                    <button type=\"button\" ng-click=\"display()\" class=\"btn btn-primary\">{{ status }}</button>\r\n                    <img src=\"../image/demo/demoImage-1.jpg\" alt=\"\" class=\"img-thumbnail\" ng-show=\"show\" />\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>','.case{\r\n    border-radius: 10px;\r\n    margin: 10px auto 0; \r\n    background-color: #efefef;\r\n    font-family: \"Consolas\",Monaco,Andale,monospace;\r\n}\r\n.caseExample{\r\n    border-radius: 10px;\r\n    margin: 10px 0;\r\n    padding: 10px 0;\r\n    background: #c0c0c0;\r\n}','$(function(){\r\n    /**\r\n     * 初始化 angular\r\n     * 由于这里的结构问题，只能使用这种方法加载并初始化 angular，\r\n     * 在实际项目中，则可以将将其写在 html 页面代码中\r\n     */\r\n    $(\'<script><\\/script>\', {\r\n        src: \'../script/lib/angular/angular.min.js\'\r\n    }).appendTo(\'body\');\r\n    \r\n    var app = angular.module(\'myApp\', []);\r\n\r\n    app.run(function( $rootScope ){\r\n        $rootScope.appName = \'Angular 框架实践-2\';\r\n        $rootScope.author = \'ZwB\';\r\n        $rootScope.guest = \'guest\';\r\n    });\r\n    \r\n    app.controller(\'MyController\', function ($scope){\r\n\t\tvar updateClock = function (){\r\n                    $scope.clock = new Date();\r\n                }\r\n            , timer = setInterval(function (){\r\n                    $scope.$apply( updateClock );\r\n                }, 1000)\r\n            ;\r\n        \r\n\t\tupdateClock();\r\n\t});\r\n    \r\n    app.controller(\'ParentController\', function( $scope ){\r\n        $scope.person = {\r\n            greeted: false\r\n        };\r\n    });\r\n    app.controller(\'ChildController\', function( $scope ){\r\n        $scope.sayHello = function(){\r\n            $scope.person.greeted = !$scope.person.greeted;\r\n        }\r\n    });\r\n    app.controller(\'PlayerController\', [\'$scope\', function( $scope ){\r\n        $scope.playing = false;\r\n        $scope.audio = document.createElement(\'audio\');\r\n        $scope.audio.src = \'../media/HR-Why.mp4\';\r\n        $scope.play = function(){\r\n            $scope.audio.play();\r\n            $scope.playing = true;\r\n        }\r\n        $scope.stop = function(){\r\n            $scope.audio.pause();\r\n            $scope.playing = false;\r\n        }\r\n        $scope.audio.addEventListener(\'ended\', function(){\r\n            $scope.$apply(function(){\r\n                $scope.stop();\r\n            });\r\n        });\r\n    }]);\r\n    \r\n    app.controller(\'CounterController\', function( $scope ){\r\n        $scope.counter = 0;\r\n        $scope.add = function( amount ){\r\n            $scope.counter += amount;\r\n        }\r\n        $scope.subtract = function( amount ){\r\n            $scope.counter -= amount;\r\n        }\r\n    });\r\n    \r\n    app.controller(\'RepeatController\', function( $scope ){\r\n        $scope.booklist = [{\r\n            name: \'JavaScript 模式\',\r\n            author: \'Stoyan Stefanov\'\r\n        }, {\r\n            name: \'基于 MVC 的 JavaScript Web 富应用开发\',\r\n            author: \'Alex MacCaw\'\r\n        }, {\r\n            name: \'编写可维护的 JavaScript\',\r\n            author: \'Nicbolas C.Zakas\'\r\n        }, {\r\n            name: \'JavaScript 语言精粹（修订版）\',\r\n            author: \'Douglas Crockford\'\r\n        }, {\r\n            name: \'CSS 禅意花园（修订版）\',\r\n            author: \'Dave Shea,Molly E.Holzschlag\'\r\n        },{\r\n            name: \'WebGL 入门指南\',\r\n            author: \'Tony Parisi\'\r\n        }, {\r\n            name: \'数据可视化实战：使用D3设计交互式图表\',\r\n            author: \'Scott Murray\'\r\n        }, {\r\n            name: \'实时用户体验\',\r\n            author: \'Ted Roden\'\r\n        }, {\r\n            name: \'Node 即学即用\',\r\n            author: \'Tom Hugbes-Croicber,Mike Wilson\'\r\n        }, {\r\n            name: \'微交互：细节设计成就卓越产品\',\r\n            author: \'Dan Saffer\'\r\n        }, {\r\n            name: \'精益创业：新创业的成长思维\',\r\n            author: \'Eric Ries\'\r\n        }];\r\n    });\r\n    \r\n    app.controller(\'DisplayController\', function( $scope ){\r\n        $scope.show = false;\r\n        $scope.status = \'显示\';\r\n        $scope.display = function(){\r\n            $scope.show = !$scope.show;\r\n            $scope.status = $scope.show ? \'隐藏\' : \'显示\';\r\n        }\r\n    })\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/lib/bootstrap/bootstrap-3.0.2/css/bootstrap.min.css,../script/lib/bootstrap/bootstrap-3.0.2/css/bootstrap-theme.min.css','Angular 框架实践-2',NULL,'../image/default/no-pic.png',NULL,NULL,'2014-02-21 17:31:32',NULL,NULL,'6,11,132,121,137','JavaScript,框架,MV*,Angular.js,编程实践');
INSERT INTO `editor` VALUES (189,'<div class=\"container case\">\r\n    <div class=\"row\">\r\n        <div class=\"col-md-12\">\r\n            <h3>Bootstrap 框架实践-8 下拉菜单</h3>\r\n            \r\n            <div id=\"dropdown\" class=\"dropdown clearfix\">\r\n                <button type=\"button\" class=\"btn btn-primary dropdown-toggle\" data-toggle=\"dropdown\">\r\n                    Dropdown <b class=\"caret\"></b>\r\n                </button>\r\n                <ul class=\"dropdown-menu\" role=\"menu\">\r\n                    <li role=\"presentation\" aria-labelledby=\"dropdownMenu\" class=\"dropdown-header\">分组标题 1</li>\r\n                    <li><a href=\"#\">操作 1</a></li>\r\n                    <li><a href=\"#\">操作 2</a></li>\r\n                    <li><a href=\"#\">操作 3</a></li>\r\n                    <li class=\"divider\"></li>\r\n                    <li role=\"presentation\" aria-labelledby=\"dropdownMenu\" class=\"dropdown-header\">分组标题 2</li>\r\n                    <li><a href=\"#\">操作 4</a></li>\r\n                    <li class=\"divider\"></li>\r\n                    <li role=\"presentation\" aria-labelledby=\"dropdownMenu\" class=\"dropdown-header\">分组标题 3</li>\r\n                    <li><a href=\"#\">操作 5</a></li>\r\n                    <li><a href=\"#\">操作 6</a></li>\r\n                </ul>\r\n            </div>\r\n            \r\n            <p id=\"logArea\"></p>\r\n        </div>\r\n    </div>\r\n</div>','.row{\r\n    margin: 15px 0;\r\n}\r\n.row div{\r\n    border: 1px solid #DDD;\r\n    border-radius: 10px;\r\n    \r\n    background-color: #efefef;\r\n    \r\n    line-height: 40px;\r\n}','$(function(){\r\n    // 切换下拉菜单的展开状态\r\n    var $dropdown = $(\'#dropdown\')\r\n        , $logArea = $(\'#logArea\')\r\n        ;\r\n        \r\n    $dropdown.on(\'show.bs.dropdown\', function(){\r\n        $logArea.append(\'您触发了打开下拉框事件<br />\');\r\n    }).on(\'shown.bs.dropdown\', function(){\r\n        $logArea.append(\'下拉框打开事件完成<br />\');\r\n    }).on(\'hide.bs.dropdown\', function(){\r\n        $logArea.append(\'您触发了关闭下拉框事件<br />\');\r\n    }).on(\'hidden.bs.dropdown\', function(){\r\n        $logArea.append(\'下拉框关闭事件完成<br />\');\r\n    });\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/lib/bootstrap/bootstrap-3.0.2/css/bootstrap.min.css,../script/lib/bootstrap/bootstrap-3.0.2/css/bootstrap-theme.min.css,../script/lib/bootstrap/bootstrap-3.0.2/js/bootstrap.min.js','Bootstrap 框架实践-8 下拉菜单',NULL,'../image/default/no-pic.png',NULL,NULL,'2014-02-24 17:14:12',NULL,NULL,'4,98,137','UI,Bootstrap,编程实践');
INSERT INTO `editor` VALUES (190,'<div class=\"container case\" ng-app=\"myApp\">\r\n    <div class=\"row\">\r\n        <div class=\"col-md-12\">\r\n            <h3>{{ appName }}</h3>\r\n            \r\n            <h4>Author: {{ author }}</h4>\r\n            <div class=\"row caseExample\" ng-controller=\"ServiceController\">\r\n                <div class=\"col-md-12\">                    \r\n                    <form action=\"\" role=\"form\">\r\n                        <div class=\"form-group\">\r\n                            <label for=\"name\">请输入 Github 用户名：</label>\r\n                            <input type=\"text\" name=\"name\" id=\"name\" class=\"form-control\" ng-model=\"username\" placeholder=\"请输入 Github 用户名\" />\r\n                            <pre ng-show=\"username\">{{ events }}</pre>\r\n                        </div>\r\n                    </form>\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>','.case{\r\n    border-radius: 10px;\r\n    margin: 10px auto 0; \r\n    background-color: #efefef;\r\n    font-family: \"Consolas\",Monaco,Andale,monospace;\r\n}\r\n.caseExample{\r\n    border-radius: 10px;\r\n    margin: 10px 0;\r\n    padding: 10px 0;\r\n    background: #c0c0c0;\r\n}','$(function(){\r\n    /**\r\n     * 初始化 angular\r\n     * 由于这里的结构问题，只能使用这种方法加载并初始化 angular，\r\n     * 在实际项目中，则可以将将其写在 html 页面代码中\r\n     */\r\n    $(\'<script><\\/script>\', {\r\n        src: \'../script/lib/angular/angular.min.js\'\r\n    }).appendTo(\'body\');\r\n\r\n    var app = angular.module(\'myApp\', []);\r\n    \r\n    app.run(function( $rootScope ){\r\n        $rootScope.appName = \'Angular 框架实践-3\';\r\n        $rootScope.author = \'ZwB\';\r\n    });\r\n    \r\n    app.service(\'githubService\', [\'$http\', function( $http ){\r\n        var githubUsername\r\n            , doRequest = function(username, path){\r\n                    return $http({\r\n                        method: \'JSONP\'\r\n                        , url: \'https://api.github.com/users/\'+ username +\'/\'+ path +\'?callback=JSON_CALLBACK\'\r\n                    });\r\n                }\r\n            ;\r\n        return {\r\n            events: function( username ){\r\n                return doRequest(username, \'events\');\r\n            }\r\n            , setUsername: function( newUsername ){\r\n                githubUserName = newUsername;\r\n            }\r\n        }\r\n    }]);\r\n    \r\n    // 自定义的 service 要写在内建的 Angular services 之后，自定义的 service 之间是没有先后顺序的\r\n    app.controller(\'ServiceController\', [\'$scope\', \'$timeout\', \'githubService\', function( $scope, $timeout, githubService ){\r\n        var timeout;\r\n        \r\n        $scope.$watch(\'username\', function(newUsername){\r\n            if( newUsername ){\r\n                if( timeout ){\r\n                    $timeout.cancel( timeout );\r\n                }\r\n                \r\n                timeout = $timeout(function(){\r\n                    githubService.events(newUsername).success(function(data, status, headers){\r\n                        $scope.events = data.data;\r\n                    });\r\n                }, 350);\r\n            }\r\n        });\r\n    }]);\r\n});','../script/lib/jQuery/jquery-1.10.2.min.js,../script/lib/bootstrap/bootstrap-3.0.2/css/bootstrap.min.css,../script/lib/bootstrap/bootstrap-3.0.2/css/bootstrap-theme.min.css','Angular 框架实践-3',NULL,'../image/default/no-pic.png',NULL,NULL,'2014-02-28 17:15:19',NULL,NULL,'6,11,132,121,137','JavaScript,框架,MV*,Angular.js,编程实践');
