INSERT INTO `ui` VALUES (1,'按钮','<button class=\"Ui-Btn roundImg btnBlue backCard\"><span class=\"cencerLeft\"> </span><i> </i>更换银行卡<span class=\"cencerRight\"> </span></button>\r\n<button class=\"Ui-Btn roundImg btnYellow trade\" id=\"exchange\"><span class=\"cencerLeft\"> </span><i> </i>兑换余额<span class=\"cencerRight\"> </span></button>\r\n<button class=\"Ui-Btn roundImg btnGreen detail\"><span class=\"cencerLeft\"> </span><i> </i>收支明细<span class=\"cencerRight\"> </span></button>\r\n<button class=\"Ui-Btn roundImg btnBlue backCard btnGray\"><span class=\"cencerLeft\"> </span><i> </i>更换银行卡<span class=\"cencerRight\"> </span></button>','/* ui-Btn */\r\nbutton.Ui-Btn{display:inline-block;position:relative; margin:0 5px; padding:0; overflow:visible;background:none;border:0; cursor:pointer; }\r\n*+html button.Ui-Btn{display:inline }\r\n* html button.Ui-Btn{display:inline }\r\nbutton.Ui-Btn:focus{outline:dotted 1px #000}\r\nbutton.Ui-Btn span{display:inline-block;position:absolute;}\r\n/* btn style */\r\nbutton.roundImg{padding:0 11px;  line-height:26px; height:26px; font-size:14px; color:#fff;letter-spacing:1px;font-family:\"微软雅黑\"}\r\n*+html button.roundImg{  line-height:20px;}\r\nbutton.roundImg i{display:inline-block;position:absolute; }\r\nbutton.roundImg span{width:11px; height:26px; right:0; top:0}\r\nbutton.roundImg span.cencerLeft{left:0}\r\n/* btn color */\r\nbutton.btnYellow{background:url(../img/usualBtn.png) 0 -104px  repeat-x;}\r\nbutton.btnYellow span.cencerLeft{background:url(../img/usualBtn.png) 0 -208px  no-repeat #fff;}\r\nbutton.btnYellow span.cencerRight{background:url(../img/usualBtn.png) 0 0  no-repeat #fff;}\r\nbutton.btnBlue{background:url(../img/usualBtn.png) 0 -156px  repeat-x;}\r\nbutton.btnBlue span.cencerLeft{background:url(../img/usualBtn.png) 0 -260px  no-repeat #fff;}\r\nbutton.btnBlue span.cencerRight{background:url(../img/usualBtn.png) 0 -52px  no-repeat #fff;}\r\nbutton.btnGreen{background:url(../img/usualBtn.png) 0 -130px  repeat-x;}\r\nbutton.btnGreen span.cencerLeft{background:url(../img/usualBtn.png) 0 -234px  no-repeat #fff;}\r\nbutton.btnGreen span.cencerRight{background:url(../img/usualBtn.png) 0 -26px  no-repeat #fff;}\r\n/* btn icon */\r\nbutton.backCard{padding-left:20px; margin-left:15px;}\r\nbutton.backCard i{ top:-3px; left:-10px;width:33px;  height:25px;background:url(../img/iconsb.png) -4px -3266px;}\r\nbutton.trade{padding-left:20px; margin-left:15px;}\r\nbutton.trade i{ top:-3px; left:-10px;width:33px;  height:30px;background:url(../img/iconsb.png) -4px -3125px;}\r\nbutton.detail{padding-left:20px; margin-left:15px;}\r\nbutton.detail i{ top:-5px; left:-10px;width:33px;  height:30px;background:url(../img/iconsb.png) -4px -3178px;}\r\n/* btnGray */\r\nbutton.btnGray {background:url(../img/usualBtn.png) 0 -182px  repeat-x; color:#000; }\r\nbutton.btnGray i{ top:-7px; left:-15px; width:35px; height:35px; margin:0 15px 0 0 ;background:url(../img/iconsb.png) 0 -3499px   no-repeat;}\r\nbutton.btnGray span.cencerLeft{background:url(../img/usualBtn.png) 0 -286px  no-repeat #fff;}\r\nbutton.btnGray span.cencerRight{background:url(../img/usualBtn.png) 0 -78px  no-repeat #fff;}',NULL,NULL,'../image/uiPreview/buttonGroup.png',NULL,6);
