INSERT INTO `ui` VALUES (4,'详情表单','<div class=\"tradeDetail\">\r\n    <div class=\"statusTable\">\r\n        <table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\">\r\n            <tbody>\r\n            <tr>\r\n                <td width=\"10%\">交易单号:</td>\r\n                <td width=\"12%\">20120815365151156</td>\r\n                <td width=\"10%\">创建时间:</td>\r\n                <td width=\"20%\">2012-08-15 15:49:24</td>\r\n                <td width=\"51%\"></td>\r\n            </tr>\r\n            </tbody>\r\n        </table>\r\n    </div>\r\n    <div class=\"tradeTable\">\r\n        <table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\">\r\n            <thead>\r\n            <tr>\r\n                <th width=\"10%\"> </th>\r\n                <th>类 型</th>\r\n                <th>金额</th>\r\n            </tr>\r\n            </thead>\r\n            <tbody>\r\n            <tr>\r\n                <th>收入账户</th>\r\n                <td>购买</td>\r\n                <td>50.00</td>\r\n            </tr>\r\n            </tbody>\r\n        </table>\r\n    </div>\r\n</div>','div.tradeDetail{width:96%; margin:10px auto; height:auto; overflow:hidden; color:#000; }\r\ndiv.tradeTable table{ border:1px solid #ccc; font-size:12px; line-height:26px;}\r\ndiv.tradeTable table td , div.tradeTable table th{ padding:4px 7px; border:1px solid #ccc; border-collapse:collapse}\r\ndiv.tradeTable table th{background:#f6f6f6; font-weight:700}\r\ndiv.statusTable{ margin:0 0 5px 0;}\r\ndiv.statusTable table td{font-size:12px; line-height:22px; padding:0 5px; letter-spacing:2px;color:#000;}',NULL,NULL,'../image/uiPreview/detailFrom.png',NULL,6);
INSERT INTO `ui` VALUES (5,'搜索区','<div class=\"formStyle\">\r\n    <form>\r\n        <span>姓名：<input type=\"text\" class=\"text\" /></span>\r\n        <span>用户名：<input type=\"text\" class=\"text\" /></span>\r\n    <span>性别:\r\n        <select name=\"\"><option>全部</option><option>女</option><option>男</option></select>\r\n    </span>\r\n        <span>年龄：<input type=\"text\" class=\"text\" />-<input type=\"text\" class=\"text\" /></span>\r\n    <span>患者类型：\r\n        <select name=\"\"><option>全部</option><option>VIP</option><option>SVIP</option><option>普通患者</option></select>\r\n    </span>\r\n        <input  type=\"submit\" value=\"搜索\" class=\"button\"   />\r\n    </form>\r\n</div>','/*搜索区*/\r\ndiv.formStyle{height:auto; padding:5px 0; font-size:0; letter-spacing:-5px; word-spacing:-5px; color:#000;}\r\ndiv.formStyle span , div.formStyle input , div.formStyle select{ display:inline-block;font-size:12px; margin:3px 5px;line-height:23px;letter-spacing:0; word-spacing:0; vertical-align:middle}\r\ndiv.formStyle span{ margin:0 10px;}\r\n*+html div.formStyle span , *+html div.formStyle input , *+html div.formStyle select{display:inline}\r\n* html div.formStyle span , * html div.formStyle input , * html div.formStyle select{display:inline}\r\n* html div.formStyle select{ position:relative; margin:2px 5px 3px 5px; left:5px;}\r\ndiv.formStyle label input{ position:relative; top:-2px; margin:3px;}\r\n* html div.formStyle label input{margin:3px 0;}\r\ndiv.formStyle input.text{width:140px; height:21px; padding:0; margin:-3px 5px 0 5px; line-height:21px;border:1px solid #abbdc6;background:url(../img/manageMentbg.png) repeat-x;}\r\n* html div.formStyle input.text{position:relative; left:5px;}\r\ndiv.formStyle input.button{ position:relative; margin:-1px 0 0 0;width:auto; padding:0 10px; height:23px; letter-spacing:2px; padding:0 6px; cursor:pointer;line-height:21px; background:#3b79b6; border:0; color:#FFF}\r\ndiv.formStyle input.button:hover{padding:0 5px; background:#fff; border:1px solid #06C;color:#030}','',NULL,'../image/uiPreview/formStyle.png','周文博',6);
