INSERT INTO `ui` VALUES (14,'翻页','<div class=\"pagination\">\r\n    <strong class=\"unclick\"><i>◀</i> 上一页</strong>\r\n    <strong id=\"currentPage\">1</strong>\r\n    <strong class=\"unclick\">下一页 <i>▶</i></strong>\r\n<span>\r\n    <form methods=\"post\" >\r\n        <label>共1页 到第<input type=\"text\" value=\"1\" size=\"3\" id=\"jumpPage\">页</label>\r\n        <input type=\"submit\" value=\"确定\" class=\"button blackbutton\" />\r\n        <label> 共<b>14</b>条 </label>\r\n    </form>\r\n</span>\r\n</div>','/*分页*/\r\ndiv.pagination{font-size:0; letter-spacing: -5px; line-height: 32px; margin: 5px 0; position: relative; text-align: center; width:100%;}\r\ndiv.pagination a, div.pagination strong, div.pagination span, div.pagination b {background:#FEFEFE;border: 1px solid #CBCBCB;color: #0033CC;display: inline-block;font-size: 14px;letter-spacing:0;line-height: 24px;margin: 3px;padding: 0 11px;position: relative;text-decoration: none; vertical-align: middle; word-spacing: 0;}\r\ndiv.pagination a i {color: #FF9900;}\r\ndiv.pagination a:hover i {color: #FFFFFF;}\r\ndiv.pagination strong, div.pagination a:hover {border:0; color:#000000; font-size:16px;}\r\ndiv.pagination a:hover{ background:#3B79B6; border:none;color: #FFFFFF;line-height: 32px;margin: 0 1px;padding: 0 14px 0 13px;vertical-align: middle;}\r\ndiv.pagination a.page:hover {font-size: 14px; padding: 0 14px;}\r\ndiv.pagination span {border:none; color:#000000; font-size:12px; padding:0; vertical-align: bottom;}\r\ndiv.pagination span form {display: inline-block;}\r\n*+html div.pagination span form {display: inline;}\r\n* html div.pagination span form {display: inline;}\r\ndiv.pagination b {border:none;color: #000000;font-size:12px;margin-top:-2px; padding:0; vertical-align:bottom;}\r\n*+html div.pagination b {margin-top: 2px;}\r\ndiv.pagination b {border:none;color:#000000;font-size:12px;margin-top: -2px; padding:0;vertical-align: bottom;}\r\ndiv.pagination span * {font-family: Tahoma,Geneva,sans-serif;vertical-ali