INSERT INTO `ui` VALUES (15,'进度条','<div id=\"progress\">\r\n    <ul>\r\n        <li class=\"start\"><span class=\"icon\"> </span><span class=\"words\">1.选择预约时间</span><span class=\"arrow\"> </span></li>\r\n        <li class=\"uncover selected\"><span class=\"icon\"> </span><span class=\"words\">2.选择患者、填写主诉等详情</span><span class=\"arrow\"> </span></li>\r\n        <li class=\"uncover\"><span class=\"icon\"> </span><span class=\"words\">3.完成预约</span><span class=\"arrow\"> </span></li>\r\n    </ul>\r\n    <div class=\"last\"><span class=\"icon\">&nbsp;</span></div>\r\n</div>','/*进度条*/\r\ndiv#progress { overflow:hidden; margin:10px 0; width:99.8%; height:30px; line-height:30px; background:#e9f5fe; font-size:14px; font-family:\"宋体\";  }\r\n* html div#progress{ width:102%;}\r\ndiv#progress ul { margin:0; padding:0; height:30px; width: auto; float:left; overflow:hidden;}\r\ndiv#progress ul li { height:30px; padding:0; margin:0 0 0 -12px; float:left; display:block; color:#317aaa}\r\ndiv#progress ul li.start { width: auto; margin-left:-15px;}\r\ndiv#progress ul li.uncover { width: auto;}\r\ndiv#progress div.last { margin-left:0; }\r\ndiv#progress div.last{ padding-left:15px;}\r\ndiv#progress div.last span { display:inline-block;}\r\ndiv#progress div.last span.icon { margin-left:-11px; width:15px; background: url(../img/progress2.png) no-repeat scroll 0 -85px transparent;}\r\ndiv#progress ul li span { height:30px; display:inline-block;vertical-align:top;}\r\ndiv#progress ul li span.icon{ width:15px; background:url(../img/progress2.png); height:30px}\r\n* html div#progress ul li span.icon{ width:15px; background:url(../img/progress2.png) -14px -1515px; height:30px}\r\ndiv#progress ul li span.words{ width: auto; padding:0 20px; text-align:center; background:#C3E1F7; color: #317AAA; height:30px; line-height:30px;}\r\n* html div#progress ul li span.words{height:31px; line-height:31px;}\r\ndiv#progress ul li span.arrow{ width:15px; background:url(../img/progress.gif) 0 0;}\r\ndiv#pro