INSERT INTO `ui` VALUES (22,'图表插件','<div id=\"charts\"></div>\r\n<script src=\"../script/plugin/highcharts/highcharts.js\"></script>\r\n<script src=\"../script/plugin/highcharts/exporting.js\"></script>','','/**\r\n *  前置条件：引入 highcharts.js、exporting.js 文件\r\n *\r\n *  P.S.未使用全部参数 更多参数说明 请看 http://api.highcharts.com/highcharts\r\n * */\r\n$(document).ready(function(){\r\n    var chartsDefaults = {\r\n        /**\r\n         *  样式设置\r\n         * */\r\n        chart: {\r\n//目标绘图标签\r\n            renderTo:&#11111charts&#11111,\r\n//绘图类型\r\n            type:&#11111line&#11111\r\n        },\r\n        /**\r\n         *  颜色\r\n         * */\r\n        colors:[&#11111#AA4643&#11111, &#11111#4572A7&#11111],\r\n        /**\r\n         *  右下角 网站 LOGO\r\n         * */\r\n        credits: {\r\n            enabled: false\r\n        },\r\n        /**\r\n         *  右上角 扩展按钮\r\n         * */\r\n        exporting:{\r\n            enabled:false\r\n        },\r\n        /**\r\n         *  标题\r\n         * */\r\n        title:{\r\n            text:&#11111每日血压变化图&#11111\r\n        },\r\n        /**\r\n         *  副标题\r\n         * */\r\n        subtitle:{\r\n            text:&#1111170年1月&#11111\r\n        },\r\n        /**\r\n         *  横轴\r\n         * */\r\n        xAxis: {\r\n            title:{\r\n                text:&#11111日期&#11111\r\n            },\r\n            type:&#11111datetime&#11111,\r\n            maxZoom:3600000,\r\n            dateTimeLabelFormats: {\r\n                day: &#11111%y/%m/%d&#11111\r\n            }\r\n        },\r\n        /**\r\n         *  纵轴\r\n         * */\r\n        yAxis: {\r\n//纵轴标题\r\n            min:0,\r\n            title: {\r\n                text: &#11111血压&#11111\r\n            }\r\n        },\r\n        /**\r\n         *  各点的提示标签\r\n         * */\r\n        tooltip: {\r\n            formatter: function() {\r\n                var d = new Date(this.x);\r\n                return &#11111<b>日期：&#11111+\r\n                        d.getFullYear()+&#11111/&#11111+\r\n                        (d.getMonth()+1)+&#11111/&#11111+\r\n                        d.getDate()+&#11111</b><br />&#11111 +\r\n                        &#11111<b>血压：&#11111+this.y+&#11111</b>&#11111;\r\n            }\r\n        },\r\n        /**\r\n         *  各个折线的数据集合\r\n         * */\r\n        series: [{\r\n            name: &#11111高压&#11111,\r\n//数据间隔\r\n            pointInterval:24*3600000,\r\n            data: [7.0, 6.9, 9.5, 14.5, 18.2, 21.5, 25.2, 26.5, 23.3, 18.3, 13.9,9.5, 14.5, 18.2, 21.5, 25.2, 26.5, 23.3, 14.5, 18.2, 21.5, 26.5, 23.3, 14.5, 18.2, 21.5, 25.2, 26.5, 23.3, 18.3, 13.9, 9.6]\r\n        }, {\r\n            name: &#11111低压&#11111,\r\n            pointInterval:24*3600000,\r\n            data: [-0.2, 0.8, 5.7, 11.3, 17.0, 0.8, 5.7, 11.3, 17.0, 0.8, 5.7, 11.3, 17.0, 0.8, 5.7, 11.3, 17.0, 0.8, 5.7, 11.3, 17.0, 0.8, 5.7, 11.3, 17.0, 22.0, 24.8, 24.1, 20.1, 14.1, 8.6, 2.5]\r\n        }]\r\n    };\r\n\r\n     charts = new Highcharts.Chart(chartsDefaults);\r\n});',NULL,'../img/uiPreview/charts.png','周文博',5);
