INSERT INTO `ui` VALUES (24,'CSS3实验-动画效果','<div id=\"animationText\" class=\"animationText\">\r\n\t这是一个使用 CSS3 动画效果的实验\r\n\t<br /><br />当鼠标放到此动画上，动画会暂停\r\n\t<br /><br />参考教程：<a target=\"_blank\" href=\"//www.w3cplus.com/content/css3-animation\">http://www.w3cplus.com/content/css3-animation</a>\r\n</div>','/* 定义动画的关键帧 */\r\n@-webkit-keyframes wobble{\r\n\t0%{\r\n\t\tmargin-left:100px;\r\n\t\tbackground:green;\r\n\t}\r\n\t40% {\r\n\t\tmargin-left:150px;\r\n\t\tbackground:orange;\r\n\t}\r\n\t60%{\r\n\t\tmargin-left:75px;\r\n\t\tbackground:blue;\r\n\t}\r\n\t100%{\r\n\t\tmargin-left:100px;\r\n\t\tbackground:red;\r\n\t}\r\n}\r\n@-moz-keyframes wobble{\r\n  \t0%{\r\n\t\tmargin-left:100px;\r\n\t\tbackground:green;\r\n\t}\r\n\t40% {\r\n\t\tmargin-left:150px;\r\n\t\tbackground:orange;\r\n\t}\r\n\t60%{\r\n\t\tmargin-left:75px;\r\n\t\tbackground:blue;\r\n\t}\r\n\t100%{\r\n\t\tmargin-left:100px;\r\n\t\tbackground:red;\r\n\t}\r\n}\r\n\r\n.animationText{\r\n     color:white;\r\n     width: 450px;\r\n     height: 200px;\r\n     margin-left: 100px;\r\n     background: blue;\r\n     \r\n     /*动画属性名，也就是我们前面keyframes定义的动画名*/\r\n     -webkit-animation-name: wobble;\r\n     -moz-animation-name: wobble;\r\n     \r\n     /*动画持续时间，为数值，单位为s（秒），默认值为 0*/\r\n     -webkit-animation-duration: 10s;\r\n     -moz-animation-duration: 10s;\r\n     \r\n     /*动画频率，元素根据时间的推进来改变属性值得变化速率，就是动画的播放方式。有六种值：ease, ease-in, ease-out, ease-in-out, linear, cubc-bezier */\r\n     -webkit-animation-timing-function: ease-in-out;\r\n     -moz-animation-timing-function: ease-in-out;\r\n     \r\n     /*动画延迟时间，为数值，单位为s（秒），默认值为 0*/\r\n     -webkit-animation-delay: 1s;\r\n     -moz-animation-delay: 1s;\r\n     \r\n     /*动画循环次数，为数值，默认值为1，也可写 infinite 为无限次*/\r\n     -webkit-animation-iteration-count: infinite;\r\n     -moz-animation-iteration-count: infinite;\r\n     \r\n     /*动画播放方向，有2个值，默认为normal，每次循环都是向前播放，另为alternate，动画第偶数次向前播放，第奇数次向反方向播放*/\r\n     -webkit-animation-direction: alternate;\r\n     -moz-animation-direction: alternate;\r\n     \r\n     /*动画的播放状态，有2个值，running 和 paused，通过设置该属性可以将该动画暂停或继续播放*/\r\n     -webkit-animation-play-state: running;\r\n     -moz-animation-play-state: running;\r\n          \r\n     /*最后可以综合为\r\n      -webkit-animation: wobble 10s ease-in-out 1s infinite alrernate;\r\n      -moz-animation: wobble 10s ease-in-out 1s infinite alrernate;\r\n      */\r\n}\r\n.animationText a{color:white;} ','$(document).ready(function(){\r\n  var prefix = &#11111&#11111;\r\n  if( $.browser.chrome ){\r\n    prefix = &#11111-webkit-&#11111;\r\n  }\r\n  else if( $.browser.mozilla ){\r\n    prefix = &#11111-moz-&#11111;\r\n  }\r\n  $(&#11111#animationText&#11111).on({\r\n    mouseover:function(){\r\n    \tthis.style[prefix+&#11111animation-play-state&#11111] = &#11111paused&#11111;\r\n    },\r\n    mouseout:function(){\r\n      \tthis.style[prefix+&#11111animation-play-state&#11111] = &#11111running&#11111;\r\n    }\r\n  });\r\n});',NULL,NULL,'',7);
INSERT INTO `ui` VALUES (25,'CSS3实验-变形效果','<h1>这是CSS3 变形效果的实验 参考教程：<a target=\"_blank\"  href=\"//www.w3cplus.com/content/css3-transform\">http://www.w3cplus.com/content/css3-transform</a></h1>\r\n<div id=\"rotateTest\">旋转</div>\r\n<div id=\"translateTest\">移动</div>\r\n<div id=\"scaleTest\">缩放</div>\r\n<div id=\"skewTest\">扭曲</div>\r\n<div id=\"matrixTest\">矩阵</div>','div{float:left; width:100px; height:100px; margin:30px; background:green; color:white; text-align:center; line-height:200%;\r\n\t-moz-transform-origin: center center;\r\n\t-webkit-transform-origin: center center;\r\n\t-o-transform-origin: center center;\r\n\t-ms-transform-origin: center center;\r\n\ttransform-origin: center center;\r\n}\r\n.rotateTest{\r\n\t-moz-transform: rotate(45deg);\r\n\t-webkit-transform: rotate(45deg);\r\n\t-o-transform: rotate(45deg);\r\n\t-ms-transform: rotate(45deg);\r\n\ttransform: rotate(45deg);\r\n}\r\n.translateTest{\r\n\t-moz-transform: translate(30px,30px);\r\n     \t-webkit-transform :translate(30px,30px);\r\n     \t-o-transform: translate(30px,30px);\r\n     \t-ms-transform: translate(30px,30px);\r\n    \ttransform: translate(30px,30px);\r\n}\r\n.scaleTest{\r\n\t-moz-transform: scale(0.8,0.8);\r\n\t-webkit-transform: scale(0.8,0.8);\r\n\t-o-transform: scale(0.8,0.8);\r\n\t-ms-transform: scale(0.8,0.8);\r\n\ttransform: scale(0.8,0.8);\r\n}\r\n.skewTest{\r\n\t-moz-transform: skew(45deg,15deg);\r\n\t-webkit-transform: skew(45deg,15deg);\r\n\t-o-transform: skew(45deg,15deg);\r\n\t-ms-transform: skew(45deg,15deg);\r\n\ttransform: skew(45deg,15deg);\r\n}\r\n.matrixTest{\r\n\t-moz-transform: matrix(1,1,-1,0,0,0);\r\n\t-webkit-transform: matrix(1,1,-1,0,0,0);\r\n\t-o-transform: matrix(1,1,-1,0,0,0);\r\n\t-ms-transform: matrix(1,1,-1,0,0,0);\r\n\ttransform: matrix(1,1,-1,0,0,0);\r\n}','$(document).ready(function(){\r\n\t$(&#11111div&#11111).on(&#11111hover&#11111, function(){\r\n\t\tthis.className = this.className === &#11111&#11111 ? this.id : &#11111&#11111;\r\n\t});\r\n});',NULL,NULL,'',7);
INSERT INTO `ui` VALUES (26,'CSS3实验-动画与变形效果的综合使用','<div id=\"Watch\" class=\"Watch\">\r\n\t<span class=\"hourhand\"></span>\r\n\t<span class=\"minutehand\"></span>\r\n\t<span class=\"secondhand\"></span>\r\n\t<span class=\"mark\"></span>\r\n\t<span class=\"mark\"></span>\r\n\t<span class=\"mark\"></span>\r\n\t<span class=\"mark\"></span>\r\n\t<span class=\"mark\"></span>\r\n\t<span class=\"mark\"></span>\r\n\t<span class=\"mark\"></span>\r\n\t<span class=\"mark\"></span>\r\n\t<span class=\"mark\"></span>\r\n\t<span class=\"mark\"></span>\r\n\t<span class=\"mark\"></span>\r\n\t<span class=\"mark\"></span>\r\n</div>','@-webkit-keyframes watch{\r\n\t0%{\r\n\t}\r\n\t25%{\r\n\t\t-webkit-transform: rotate(90deg);\r\n\t}\r\n\t50%{\r\n\t\t-webkit-transform: rotate(180deg);\r\n\t}\r\n\t75%{\r\n\t\t-webkit-transform: rotate(270deg);\r\n\t}\r\n\t100%{\r\n\t\t-webkit-transform: rotate(360deg);\r\n\t}\r\n}\r\n@-moz-keyframes watch{\r\n\t0%{\r\n\t}\r\n\t25%{\r\n\t\t-moz-transform: rotate(90deg);\r\n\t}\r\n\t50%{\r\n\t\t-moz-transform: rotate(180deg);\r\n\t}\r\n\t75%{\r\n\t\t-moz-transform: rotate(270deg);\r\n\t}\r\n\t100%{\r\n\t\t-moz-transform: rotate(360deg);\r\n\t}\r\n}\r\n\r\n\r\n.Watch{position:absolute; width:200px; height:200px; border:5px solid #C0C0C0; border-radius: 110px;}\r\n\r\n.Watch span{\r\n\t-webkit-transform-origin: center bottom;\r\n\t-moz-transform-origin: center bottom;\r\n\t-ms-transform-origin: center bottom;\r\n}\r\n\r\n.Watch .mark{\r\n\tposition:absolute; left:97px; width:6px; height:10px; background:#000;\r\n\t-webkit-transform-origin:center 100px;\r\n\t-moz-transform-origin:center 100px;\r\n\t-ms-transform-origin:center 100px;\r\n}\r\n\r\n.hourhand{\r\n\tposition:absolute; left:91px; top: 30px; width:18px; height:70px; background:#000;\r\n\t-webkit-animation: watch 43200s linear 0s infinite normal;\r\n\t-moz-animation: watch 43200s linear 0s infinite normal;\r\n}\r\n.minutehand{\r\n\tposition:absolute; left:95px; top:15px; width:10px; height:85px; background:#000;\r\n\t-webkit-animation: watch 3600s linear 0s infinite normal;\r\n\t-moz-animation: watch 3600s linear 0s infinite normal;\r\n}\r\n.secondhand{\r\n\tposition:absolute; left:97px; top:0; width:6px; height:100px; background:#000;\r\n\t-webkit-animation: watch 60s linear 0s infinite normal;\r\n\t-moz-animation: watch 60s linear 0s infinite normal;\r\n}','$(document).ready(function(){\r\n  var prefix;\r\n  if( $.browser.chrome ){\r\n    prefix = &#11111-webkit-&#11111;\r\n  }\r\n  else if( $.browser.mozilla ){\r\n    prefix = &#11111-moz-&#11111;\r\n  }\r\n  else if( $.browser.msie ){\r\n  \tprefix = &#11111-ms-&#11111;\r\n  }\r\n  $(&#11111#Watch&#11111).find(&#11111.mark&#11111).css(prefix+&#11111transform&#11111, function(i){\r\n    return &#11111rotate(&#11111 + i*30 +&#11111deg)&#11111;\r\n  });\r\n});',NULL,'../image/preview/animation&transform.jpg','',7);
INSERT INTO `ui` VALUES (27,'HTML5实验-桌面提醒','<div id=\"Warm\">\r\n\t目前 HTML5 桌面提醒 API 只有 webkit内核浏览器支持，请使用 Chrome 或 Safari 浏览器查看\r\n</div>\r\n<input type=\"button\" id=\"showNotify\" value=\"显示桌面提醒\"  />','#Warm{margin:5px; padding:10px; background:blue; line-height:150%; color:white;}','$(document).ready(function(){\r\n\tif( &#11111webkitNotifications&#11111 in window ){\r\n\t\t$(&#11111#showNotify&#11111).on(&#11111click&#11111, function(){\r\n\t\t\tvar notify = window.webkitNotifications;\r\n\r\n                \t//判断浏览器是否已允许桌面提醒\r\n               \t\tif(notify.checkPermission() === 0){//已允许\r\n                   \t\tnotify.createNotification(&#11111../image/AOM.ico&#11111, &#11111桌面提醒&#11111, &#11111这是一条桌面提醒&#11111).show();\r\n                   \t\t// 也可以使用 createHTMLNotification 函数直接调用页面\r\n                \t}\r\n                \telse{//未允许\r\n                    \t\tnotify.requestPermission();\r\n                \t}\r\n\t\t});\r\n\t}\r\n});',NULL,'../image/preview/notify.jpg','',8);
INSERT INTO `ui` VALUES (28,'图片灰化','<img src=\"../img/test/psb (2).jpg\" width=\"670\" height=\"448\" />\r\n<img src=\"../img/test/psb (5).jpg\" width=\"670\" height=\"448\" />\r\n<img src=\"../img/test/psb (6).jpg\" width=\"670\" height=\"448\" />\r\n<img src=\"../img/test/psb.jpg\" width=\"670\" height=\"448\" />','body img{\r\n    -webkit-filter: grayscale(100%);\r\n    -moz-filter: grayscale(100%);\r\n    -ms-filter: grayscale(100%);\r\n    -o-filter: grayscale(100%);\r\n    filter: grayscale(100%);\r\n    \r\n    filter: url(../img/test/gray.svg#grayscale);\r\n    filter: Gray;\r\n}','',NULL,'../image/preview/gray.jpg','',7);
INSERT INTO `ui` VALUES (29,'HTML5 实验-audio 标签','<div id=\"warm\">\r\n    这是 HTML5 的 audio 标签实验<br />\r\n    参考教程：<br />\r\n    <a href=\"http://www.cnblogs.com/wzh2010/archive/2012/05/21/2511130.html\">http://www.cnblogs.com/wzh2010/archive/2012/05/21/2511130.html</a><br />\r\n    <a href=\"http://www.cnblogs.com/whitewolf/archive/2011/02/28/1967456.html\">http://www.cnblogs.com/whitewolf/archive/2011/02/28/1967456.html</a><br />\r\n    实例为 mp3 wav 文件<br />\r\n</div>\r\n<audio id=\"media\" controls=\"controls\" >\r\n    <source src=\"../img/test/[Asher Book]Try.mp3\" />\r\n    <source src=\"../img/test/[Asher Book]Try.wav\" />\r\n</audio>\r\n<br />\r\n<input type=\"button\" value=\"播放\" id=\"start\" />\r\n<input type=\"button\" value=\"暂停\" id=\"pause\" />','#warm{margin:5px; padding:10px; background:blue; line-height:150%; color:white;}\r\n#warm a{color:white;}','$(document).ready(function(){\r\n  \tvar Support = {};\r\n\tSupport.audio = function(){//判断是否支持 HTML5 的 audio 标签，及支持的文件类型\r\n  \t\tvar result = null,\r\n            audio = document.createElement(&#11111audio&#11111),\r\n            temp;\r\n    \tif( audio.canPlayType ){\r\n      \t\tresult = {};\r\n          \tresult.audioType = {};\r\n          \t\r\n          \ttemp = audio.canPlayType(&#11111audio/mpeg;&#11111);\r\n      \t\tresult.audioType.mp3 = (temp !== &#11111&#11111 && temp !== &#11111no&#11111) \r\n          \r\n          \ttemp = audio.canPlayType(&#11111audio/opgg;codecs=\"vorbis\"&#11111);\r\n      \t\tresult.audioType.ogg = temp !== &#11111&#11111 && temp !== &#11111no&#11111;\r\n          \t\r\n          \ttemp = audio.canPlayType(&#11111audio/wav;codecs=\"1\"&#11111);\r\n          \tresult.audioType.wav = temp !== &#11111&#11111 && temp !== &#11111no&#11111;\r\n          \t\r\n          \ttemp = audio.canPlayType(&#11111audio/mp4;codecs=\"mp4a.40.2\"&#11111)\r\n          \tresult.audioType.mp4 = temp !== &#11111&#11111 && temp !== &#11111no&#11111;\r\n    \t}\r\n      \treturn result;\r\n  \t}\r\n  \r\n  \tvar rs = Support.audio().audioType,\r\n        text, audio;        \t\r\n  \tif( rs ){\r\n      \ttext = &#11111您的浏览器支持 HTML5 的 audio 标签<br />该浏览器所支持的音频文件类型有：&#11111;\r\n      \tfor( var type in rs ){\r\n          \ttext += rs[type] ? type + &#11111,&#11111 : &#11111&#11111;            \t\r\n        }\r\n      \taudio = $(&#11111#media&#11111).get(0);\r\n\r\n      \t$(&#11111#start&#11111).on(&#11111click&#11111, function(){\r\n        \taudio.play();\r\n        });\r\n      \t$(&#11111#pause&#11111).on(&#11111click&#11111, function(){\r\n        \taudio.pause();\r\n        });\r\n    }\r\n  \telse{\r\n      \ttext = &#11111您的浏览器还不支持 HTML5 的 audio 标签&#11111;\r\n    }\r\n      \t\r\n  \t\r\n  \t$(&#11111#warm&#11111).html(function(){\r\n    \treturn this.innerHTML + text;\r\n    });\r\n});',NULL,'../image/preview/audio.jpg','',8);
INSERT INTO `ui` VALUES (30,'HTML5 实验-video 标签','<div id=\"warm\">\r\n    这是 HTML5 的 video 标签实验<br />\r\n    参考教程：<br />\r\n    <a href=\"http://www.cnblogs.com/wzh2010/archive/2012/05/21/2511130.html\">http://www.cnblogs.com/wzh2010/archive/2012/05/21/2511130.html</a><br />\r\n    <a href=\"http://www.cnblogs.com/whitewolf/archive/2011/02/28/1967456.html\">http://www.cnblogs.com/whitewolf/archive/2011/02/28/1967456.html</a><br />\r\n    实例为 mp4 webm 文件<br />\r\n</div>\r\n<video width=\"640\" height=\"360\" id=\"media\" controls=\"controls\" >\r\n    <source src=\"../img/test/HR-Why.mp4\" />\r\n    <source src=\"../img/test/HR-Why.webm\" />\r\n</video>\r\n<br />\r\n<input type=\"button\" value=\"播放\" id=\"start\" />\r\n<input type=\"button\" value=\"暂停\" id=\"pause\" />','#warm{margin:5px; padding:10px; background:blue; line-height:150%; color:white;}\r\n#warm a{color:white;}','$(document).ready(function(){\r\n  \tvar Support = {};\r\n\tSupport.video = function(){//判断是否支持 HTML5 的 audio 标签，及支持的文件类型\r\n  \t\tvar result = null,\r\n            video = document.createElement(&#11111video&#11111),\r\n            temp;\r\n    \tif( video.canPlayType ){\r\n      \t\tresult = {};\r\n          \t\r\n          \tresult.track = \"track\" in document.createElement(\"track\");\r\n          \tresult.videoType = {};\r\n          \t          \t\r\n          \ttemp = video.canPlayType(&#11111video/mp4;codecs=\"avc1.42E01E,mp44a.40.2\"&#11111);\r\n          \tresult.videoType.mp4 = temp !== &#11111&#11111 && temp !== &#11111no&#11111;\r\n          \t\r\n          \ttemp = video.canPlayType(&#11111video/ogg;codecs=\"theora,vorbis\"&#11111);\r\n          \tresult.videoType.ogg = temp !== &#11111&#11111 && temp !== &#11111no&#11111;\r\n          \r\n          \ttemp = video.canPlayType(&#11111video/webm;codecs=\"vp8,vorbis\"&#11111);\r\n          \tresult.videoType.webM = temp !== &#11111&#11111 && temp !== &#11111no&#11111;\r\n\r\n          \t\r\n    \t}\r\n      \treturn result;\r\n  \t}\r\n  \r\n  \tvar rs = Support.video(),\r\n        text, video;        \t\r\n  \tif( rs ){\r\n      \ttext = &#11111您的浏览器支持 HTML5 的 video 标签<br />&#11111;      \r\n      \ttext += rs.track ? &#11111您的浏览器支持字幕功能&#11111 : &#11111&#11111;        \r\n      \ttext += &#11111<br />该浏览器所支持的视频文件类型有：&#11111;        \r\n        \r\n      \tfor( var type in rs.videoType ){\r\n          \ttext += rs.videoType[type] ? type + &#11111,&#11111 : &#11111&#11111;            \t\r\n        }\r\n      \tvideo = $(&#11111#media&#11111).get(0);\r\n\r\n      \t$(&#11111#start&#11111).on(&#11111click&#11111, function(){\r\n        \tvideo.play();\r\n        });\r\n      \t$(&#11111#pause&#11111).on(&#11111click&#11111, function(){\r\n        \tvideo.pause();\r\n        });\r\n    }\r\n  \telse{\r\n      \ttext = &#11111您的浏览器还不支持 HTML5 的 video 标签&#11111;\r\n    }\r\n      \t\r\n  \t\r\n  \t$(&#11111#warm&#11111).html(function(){\r\n    \treturn this.innerHTML + text;\r\n    });\r\n});',NULL,'../image/preview/video.jpg','',8);
INSERT INTO `ui` VALUES (31,'HTML5 实验-全屏显示','<div id=\"Warm\">\r\n  HTML5 全屏显示 实验<br />\r\n  参考教程：<a href=\"http://davidwalsh.name/fullscreen\">http://davidwalsh.name/fullscreen</a>\r\n</div>\r\n<input type=\"button\" id=\"requestFullScreen\" value=\"全屏显示\"  />','#Warm{margin:5px; padding:10px; background:blue; line-height:150%; color:white;}\r\n#Warm a{color:white;} ','$(document).ready(function(){\r\n\t$(&#11111#requestFullScreen&#11111).on(&#11111click&#11111, function(){\r\n    \tif( &#11111requestFullScreen&#11111 in this ){\r\n          \twindow.top.document.documentElement.requestFullScreen();\r\n        }\r\n      \telse if( &#11111webkitRequestFullScreen&#11111 in this ){\r\n        \twindow.top.document.documentElement.webkitRequestFullScreen();\r\n        }\r\n      \telse if( &#11111mozRequestFullScreen&#11111 in this){\r\n          \twindow.top.document.documentElement.mozRequestFullScreen();\r\n        }\r\n    });\r\n});',NULL,'../image/preview/fullScreen.jpg','',8);
INSERT INTO `ui` VALUES (32,'HTML5 实验-摄像头 API','<div id=\"Warm\">\r\n\t目前 HTML5 摄像头 API 只有 webkit内核浏览器支持，请使用 Chrome 或 Safari 浏览器查看<br />\r\n\t参考教程：<a href=\"http://davidwalsh.name/demo/camera.php\">http://davidwalsh.name/demo/camera.php</a>\r\n</div>\r\n<input type=\"button\" id=\"camera\" value=\"开启摄像头\" />\r\n<video id=\"video\" width=\"640\" height=\"480\" autoplay=\"autoplay\" ></video>','#Warm{margin:5px; padding:10px; background:blue; line-height:150%; color:white;}\r\n#warm a{color:white;}','$().ready(function(){\r\n\tvar video = $(&#11111#video&#11111).get(0),\r\n        videoObj = {&#11111video&#11111:true},\r\n        errBack = function(error){\r\n        \tif( console && console.log ){\r\n            \tconsole.log(&#11111Video capture error:&#11111, error.code);\r\n            }\r\n        };\r\n  \r\n  \t$(&#11111#camera&#11111).on(&#11111click&#11111, function(){\r\n      \tif( navigator.getUserMedia ) { // Standard\r\n    \t\tnavigator.getUserMedia(videoObj, function(stream) {\r\n        \t\tvideo.src = stream;\r\n        \t\tvideo.play();\r\n      \t\t}, errBack);\r\n    \t}\r\n  \t\telse if( navigator.webkitGetUserMedia ) { // WebKit-prefixed\r\n      \t\tnavigator.webkitGetUserMedia(videoObj, function(stream){\r\n        \t\tvideo.src = window.webkitURL.createObjectURL(stream);\r\n        \t\tvideo.play();\r\n      \t\t}, errBack);\r\n    \t}\r\n    });\r\n});',NULL,NULL,'',8);
